  /**
   * Get dropdown template for topics
   *
   * @param {topicData} args
   * @returns JSON - dropdown template with topic data
   */
  function _getTopicData(args) {
    let { topicData } = args;
    let topicDataJson = [];
    if (topicData.length == 0) return [];

    //default option
    topicDataJson.push({
      id: "all",
      label: "All",
    });
    for (let topic of topicData) {
      topicDataJson.push({
        id: topic,
