function include({
    imports
}) {
    class OnCallEscalationPolicyClientUtilsSNC {
        static getTransformedPayloadForSubmit(policyName, basicInfoSection, conditionsSection, stepsSection, notificationsSection, escalationPolicyType, rotaId, escalationSetId) {
            var transformedLoad = {};
            transformedLoad.escalation_policy_type = escalationPolicyType;
            if (rotaId && rotaId != "") {
                transformedLoad.sys_id = "-1";
                transformedLoad.is_dirty = true;
            } else if (escalationSetId && escalationSetId != "") {
                transformedLoad.sys_id = escalationSetId;
                transformedLoad.is_dirty = true;
            }
            transformedLoad.basic_info = {
                "active": basicInfoSection.active,
                "cmn_rota": basicInfoSection.cmn_rota,
                "default": basicInfoSection.default,
                "order": basicInfoSection.default ? "" : basicInfoSection.order,
                "description": basicInfoSection.description,
                "name": policyName.value
            };
            transformedLoad.conditions = {
                "table": basicInfoSection.default ? "" : conditionsSection.table,
                "encoded_query": basicInfoSection.default ? "" : conditionsSection.condition
            };
            transformedLoad.escalation_notifications = {
                "override_user_contact_preference": notificationsSection.overrideUserContactPreference
            };
            transformedLoad.escalation_notifications.notifications = notificationsSection.attempts;
            let notifArr = transformedLoad.escalation_notifications.notifications;
            for (var i = 0; i < notifArr.length; i++) {

                let commTypes = [...notifArr[i].communication_types];
                var commTypesStr = "";
                for (var j = 0; j < commTypes.length; j++) {
                    if (commTypesStr != "")
                        commTypesStr += ",";
                    commTypesStr += commTypes[j].sys_id;
                }
                notifArr[i].communication_types = commTypesStr;
                notifArr[i].cmn_rota_escalation_set = escalationSetId || "-1";
                notifArr[i].type = "escalation_set";
                notifArr[i].is_dirty = true;
            }
            transformedLoad.escalation_notifications.notifications = notifArr;
            transformedLoad.escalation_steps = {
                "steps": [...stepsSection.steps]
            };
            return transformedLoad;
        };

        static async validateEscPolicyData(helpers, policyName, basicInfoSection, conditionsSection, stepsSection, notificationsSection, groupId) {
            var dataValidationResult = {
                status: "error",
                errorMsgs: []
            };
            basicInfoSection.isInvalid = false;
            conditionsSection.isInvalid = false;
            stepsSection.isInvalid = false;
            notificationsSection.isInvalid = false;
            if (!policyName.value || policyName.value == "") {
                dataValidationResult.errorMsgs.push(await helpers.translate("Escalation policy name is required."));
            }
            if (!basicInfoSection.cmn_rota || basicInfoSection.cmn_rota == "") {
                dataValidationResult.errorMsgs.push(await helpers.translate("Shift field is required."));
                basicInfoSection.isInvalid = true;
            }
            if(basicInfoSection.createMode == "from_template" && (!basicInfoSection.template || basicInfoSection.template == "")){
                dataValidationResult.errorMsgs.push(await helpers.translate("Escalation policy template is required."));
                basicInfoSection.isInvalid = true;
            }
            if (!basicInfoSection.description || basicInfoSection.description== "") {
                    dataValidationResult.errorMsgs.push(await helpers.translate("Description field is required."));
                    basicInfoSection.isInvalid = true;
            }
            if (!basicInfoSection.default) {
                if (!basicInfoSection.order || basicInfoSection.order == "") {
                    dataValidationResult.errorMsgs.push(await helpers.translate("Order field is required."));
                    basicInfoSection.isInvalid = true;
                }
                if (!conditionsSection.table || conditionsSection.table == "") {
                    dataValidationResult.errorMsgs.push(await helpers.translate("Table field is required."));
                    conditionsSection.isInvalid = true;
                }
            }
            if (stepsSection.steps.length > 0) {
                for (var i = 0; i < stepsSection.steps.length; i++) {
                    var step = stepsSection.steps[i];
                    if (!step.name || step.name == "" || (typeof step.name != "string" && step.name.value == "")) {
                        dataValidationResult.errorMsgs.push(await helpers.translate("Escalation step name is required, found empty for step {0}", (i + 1)));
                        stepsSection.isInvalid = true;
                    }
                   var specificGroups = step.sys_user_groups || "";
                   if (specificGroups.indexOf(groupId) !== -1) {
                        dataValidationResult.errorMsgs.push(await helpers.translate('Escalation step {0} has cyclic dependency on the same group on which this escalation policy is defined', i+1));
                        stepsSection.isInvalid = true;
                  }
                   if (step.reminders && !step.time_between_reminders) {
                        dataValidationResult.errorMsgs.push(await helpers.translate("Time between notifications is required when number of notifications is set, found empty for step {0}", (i + 1)));
                        stepsSection.isInvalid = true;
                   }
                }
            }
            if(notificationsSection.preferenceMode == "template" && (!notificationsSection.template || notificationsSection.template == "")){
                dataValidationResult.errorMsgs.push(await helpers.translate("Notification preferences template is required."));
                notificationsSection.isInvalid = true;
            }
            if (notificationsSection.attempts.length > 0) {
                for (var i = 0; i < notificationsSection.attempts.length; i++) {
                    var attempt = notificationsSection.attempts[i];
                    if (!attempt.contact_attempt || attempt.contact_attempt == "") {
                        dataValidationResult.errorMsgs.push(await helpers.translate("Escalation notification contact preference attempt number is required, found empty for attempt {0}", (i + 1)));
                        notificationsSection.isInvalid = true;
                    }
                    if (!attempt.communication_types || attempt.communication_types.length == 0) {
                        dataValidationResult.errorMsgs.push(await helpers.translate("Escalation notification contact preference type is required, found empty for attempt {0}", (i + 1)));
                        notificationsSection.isInvalid = true;
                    }
                }
            }
            return dataValidationResult;
        };

	//time strings are expected in d HH:mm:ss
	static addTimeDuration(timeStr1, timeStr2) {
		if (!timeStr1 || !timeStr2) return;

		var tokens1 = timeStr1.split(" ");
		var tokens2 = timeStr2.split(" ");

		if (tokens1.length != 2 || tokens2.length != 2) return;

		const dayRegex = new RegExp(/^\d+$/);
		if (!dayRegex.test(tokens1[0]) || !dayRegex.test(tokens2[0])) return;

		const timeRegex = new RegExp(/^(?:[01]?[0-9]|2[0-3]):[0-5]?[0-9](?::[0-5]?[0-9])?$/);
		if (!timeRegex.test(tokens1[1]) || !timeRegex.test(tokens2[1])) return;

		var days1 = tokens1[0];
		var days2 = tokens2[0];
		var timeStrArr1 = tokens1[1].split(":");
		var timeStrArr2 = tokens2[1].split(":");

		var secs =  (parseInt(timeStrArr1[2]) + parseInt(timeStrArr2[2])) % 60;
		var mins =  Math.floor((parseInt(timeStrArr1[2]) + parseInt(timeStrArr2[2])) / 60);
		mins =  mins + (parseInt(timeStrArr1[1]) + parseInt(timeStrArr2[1])) % 60;
		var hrs =  Math.floor((parseInt(timeStrArr1[1]) + parseInt(timeStrArr2[1])) / 60);
		hrs =  hrs + (parseInt(timeStrArr1[0]) + parseInt(timeStrArr2[0])) % 24;
		var days =  Math.floor((parseInt(timeStrArr1[0]) + parseInt(timeStrArr2[0])) / 24);
		days =  days + parseInt(days1) + parseInt(days2);

		secs = (secs < 10) ? "0" + secs : "" + secs;
		mins = (mins < 10) ? "0" + mins : "" + mins;
		hrs = (hrs < 10) ? "0" + hrs : "" + hrs;

		return days + " " + hrs + ":" + mins + ":" + secs;
	};

	//time string is expected in d HH:mm:ss
	static repeatTimeDuration(timeStr, count) {
		if (!timeStr || !count) return;

		var totalTime = "0 00:00:00";

		for (var i = 0; i < count; i++) {
			totalTime = this.addTimeDuration(totalTime, timeStr);
			if (!totalTime) return;
		};

		return totalTime;
	};

	static updateTotalTimeToCurStep(escalationStepSection, curStepIndex) {
		var totalTimeDurToCurStep;
		for (var ind = curStepIndex; ind < escalationStepSection.steps.length; ind++) {
			if (ind === 0){
				totalTimeDurToCurStep = "0 00:00:00";
				escalationStepSection.steps[0].total_time_to_cur_step = {
					value: totalTimeDurToCurStep,
					duration_value: totalTimeDurToCurStep
				};
				continue;
			}
			var stepData = escalationStepSection.steps[ind];
			var previousStepData = escalationStepSection.steps[ind - 1];
			var reminders = previousStepData.reminders.value;
			if (reminders) reminders = parseInt(reminders);
			var timeBetweenReminders = previousStepData.time_between_reminders.value;
			if (timeBetweenReminders) {
				timeBetweenReminders = timeBetweenReminders.split(" ");
				if (timeBetweenReminders.length) timeBetweenReminders = timeBetweenReminders[timeBetweenReminders.length - 1];
			}
			var timeToNextStep = previousStepData.time_to_next_step.value;
			if (timeToNextStep) {
				timeToNextStep = timeToNextStep.split(" ");
				if (timeToNextStep.length) timeToNextStep = timeToNextStep[timeToNextStep.length - 1];
			}

			totalTimeDurToCurStep = previousStepData.total_time_to_cur_step.duration_value;
			if (totalTimeDurToCurStep.split(" ").length < 2) totalTimeDurToCurStep = "0 " + totalTimeDurToCurStep;

			if (reminders && timeBetweenReminders) {
				var totalReminderTimeDur = this.repeatTimeDuration("0 " + timeBetweenReminders, reminders);
				totalReminderTimeDur = totalReminderTimeDur || "0 00:00:00";
				totalTimeDurToCurStep = this.addTimeDuration(totalTimeDurToCurStep, totalReminderTimeDur);
			}
			if (timeToNextStep) {
				totalTimeDurToCurStep = this.addTimeDuration(totalTimeDurToCurStep, "0 " + timeToNextStep);
			}

			stepData.total_time_to_cur_step = {
				value: totalTimeDurToCurStep,
				duration_value: totalTimeDurToCurStep
			};
		}
	};

    }
    return OnCallEscalationPolicyClientUtilsSNC;
}

