/**
  * Copy of sn_sow.SOWDateTimeHelper
  */
function include({ imports }) {
	const { NowDateTime } = imports['sn_uxf.core_libraries']();

	const DEFAULT_DATE_TIME_FORMAT = 'YYYY-MM-DD HH:mm:ss';
	const DEFAULT_SHORT_DATE_FORMAT = 'DD-MMM-YY';
	const DEFAULT_TIME_FORMAT = 'HH:mm';

	/**
	 * NowDateTime format tokens have some differences with those in the platform
	 * so this function replaces the more commonly used ones
	 * e.g. 'y' in a platform date/time format with 'Y' for NowDateTime
	 */
	const _parseFormat = function (format) {
		if (!format)
			return DEFAULT_DATE_TIME_FORMAT;

		const tokenReplacements = {
			y: 'Y',
			d: 'D',
			S: 'SSS'
		};
		const regEx = new RegExp(Object.keys(tokenReplacements).join('|'), 'g');

		format = format.replace(regEx, token => {
			return tokenReplacements[token];
		});

		return format;
	};

	/**
	 * Returns a NowDateTime object
	 *
	 * @param {String} [dateTime]           the date/time to return a NowDateTime object for
	 * @param {String} [dateTimeFormat]     the format of the provided date/tine e.g. DD/MM/YY hh:mm a
	 * @param {String} [sourceTimezone]     the timezone of the provided date/time e.g. US/Pacific
	 * @param {String} [convertToTimezone]  the timezone to convert the returned NowDateTime object to e.g. Europe/London
	 * 
	 * @return {Object} a NowDateTime object
	 */
	const getNowDateTime = function (
		dateTime,
		dateTimeFormat,
		sourceTimezone,
		convertToTimezone
	) {
		if (!dateTime)
			return NowDateTime();

		let nowDateTime = dateTimeFormat
			? NowDateTime(dateTime, _parseFormat(dateTimeFormat))
			: NowDateTime(dateTime);

		if (sourceTimezone)
			nowDateTime = nowDateTime.tz(sourceTimezone, true);

		return convertToTimezone ? nowDateTime.tz(convertToTimezone) : nowDateTime;
	};

	/**
	 * Returns a string representation of the supplied NowDateTime object in relative time format
	 * e.g. about a minute ago, 3 days from now
	 *
	 * @param {Object} helpers  the helpers object that is made available to UIB client scripts
	 * @param {Object} date     the NowDateTime object to be returned in relative time format
	 * 
	 * @return {String} the relative time format of the supplied NowDateTime object
	 */
	const getRelativeTimeAsync = async function (
		helpers,
		date = getNowDateTime()
	) {
		const dateFromNow = date.fromNow();
		const isPast = dateFromNow.endsWith('ago');
		const timeRange = isPast
			? dateFromNow.replace(/ago$/, '').trim()
			: dateFromNow.replace(/^in/, '').trim();

		switch (timeRange) {
			case 'a few seconds':
				// range 0-44 seconds
				return isPast
					? await helpers.translate('just now')
					: await helpers.translate('almost now');
			case 'a minute':
				// range 45-89 seconds
				return isPast
					? await helpers.translate('about a minute ago')
					: await helpers.translate('about a minute from now');
			case 'an hour':
				// 45 to 89 minutes
				return isPast
					? await helpers.translate('about an hour ago')
					: await helpers.translate('about an hour from now');
			case 'a day':
				// 22 to 35 hours
				return isPast
					? await helpers.translate('about a day ago')
					: await helpers.translate('about a day from now');
			case 'a month':
				// 26 to 45 days
				return isPast
					? await helpers.translate('about a month ago')
					: await helpers.translate('about a month from now');
			case 'a year':
				// 11 months to 17 months
				return isPast
					? await helpers.translate('about a year ago')
					: await helpers.translate('about a year from now');
			// plural value returned below
			default:
				break;
		}

		// in the past (eg. 2 hours ago, 5 days ago, 20 minutes ago, etc.)
		// in the future (eg. in 2 hours, in 5 days, in 20 minutes, etc.)
		const splitTime = dateFromNow.split(' ');
		const timeType = isPast ? splitTime[1] : splitTime[2];
		const timeCount = isPast ? splitTime[0] : splitTime[1];

		switch (timeType) {
			case 'minutes':
				return isPast
					? await helpers.translate('{0} minutes ago', timeCount)
					: await helpers.translate('{0} minutes from now', timeCount);
			case 'hours':
				return isPast
					? await helpers.translate('{0} hours ago', timeCount)
					: await helpers.translate('{0} hours from now', timeCount);
			case 'days':
				return isPast
					? await helpers.translate('{0} days ago', timeCount)
					: await helpers.translate('{0} days from now', timeCount);
			case 'months':
				return isPast
					? await helpers.translate('{0} months ago', timeCount)
					: await helpers.translate('{0} months from now', timeCount);
			case 'years':
				return isPast
					? await helpers.translate('{0} years ago', timeCount)
					: await helpers.translate('{0} years from now', timeCount);
			default:
				break;
		}
		// should never get here
		return await helpers.translate('Unknown');
	};

	/**
	 * Returns a string representation of the supplied NowDateTime object in relative day format
	 * e.g. today, yesterday, this Friday
	 *
	 * @param {Object}  helpers                     the helpers object that is made available to UIB client scripts
	 * @param {Object}  date                        the NowDateTime object to be returned in relative day format
	 * @param {Integer} [relativeFormatDayLimit]    number indicating the number of relative days which then triggers
	 *                                              this function to return a date string instead
	 * @param {String}  [dateFormat]                the format of the date string to be returned if relativeFormatDayLimit is exceeded
	 *                                              e.g. YYYY-MM-DD
	 * 
	 * @return {String} the relative day format of the supplied NowDateTime object
	 */
	const getRelativeDayAsync = async function (
		helpers,
		date = getNowDateTime(),
		relativeFormatDayLimit,
		dateFormat = DEFAULT_SHORT_DATE_FORMAT
	) {
		relativeFormatDayLimit = parseInt(relativeFormatDayLimit);

		const now = getNowDateTime();
		/*
		  gets a new NowDateTime object for now but bases it on an existing NowDateTime
		  this is needed to maintain the correct timezone in the new object as you cannot
		  get the timezone out of a NowDateTime
		*/
		const nowInTimezone = date.add(now.valueOf() - date.valueOf(), 'millisecond');
		const startOfToday = nowInTimezone.startOf('day');
		const startOfDate = date.startOf('day');
		const dayDiff = Math.abs(startOfDate.diff(startOfToday, 'days'));
		const weekDayOfDate = date.format('d');
		const startOfWeekNow = nowInTimezone.startOf('week').add(1, 'day'); // adding 1 day to account for first day of week defaulting to Sunday
		const endOfWeekNow = nowInTimezone.endOf('week').add(1, 'day'); // adding 1 day to account for first day of week defaulting to Sunday

		/*
		  if a 'relativeFormatDayLimit' has been passed in this number is used to check the difference in days 
		  and if it exceeds this limit then just return a formatted date/time value instead
		*/
		if (!isNaN(relativeFormatDayLimit) && dayDiff > relativeFormatDayLimit)
			return getFormattedDateText(date, null, dateFormat);

		const isPast = date.isBefore(now);
		const isThisWeek = date.valueOf() >= startOfWeekNow.valueOf() && date.valueOf() <= endOfWeekNow.valueOf();
		const dayNames = [
			await helpers.translate('Sunday'),
			await helpers.translate('Monday'),
			await helpers.translate('Tuesday'),
			await helpers.translate('Wednesday'),
			await helpers.translate('Thursday'),
			await helpers.translate('Friday'),
			await helpers.translate('Saturday')
		];

		// return text based on the number of days difference between the date passed in and now
		switch (true) {
			case dayDiff === 0:
				return await helpers.translate('today');
			case dayDiff === 1:
				return isPast
					? await helpers.translate('yesterday')
					: await helpers.translate('tomorrow');
			case dayDiff < 8:
				return isThisWeek ? await helpers.translate('this {0}', dayNames[weekDayOfDate]) :
					(isPast ? await helpers.translate('last {0}', dayNames[weekDayOfDate])
						: await helpers.translate('next {0}', dayNames[weekDayOfDate]));
			default:
				return await getRelativeTimeAsync(helpers, date);
		}
	};

	/**
	 * Returns a string representation of the supplied NowDateTime object in relative day format
	 * e.g. today at 5pm, next Friday at 17:21
	 *
	 * @param {Object}  helpers                     the helpers object that is made available to UIB client scripts
	 * @param {Object}  date                        the NowDateTime object to be returned in relative day format
	 * @param {String}  [timeFormat]                the format of the time string to be returned e.g. hh:mm a
	 * @param {Integer} [relativeFormatDayLimit]    number indicating the number of relative days which then triggers
	 *                                              this function to return a date string instead
	 * @param {String}  [dateFormat]                the format of the date string to be returned if relativeFormatDayLimit is exceeded
	 *                                              e.g. YYYY-MM-DD
	 * 
	 * @return {String} the relative day format with time of the supplied NowDateTime object
	 */
	const getRelativeDayAndTimeAsync = async function (
		helpers,
		date = getNowDateTime(),
		timeFormat = DEFAULT_TIME_FORMAT,
		relativeFormatDayLimit,
		dateFormat
	) {
		// get a relative day based text for the date e.g. today, last Friday, 10 days from now
		let dayText = await getRelativeDayAsync(
			helpers,
			date,
			relativeFormatDayLimit,
			dateFormat
		);

		// get just the time portion of the NowDateTime object based on the format passed in
		timeFormat = _parseFormat(timeFormat);
		let timeText = date.format(timeFormat);

		return await helpers.translate('{0} at {1}', dayText, timeText);
	};

	/**
	 * Returns a string representation of the supplied NowDateTime object in relative day format
	 * e.g. today at 5pm, next Friday at 17:21
	 *
	 * @param {Object} date         the NowDateTime object to be returned as a string in the specified format
	 * @param {String} [timeZone]   the timezone to return the date/time in e.g. US/Pacific
	 * @param {String} [format]     the format to return the date/time in e.g. DD/MM/YY hh:mm a
	 * 
	 * @return {String} the string format with appropriate timezone conversion of the supplied NowDateTime object
	 */
	const getFormattedDateText = function (
		date = getNowDateTime(),
		timeZone,
		dateTimeFormat
	) {
		dateTimeFormat = _parseFormat(dateTimeFormat);

		return timeZone ? date.tz(timeZone).format(dateTimeFormat) : date.format(dateTimeFormat);
	};

	return {
		getRelativeTimeAsync,
		getRelativeDayAsync,
		getRelativeDayAndTimeAsync,
		getFormattedDateText,
		getNowDateTime
	};
}

function include({ imports }, api, helpers) {


    const { EvalStatus, isEmpty } = imports["sn_access_analyzer.UserComparisonUtil"](api, helpers);

    class AccessComparator {

        constructor(baseUserRef, modelUserRef, params) {
            this.params = Object.assign({
                baseUserRef: baseUserRef,
                modelUserRef: modelUserRef,
                defaultStatus: EvalStatus.SKIPPED.value
            }, params);
        }

        //------------------------------------------------------------//

        /**
         * attributes in requestMapper
         * rowData(id,name), userData(script,status, etc.,)
         */
        requestRowMapper(rowData) {
            return rowData;
        }

        //------------------------------------------------------------//

        getComparisonResult(dataList) {
            var map = {};
            var index = 0;
            dataList.forEach(row => {
                var requestData = this.requestRowMapper(row, index);
                if (requestData == null) return;
                if (!map[requestData.rowData.id]) {
                    map[requestData.rowData.id] = {
                        index: index++,
                        rowData: requestData.rowData,
                        baseUser: null,
                        modelUser: null
                    }
                }
                map[requestData.rowData.id][this.params.baseUserRef == requestData.userData.id ? "baseUser" : "modelUser"] = requestData.userData;
            });
            return Object.keys(map).map(id => this.resultRowMapper(map[id]));
        }

        //------------------------------------------------------------//


        resultRowMapper(row) {
            var val = row.rowData || {};
            val.baseUser = row.baseUser ? row.baseUser.value : null;
            val.modelUser = row.modelUser ? row.modelUser.value : null;
            return {
                index: row.index,
                name: {
                    label: row.rowData.name,
                    value: row.rowData.name,
                    metadata: val
                },
                baseUser: this.getStatus(row.baseUser),
                modelUser: this.getStatus(row.modelUser)
            };
        }

        //------------------------------------------------------------//

        getStatus(user) {
            user = user || {};
            var status = (isEmpty(user.status) ? this.params.defaultStatus : user.status).toUpperCase();
            if ((status == "PASSED" || status == "BLOCKED") && !isEmpty(user.script)) {
                return status + "_MAYBE";
            }
            return status;
        }
    }

    //--------------------------------------------------------------------------------//

    class GroupComparator extends AccessComparator {

        constructor(baseUserId, modelUserId, params) {
            super(baseUserId, modelUserId, params);
            this.params.defaultStatus = EvalStatus.GROUP_NOT_GRANTED.value
        }

        requestRowMapper(row) {
            return {
                rowData: {
                    id: row.group.value,
                    name: row.group.displayValue
                },
                userData: {
                    id: row.user.value,
                    status: EvalStatus.HAS_GROUP.value
                }
            }
        }
    }

    //--------------------------------------------------------------------------------//

    class RoleComparator extends AccessComparator {

        constructor(baseUserId, modelUserId, params) {
            super(baseUserId, modelUserId, params);
            this.params.defaultStatus = EvalStatus.ROLE_NOT_GRANTED.value
        }


        requestRowMapper(row) {
            return {
                rowData: {
                    id: row.role.value,
                    name: row.role.displayValue
                },
                userData: {
                    id: row.user.value,
                    status: EvalStatus.HAS_ROLE.value
                }
            }
        }
    }

    //--------------------------------------------------------------------------------//

    class UserComparator extends AccessComparator {

        setFieldList(fieldList) {
            this.fieldList = fieldList;
        }

        getComparisonResult(list) {
            var fieldMap = new Map([
                ["user_name", null],
                ["email", null],
                ["active", null],
                ["sys_created_on", null],
                ["failed_attempts", null],
                ["last_login", null],
                ["internal_integration_user", null],
                ["password_needs_reset", null],
                ["sys_created_by", null],
                ["sys_updated_by", null]
            ]);

            this.fieldList.forEach(field => fieldMap.set(field.element.value, field.column_label.displayValue));
            var result = [];
            list.forEach(user => {
                fieldMap.forEach((fieldLabel, fieldName) => {
                    if (fieldLabel == null) return;
                    result.push({
                        rowData: {
                            id: fieldName,
                            label: fieldLabel,
                        },
                        userData: {
                            id: user._row_data.uniqueValue,
                            value: user[fieldName] ? user[fieldName].displayValue : ""
                        }
                    });
                });
            });
            return super.getComparisonResult(result);
        }

        resultRowMapper(row) {
            return {
                index: row.index,
                name: {
                    label: row.rowData.label,
                    value: row.rowData.label
                },
                baseUser: row.baseUser ? row.baseUser.value : "",
                modelUser: row.modelUser ? row.modelUser.value : ""
            };
        }

    }

    //--------------------------------------------------------------------------------//

    class OperationComparator extends AccessComparator {

        requestRowMapper(row) {
            return {
                rowData: {
                    id: row.operation.value,
                    name: row.operation.value,
                },
                userData: {
                    id: row.access_analyzer_request.value,
                    status: row.overall_access.value,
                    script: row.script_execution.value,
                    value: {
                        resultId: row._row_data.uniqueValue
                    }
                }
            }
        }
    }

    //--------------------------------------------------------------------------------//

    class DebugLogComparator extends AccessComparator {

        sortLogs(result, prefOrder){
            result.sort(function (a, b) {
                if (a.log_sequence.value != b.log_sequence.value) {
                    return a.log_sequence.value - b.log_sequence.value;
                }
                return prefOrder.indexOf(a.type.value) - prefOrder.indexOf(b.type.value);
            });
        }
        getComparisonResult(result) {
            var prefOrder = ["Business Rule", "Access Handler", "Data Filtration", "ACL"];
            this.sortLogs(result, prefOrder);
            return super.getComparisonResult(result);
        }
        requestRowMapper(row) {
            var isAH = row.type.value == "Access Handler";
            return {
                rowData: {
                    id: isAH ? row.applies_to.value : row.record_ref.value,
                    name: isAH ? 'Access handler' : row.record_ref.displayValue,
                    decisionType: row.decision_type.displayValue,
                    appliesToCondition: row.applies_to_condition_result.displayValue,
                    empty: row.empty.displayValue,
                    appliesTo: row.applies_to.value,
                    type: row.type.value
                },
                userData: {
                    id: row.access_result.value,
                    status: row.status.value,
                    script: row.script_execution.value,
                    value: {
                        sysId: row._row_data.uniqueValue,
                    }
                }
            };
        }

        resultRowMapper(row) {
            var result = super.resultRowMapper(row);
            result.decisionType = row.rowData.decisionType;
            result.appliesToCondition = row.rowData.appliesToCondition;
            result.empty = row.rowData.empty;
            result.appliesTo = row.rowData.appliesTo;
            result.index++;
            if (row.rowData.type == "Business Rule") {
                result.baseUser = "";
                result.modelUser = "";
            }
            return result;
        }
    }

    //--------------------------------------------------------------------------------//

    class ACLDetailComparator extends AccessComparator {

        getComparisonResult(result) { 
            return super.getComparisonResult(this.getModifiedResult(result));
        }

        getModifiedResult(result){
            var list = [];
            result.forEach(row => {
                this.add(list, row, "Roles", "roles_result")
                this.add(list, row, "Security Attribute", "security_attribute");
                this.add(list, row, "Condition", "condition_result");
                this.add(list, row, "Script", "script_result");
            });
            return list;
        }

        add(list, row, label, name) {
            list.push({
                rowData: {
                    id: name,
                    name: label,
                },
                userData: {
                    id: row._row_data.uniqueValue,
                    status: row[name] ? row[name].value : EvalStatus.SKIPPED.value,
                    script: label == "Script" ? row.script_execution.value : null
                }
            });
        }

        resultRowMapper(row) {
            var result = super.resultRowMapper(row);
            result.name.value = row.rowData.name;
            return result;
        }

    }


    //--------------------------------------------------------------------------------//

    class SimulatedResultsAllOperationComparator extends AccessComparator {

        requestRowMapper(row) {
            return {
                rowData: {
                    id: row.name, //read,write,..
                    name: row.name,
                },
                userData: {
                    id: row.category, //present_status or simualted_status
                    status: row.status,
                    script: row.containsScriptedACL ? 'ACL' : null
                }
            }
        }
    }

    //--------------------------------------------------------------------------------//

    class SimulatedResultsDebugLogComparator extends DebugLogComparator {

        sortLogs(result, prefOrder) {
            result.sort(function (a, b) {
                if (a.log_sequence != b.log_sequence) {
                    return a.log_sequence - b.log_sequence;
                }
                return prefOrder.indexOf(a.type) - prefOrder.indexOf(b.type);
            });
        }

        requestRowMapper(row) {
            var isAH = row.type == "Access Handler";
            var isACL = row.type == "ACL";
            return {
                rowData: {
                    id: isAH ? row.appliesTo : row.sysId,
                    name: isAH ? 'Access handler' : row.name,
                    decisionType: isACL ? row.aclDecisionType : '',
                    appliesToCondition: isACL ? row.appliesToConditionResult : '',
                    empty: isACL? row.empty : '',
                    appliesTo: row.appliesTo,
                    type: row.type
                },
                userData: {
                    id: row.category, //present_status or simualted_status
                    status: row.status,
                    script: row.hasScript ? 'Script' : null,
                    value: {
                        acl: row,
                    }
                }
            };
        }
    }

    //--------------------------------------------------------------------------------//

    class SimulatedResultsACLDetailComparator extends ACLDetailComparator {
        getModifiedResult(result) {
            var list = [];
            result.forEach(row => {
                this.add(list, row, "Roles", "rolesResult")
                this.add(list, row, "Security Attribute", "securityAttributeResult");
                this.add(list, row, "Condition", "conditionResult");
                this.add(list, row, "Script", "scriptResult");
            });
            return list;
        }

        add(list, row, label, name) {
            list.push({
                rowData: {
                    id: name,
                    name: label,
                },
                userData: {
                    id: row.category,
                    status: row[name] ? row[name] : EvalStatus.SKIPPED.value,
                    script: label == "Script" && row.hasScript ? 'Script' : null
                }
            });
        }

    }

    //--------------------------------------------------------------------------------//

    return {
        getComparator: function (type, baseUserRef, modelUserRef) {
            switch (type) {
                case "user":
                    return new UserComparator(baseUserRef, modelUserRef)
                case "role":
                    return new RoleComparator(baseUserRef, modelUserRef);
                case "group":
                    return new GroupComparator(baseUserRef, modelUserRef);
                case "operation":
                    return new OperationComparator(baseUserRef, modelUserRef);
                case "debuglog":
                    return new DebugLogComparator(baseUserRef, modelUserRef);
                case "acl":
                    return new ACLDetailComparator(baseUserRef, modelUserRef);
                case "simulated_results_all_operation":
                    return new SimulatedResultsAllOperationComparator(baseUserRef, modelUserRef);
                case "simulated_results_debug_logs":
                    return new SimulatedResultsDebugLogComparator(baseUserRef, modelUserRef);
                case "simulated_results_acl_detail":
                    return new SimulatedResultsACLDetailComparator(baseUserRef, modelUserRef);
            }
        }
    }

}

