function include({
    imports
}) {
    const {
        ERROR_NOTIFICATION_PROPS,
        SUCCESS_NOTIFICATION_PROPS
    } = imports["sn_cmdb_ws.CMDBWsNotificationUtil"]();

    const LIFECYCLE_TASK_ACTION = {
        DM_EXCLUDE_RECORDS: 'DM_EXCLUDE_RECORDS'
    };

    const TASK_PAGE_PARAMS = {
        MY_WORK: {
            REFRESH_LIST: 'refreshList',
            SHOW_NOTIFICATION: 'showNotification'
        }
    };

    const TASK_FILTERS = {
        IS_OVERDUE: "due_date<javascript:gs.beginningOfToday()",
        IS_NOT_CLOSED: "stateNOT IN3,4,7,8",
        ASSIGNED_TO_IS_EMPTY: "assigned_toISEMPTY",
        ASSIGNMENT_GROUP_IS_EMPTY: "assignment_groupISEMPTY",
        IS_ASSIGNED_TO_ME: "assigned_toDYNAMIC90d1921e5f510100a9ad2572f2b477fe",
        IS_ASSIGNED_TO_MY_GROUP: "assignment_groupDYNAMICd6435e965f510100a9ad2572f2b47744"
    };

    var _validateTaskRecord = function (taskRecord, api) {
        if (taskRecord) {
            const isAdminUser = taskRecord.userIsAdminOrMaint || taskRecord.userIsDataManagerAdmin;
            const recordNotFound = taskRecord.invalidTask || !isAdminUser && (taskRecord.userNotAssigned && taskRecord.userNotWithinGroup);
            api.setState('recordNotFound', recordNotFound);
        } else {
            api.setState('recordNotFound', true);
        }
    }

    var _handleRemoveNotifications = function (emit, notificationId, helpers) {
        const timeoutId = helpers.timing.setTimeout(() => {
            emit('NOW_UXF_PAGE#REMOVE_NOTIFICATIONS', {
                items: [{
                    id: notificationId
                }]
            });
        }, 5000);
    }

    var _handleLifecycleTaskNotification = async function (response, api, event, helpers) {
        const {
            error,
            action,
            successCount,
            failureCount
        } = response.result;

        const notifications = [];

        if (error && error.message) {
            notifications.push({
                ...ERROR_NOTIFICATION_PROPS,
                "content": await helpers.translate("Error - {0}", error.message)
            });
        }
        if (successCount > 0) {
            let messageContent = '';
            let txtLinkProp = {};
            if (action === LIFECYCLE_TASK_ACTION.DM_EXCLUDE_RECORDS) {
                messageContent = successCount > 1 ?
                    await helpers.translate("{0} records excluded", successCount) :
                    await helpers.translate("{0} record excluded", successCount);
            }
            const notificationId = 'successId' + new Date().valueOf();
            notifications.push({
                ...SUCCESS_NOTIFICATION_PROPS,
                "content": messageContent,
                "textLinkProps": txtLinkProp,
                "id": notificationId
            });
        }
        if (failureCount > 0) {
            notifications.push({
                ...ERROR_NOTIFICATION_PROPS,
                "content": await helpers.translate("Unable to exclude {0} CIs", failureCount)
            });
        };
        if (notifications.length > 0) {
            api.emit("NOW_UXF_PAGE#ADD_NOTIFICATIONS", {
                items: notifications
            });
        }

        if (notifications.length > 0) {
            notifications.forEach(item => {
                _handleRemoveNotifications(api.emit, item.id, helpers);
            });
        }
    }

    return {
        LIFECYCLE_TASK_ACTION,
        TASK_PAGE_PARAMS,
        TASK_FILTERS,
        validateTaskRecord: _validateTaskRecord,
        handleRemoveNotifications: _handleRemoveNotifications,
        handleLifecycleTaskNotification: _handleLifecycleTaskNotification
    }
}

function include({imports}) {
	const {getRelativeDateTime, getTimeTillDateChangeInMillis} = imports['sn_sow_on_call.SOWOnCallRelativeDateTime']();

	const translateDateTime = async function(relDT, helpers) {
		if (!relDT) return;
		let relDay = relDT[0];
		let relTime = relDT[1];
		if (relDay.indexOf('today') !== -1) {
			return await helpers.translate('Today at {0}', relTime);
		} else if (relDay.indexOf('tomorrow') !== -1) {
			return await helpers.translate('Tomorrow at {0}', relTime);
		} else if (relDay.indexOf('yesterday') !== -1) {
			return await helpers.translate('Yesterday at {0}', relTime);
		} else {
			const month = relDay.split('-')[1];
			let translatedMonth = '';
			switch(month) {
				case 'Jan':
					translatedMonth = await helpers.translate('January');
					break;
				case 'Feb':
					translatedMonth = await helpers.translate('February');
					break;
				case 'Mar':
					translatedMonth = await helpers.translate('March');
					break;
				case 'Apr':
					translatedMonth = await helpers.translate('April');
					break;
				case 'May':
					translatedMonth = await helpers.translate('May');
					break;
				case 'Jun':
					translatedMonth = await helpers.translate('June');
					break;
				case 'Jul':
					translatedMonth = await helpers.translate('July');
					break;
				case 'Aug':
					translatedMonth = await helpers.translate('August');
					break;
				case 'Sep':
					translatedMonth = await helpers.translate('September');
					break;
				case 'Oct':
					translatedMonth = await helpers.translate('October');
					break;
				case 'Nov':
					translatedMonth = await helpers.translate('November');
					break;
				case 'Dec':
					translatedMonth = await helpers.translate('December');
					break;
				default:
					break;
			}
			if (translatedMonth) relDay = relDay.replace(month, translatedMonth);
		}
		return await helpers.translate('{0} at {1}', relDay, relTime);
    };

	const formatDateTime = async function(obj, tgtTZ, helpers, time_fmt) {
		if (!obj) return;
		const keys = Object.keys(obj);
		for(let i = 0; i < keys.length; i++) {
			let el = keys[i];
			if( typeof obj[el] === 'object' && obj[el]) {
				await formatDateTime(obj[el], tgtTZ, helpers, time_fmt);
			}

			if (el === 'display_value_relative' && obj['display_value_internal']) {
				obj['display_value_relative'] = {
					dateTime: await translateDateTime(getRelativeDateTime(obj['display_value_internal'], false, tgtTZ, time_fmt), helpers),
					time: getRelativeDateTime(obj['display_value_internal'], true, tgtTZ, time_fmt)
				};
			} 
		}
	};

	return {
		formatDateTime,
		getTimeTillDateChangeInMillis
	};
}

