function resizablePanelUtils(proxyArg, params) {
    const { api, event, helpers, imports } = params;

    const SIDEBAR_CLOSED_DEFAULT_POSITION = "-48px";
    const SIDEBAR_CLOSED_CONSTRAINT = { "minPixels": 48 };
    const SIDEBAR_OPEN_CONSTRAINT = { "minRatio": 20, "minPixels": 370 };
    const SIDEBAR_OPEN_DEFAULT_POSITION = "75";

    const pageId = (api.context.props.pageId || "").replace(" ", "_");
    const SIDEBAR_RATIO_PREF_NAME = `cmdbws.${pageId}.sidebar-ratio`;
    const SHOW_SIDEBAR_PREF_NAME = `cmdbws.${pageId}.show-sidebar`;

    const onMacroponentReady = () => {
        api.setState('lookupPreferenceNames', [SHOW_SIDEBAR_PREF_NAME, SIDEBAR_RATIO_PREF_NAME].join(","));
    }

    const getUserPreferenceMap = () => {
        const userPrefs = api.data?.lookupSidebarUserPref?.result?.preferences;
        const userPrefMap = new Map();
        userPrefs.forEach(item => {
            if (item.value != undefined || item.value != null) {
                userPrefMap.set(item.name, item.value);
            }
        });
        return userPrefMap;
    };

    const getUserPreference = (preference, defaultValue) => {
        const userPrefMap = getUserPreferenceMap();
        if (userPrefMap.has(preference)){
            if (userPrefMap.get(preference) === 'true' || userPrefMap.get(preference) === true)
                return true;
            if (userPrefMap.get(preference) === 'false' || userPrefMap.get(preference) === false)
                return false;
            return userPrefMap.get(preference);
        } else {
            return defaultValue;
        }
    };

    function toggleSidebar(isHidden) {
		if (isHidden) {
			// If the savedSidebarRatio has not been initialized before hiding the element
			// then save it before collapsing the sidebar.
			if (!api.state.sidebarRatioSaved)
				api.setState('sidebarRatioSaved', api.state.sidebarDividerPosition);

			// close the sidebar
			api.setState("sidebarPaneContraints", SIDEBAR_CLOSED_CONSTRAINT);
			api.setState("sidebarDividerPosition", SIDEBAR_CLOSED_DEFAULT_POSITION);
		} else {
			// open the sidebar
			api.setState("sidebarPaneContraints", SIDEBAR_OPEN_CONSTRAINT);
			api.setState("sidebarDividerPosition", api.state.sidebarRatioSaved);
		}
		api.setState("sidebarDisabled", isHidden);
    	api.data.setUserPref.execute({
			name: SHOW_SIDEBAR_PREF_NAME,
			value: !isHidden
		});
	};

    function initializeSidebarPosition () {
        const userPrefMap = getUserPreferenceMap(); 
        const sidebarOn = getUserPreference(SHOW_SIDEBAR_PREF_NAME, true);
        const sidebarDisabled = !sidebarOn;
        const sidebarRatioSavedValue = getUserPreference(SIDEBAR_RATIO_PREF_NAME, SIDEBAR_OPEN_DEFAULT_POSITION);
        const sidebarRatioSaved = typeof sidebarRatioSavedValue === 'string' ? sidebarRatioSavedValue : null;
        const position = sidebarOn ? sidebarRatioSaved : SIDEBAR_CLOSED_DEFAULT_POSITION;
        if (sidebarDisabled)
            api.setState("sidebarPaneContraints", SIDEBAR_CLOSED_CONSTRAINT);
        else
            api.setState("sidebarPaneContraints", SIDEBAR_OPEN_CONSTRAINT);
        if (sidebarRatioSaved)
            api.setState('sidebarRatioSaved', sidebarRatioSaved);

        api.setState('sidebarDisabled', sidebarDisabled);
        if (position)
            api.setState('sidebarDividerPosition', position);
    };

    function resizePane(dividerPosition) {
        api.setState('sidebarRatioSaved', dividerPosition);
        api.data.setUserPref.execute({
            name: SIDEBAR_RATIO_PREF_NAME,
            value: dividerPosition
        });
    }

    return {
        onMacroponentReady,
        toggleSidebar,
        initializeSidebarPosition,
        getUserPreference,
        resizePane
    };
}

function include() {

	// Constructs payload for UI navigation to CoreUI list view
	function getCoreUIListNavigationPayload(coreUIParam, title) {
		return {
			context: {
				path: 'now/nav/ui'
			},
			params: {
				target: coreUIParam,
			},
			route: 'classic',
			title: title,
			navigationOptions: {},
		}
	}

	// Constructs payload for UI navigation to CoreUI form view
	function getCoreUIFormNavigationPayload(coreUIParam, title) {
		return {
			context: {
				path: 'now/nav/ui'
			},
			params: {
				target: coreUIParam,
			},
			route: 'classic',
			title: title || 'Form Record',
			navigationOptions: {},
		};
	}

	// Constructs payload for UI navigation to Seismic list view
	function getSeismicListNavigationPayload(table, query, title, drilldownListView) {
		return  {
			route: 'simplelist',
			fields: {table: table || ''},
			params: {
				query: query || '',
				listTitle: title || table,
				listView: drilldownListView || '-1',
			},
			title: title,
			navigationOptions: {},
		};
	}

	// Constructs payload for UI navigation to Seismic form view
	function getSeismicFormNavigationPayload(table, sysId, title){
		return{
			route: 'record',
			fields: {
				table: table || '',
				sysId: sysId || '',
			},
			title: title || 'Form Record',
			navigationOptions: {},
		};
	}

        const navigateToCoreUI = (url, globalWindow) => {
                 if (!globalWindow) return;
	         const urlPrefix = '/nav_to.do?uri=/';
	         globalWindow.open(urlPrefix + encodeURIComponent(url), '_self');
        };

	return {
		getPayload (event,  isPolarisDisabled, globalThis) {
			const {eventOrigin, componentProps, widgetProps, drilldownToCoreUI} = event.payload;
			const drilldownListView = widgetProps?.drilldownListView;
                        const globalWindow = globalThis?.window;
			let payload;

			if (eventOrigin === 'NOW_VIS_WRAPPER#CLICKED') {
				const {
					params: {
						table,
						query,
						indicatorSysid,
						kpiSysId,
						breakdowns,
						aggregate,
						domain,
						scoreDate,
						dataSourceType,
					},
					title,
				} = event.payload;

				if (dataSourceType === 'metricBase') {
					const {
						params: {
							dateRange: {
								startTime,
								endTime,
							},
							ciSysId,
							ciType,
						},
					} = event.payload;
					payload = {
						route: 'metric-explorer', 
						params: {
							ciSysId,
							ciType,
							startTime,
							endTime,
							chartIndex: -1,
						},
						title: 'Metric Explorer',
					};
				} else if (dataSourceType === 'hlaElasticSearchComponents') {
					payload = {
						route: 'log-viewer',
						params: {},
						title: 'Log viewer',
					};
				} else if (dataSourceType === 'table') {
					const coreUIParam = `${table}_list.do?sysparm_query=${query}&sysparm_view=${drilldownListView}`;
                                        if (drilldownToCoreUI && isPolarisDisabled) {
                                                    navigateToCoreUI(coreUIParam, globalWindow)
                                                    return;
                                        }
					payload = drilldownToCoreUI ? getCoreUIListNavigationPayload(coreUIParam, title) : getSeismicListNavigationPayload(table, query, title || table, drilldownListView);
				} else if (kpiSysId) {
					payload = {
						fields: {
							dataSourceId: kpiSysId,
							dataSource: 'analyticalmodel',
						},
						params: {
							breakdowns,
							aggregate,
							scoreDate,
							domain,
							listView: drilldownListView || '-1',
						},
						route: 'kpi-details',
						title: title || 'KPI Details',
					};
				} else if (dataSourceType === 'indicator') {
					payload = {
						fields: {
							dataSourceId: indicatorSysid,
							dataSource: 'kpid',
						},
						params: {
							breakdowns,
							aggregate,
							scoreDate,
							domain,
							listView: drilldownListView || '-1',
						},
						route: 'kpi-details',
						title: title || 'KPI Details',
					};
				}
			}

			if (eventOrigin === 'NOW_RECORD_LIST_CONNECTED#ROW_CLICKED') {
				const {table, row, title} = event.payload;
				const sysId = row?.sys_id?.value;
				const coreUIParam = `${table}.do?sys_id=${sysId || '-1'}&sysparm_view=${drilldownListView}`;
                                 if (drilldownToCoreUI && isPolarisDisabled) {
                                                    navigateToCoreUI(coreUIParam, globalWindow)
                                                    return;
                                }
				payload = drilldownToCoreUI ? getCoreUIFormNavigationPayload(coreUIParam, title) : getSeismicFormNavigationPayload(table, sysId, title);
			}

			if (eventOrigin === 'SN_PAR_CALENDAR_CONNECTED#EVENT_CLICKED') {
				const {table, title, sysId} = event.payload.event;

				const coreUIParam = `${table}.do?sys_id=${sysId || '-1'}&sysparm_view=${drilldownListView}`;
                                 if (drilldownToCoreUI && isPolarisDisabled) {
                                                    navigateToCoreUI(coreUIParam, globalWindow)
                                                    return;
                                 }
                                 payload = drilldownToCoreUI ? getCoreUIFormNavigationPayload(coreUIParam, title) : getSeismicFormNavigationPayload(table, sysId, title);
			}

			if (eventOrigin === 'NOW_RECORD_LIST_CONNECTED#VIEW_ALL_CLICKED') {
				const {table, query, listTitle, currentTabParFilters} = event.payload;
				const listFilters = componentProps?.fixedQuery;
				const filterQueries = currentTabParFilters && currentTabParFilters.encodedQueries && currentTabParFilters.encodedQueries[table] ? `${currentTabParFilters.encodedQueries[table]}^` : '';
				let queryWithFilterClauses = `${filterQueries}${query || ''}`;
				if (listFilters)
					queryWithFilterClauses = `${queryWithFilterClauses}^${listFilters}`;

				const coreUIParam = `${table}_list.do?sysparm_query=${queryWithFilterClauses}&sysparm_view=${drilldownListView}`;
                                if (drilldownToCoreUI && isPolarisDisabled) {
                                               navigateToCoreUI(coreUIParam, globalWindow)
                                               return;
                                }

				payload = drilldownToCoreUI ? getCoreUIListNavigationPayload(coreUIParam, listTitle) : getSeismicListNavigationPayload(table, queryWithFilterClauses, listTitle || 'List', drilldownListView);
			}

			if (event.payload.navigationOptions) {
				payload.navigationOptions = event.payload.navigationOptions;
			}
			return payload;
		},
	};
}

function include({
    imports
}) {
    return {
        updatePropsDetailsForCtaCard: updatePropsDetailsForCtaCard,
        getCurrentDateTime: getCurrentDateTime,
        shuffleCtaCards: shuffleCtaCards
    };

    function getPageBodyContainer(contentBlocks) {
        return contentBlocks[1];
    }

    function updatePropsDetailsForCtaCard(contentBlocks, cardId, propsDetails) {
        patchPropsDetails(cardId, propsDetails, contentBlocks);

        function patchPropsDetails(cardId, propsDetails, contentBlocks) {
            for (let i = 0; i < contentBlocks.length; i++) {
                const contentBlock = contentBlocks[i];
                if (contentBlock.block_id === cardId) {
                    contentBlocks[i].props_details = contentBlocks[i].props_details || {};
                    contentBlocks[i].props_details = { ...contentBlocks[i].props_details,
                        ...propsDetails
                    };
                    break;
                } else if (Array.isArray(contentBlock.children) && contentBlock.children.length) {
                    patchPropsDetails(cardId, propsDetails, contentBlock.children);
                }
            }
        }
    }

    function getCurrentDateTime() {
        var dateTime = new Date();
        return dateTime.getUTCFullYear() + "-" +
            ('0' + (dateTime.getUTCMonth() + 1)).slice(-2) + "-" +
            ('0' + dateTime.getUTCDate()).slice(-2) + " " +
            ('0' + dateTime.getUTCHours()).slice(-2) + ":" +
            ('0' + dateTime.getUTCMinutes()).slice(-2) + ":" +
            ('0' + dateTime.getUTCSeconds()).slice(-2);
    }

    function shuffleCtaCards(contentBlocks) {
        class CTACard {
            constructor(card) {
                this.card = card;
                this.card.props_details = this.card.props_details || {};
            }

            /**
             * return -1 if card is pending, else the 'last action on' date time in ms
             */
            getLastActionOnInMs() {
                var lastActionOn = this.card.props_details.last_action_on;
                if (!lastActionOn)
                    return -1;

                lastActionOn = lastActionOn.replace(/\s/, 'T');

                var lastActionOnDateTimeInMs = new Date(lastActionOn).getTime() || 0;
                return lastActionOnDateTimeInMs;
            }

            isComplete() {
                if (this.card.hasOwnProperty('__is_complete')) {
                    return (this.card.__is_complete == true);
                } else if (this.card.children) {
                    var screens = this.card.children;
                    var screenIndex;
                    for (screenIndex = 0; screenIndex < screens.length; screenIndex++) {
                        var screen = screens[screenIndex];
                        if (screen.hasOwnProperty('__is_complete')) {
                            return (screen.__is_complete == true);
                        }
                    }
                    return true;
                }
            }

            getCard() {
                return this.card;
            }
        }

        let ctaCards = getCtaCards(contentBlocks);
        ctaCards = ctaCards.map(card => new CTACard(card));

        ctaCards.sort((card1, card2) => card1.getLastActionOnInMs() - card2.getLastActionOnInMs());

        let tier1Cards = [];
        let tier2Cards = [];

        ctaCards.forEach(function(card) {
            var canPushToTier1 = (!card.isComplete() && (tier1Cards.length < 3));
            if (canPushToTier1) {
                tier1Cards.push(card.getCard());
            } else {
                tier2Cards.push(card.getCard());
            }
        });

        setTier1Cards(tier1Cards, contentBlocks);
        setTier2Cards(tier2Cards, contentBlocks);

        return [ ...contentBlocks ];
    }

    function getCtaCards(contentBlocks) {
        var tier1 = getTier1Cards(contentBlocks);
        var tier2 = getTier2Cards(contentBlocks);

        return tier1.concat(tier2);
    }

    function setTier1Cards(tierChildren, contentBlocks) {
        let pageBodyContainer = getPageBodyContainer(contentBlocks);
        setTier(1, tierChildren, pageBodyContainer);
    }

    function setTier2Cards(tierChildren, contentBlocks) {
        let pageBodyContainer = getPageBodyContainer(contentBlocks);
        setTier(2, tierChildren, pageBodyContainer);
    }

    function getTier1Cards(contentBlocks) {
        let pageBodyContainer = getPageBodyContainer(contentBlocks);
        return getTierCards(1, pageBodyContainer);
    }

    function getTier2Cards(contentBlocks) {
        let pageBodyContainer = getPageBodyContainer(contentBlocks);
        return getTierCards(2, pageBodyContainer);
    }

    function setTier(tierNumber, tierChildren, pageBodyContainer) {
        var tierIndex = findTierIndex(tierNumber, pageBodyContainer);
        pageBodyContainer.children[tierIndex].children = tierChildren;
    }

    function getTierCards(tierNumber, pageBodyContainer) {
        var tierIndex = findTierIndex(tierNumber, pageBodyContainer);
        if (tierIndex == -1) {
            return [];
        }
        const ctaTier = pageBodyContainer.children[tierIndex];
        return ctaTier.children || [];
    }

    function findTierIndex(tierNumber, context) {
        if (!context || !Array.isArray(context.children))
            return -1;

        var index;
        for (index = 0; index < context.children.length; index++) {
            var child = context.children[index];
            if (child.__cta_tier == tierNumber) {
                return index;
            }
        }

        return -1;
    }
}
