result.script:
/**
 * Data broker to add groups to subscription, if not already added
 * @param {object} input - Gets subscriptionId and groupIds as input as defined in the Properties
 * @return {string}
 */
function transform(input) {
    const subscriptionId = input.subscriptionId;
    const groupIds = input.groupIds;
    if (!subscriptionId || subscriptionId.length === 0 || !groupIds || groupIds.length === 0) {
        // Counter-intuitively, this script gets called multiple times and often with an empty group list
        // Returning 'false' here will make other scripts think that the allocation failed and display error messages
        // Returning 'true' with the input guard is necessary for the downstream behavior to work as desired.
        return 'true';
    }

    const maxEntityCountToProcessSync = parseInt(gs.getProperty('sn_sub_man.add_group_max_entity_count_for_sync', 500));

    const groupAllocationService = new sn_sub_man.GroupAllocationService();
    const entityCount = groupAllocationService.getEntityCountByGroupIds(groupIds);

    if (entityCount > maxEntityCountToProcessSync) {
        // Execute sync
        groupAllocationService.addGroupsToSubscription(groupIds, subscriptionId);
        groupAllocationService.addGroupMembersToSubscription(groupIds, subscriptionId);

        // Execute async
        const steps = gs
            .getProperty('sn_sub_man.add_group_async_steps', 'executePartialUpdateForGroups')
            .split(',');
        const args = {
            subscriptionId: subscriptionId,
            groupIds: groupIds
        };

        gs.eventQueue('sn_sub_man.add_groups_to_subscription', null, JSON.stringify(steps), JSON.stringify(args));

        const asyncInfoMessage = sn_i18n.Message.getMessage('Subscription Management', 'An update job has started and will continue in the background. Allocations will be updated when the process completes. Refresh the page and check allocations in a few minutes.');
        gs.addInfoMessage(asyncInfoMessage);
    } else {
        // Execute sync
        groupAllocationService.addGroupsToSubscription(groupIds, subscriptionId);
        groupAllocationService.addGroupMembersToSubscription(groupIds, subscriptionId);
        groupAllocationService.executePartialUpdateForGroups(groupIds);
    }

    return 'true';
}

