function transform(input) {
	if (!input.changeState)
		return [];


	var currentStates = [];
	var order = 0;
	if (input.changeModel) {
		var chg_model = new GlideRecordSecure('chg_model');
		if (chg_model.get(input.changeModel)) {
			var modelStates = new global.ChangeModel(chg_model).getStates();
			while(modelStates.next()) {
				var modelChoice = {
					"value": modelStates.getValue("state_value"),
					"label": modelStates.getDisplayValue("state_label"),
					"order": order++
				};
				currentStates.push(modelChoice);
			}
		}
	} else {
		var blankModel = {
			table_name: "change_request",
			state_field: "state",
			getValue: function(name) { return this[name];}
		};
		var choiceGr = new global.STTRMStateChoiceUtil(blankModel)._getSysChoices();
		while (choiceGr.next()) {
			if (choiceGr.getValue("inactive") === "1")
				continue;

			var chgChoice = {
				"value": choiceGr.getValue('value'),
				"label": choiceGr.getValue('label'),
				"order": order++
			};
			currentStates.push(chgChoice);
		}
	}

	var stepperData = {};
	var beforeState = true;
	stepperData.steps = [];
	for (var i = 0; i < currentStates.length; i++) {
		var step = {};
		step.id = 'step' + (i+1);
		step.label = currentStates[i].label;
		if (input.changeState + '' === currentStates[i].value + '') {
			step.progress = 'partial';
			stepperData.selected = step.id;
			beforeState = false;
		} else {
			if (beforeState)
				step.progress = 'done';
			else
				step.progress = 'none';
		}

		stepperData.steps.push(step);
	}
	return stepperData;

}

