result.script:
function transform(input) {
    var policyManager = new sn_vsc.SecurityPoliciesManager();
    var resultData = input.action_attributes || {};
    var checkboxData = input.checkbox_value || {};
    var id = input && input.sys_id;
    var removedConditionsIds = input.removed_conditions_ids || [];
    var removedNotificationsIds = input.removed_notifications_ids || [];
    var condition_logic = input.condition_logic || "";
    var selectedConditionWithoutValues = input.selected_condition_ids || [];
    var sysId = [];
    var columnName = "";
    var commaSeparatedIds = "";
    var columnsData = {};
    var policyConditionTableName = "sn_vsc_security_policy_condition";

    if (selectedConditionWithoutValues.length > 0) {
        for (var selectedConditionId in selectedConditionWithoutValues) {
            insertOrUpdatePolicy(selectedConditionWithoutValues[selectedConditionId], "", "", policyConditionTableName);
        }
    }

    for (var modelIdVal in checkboxData) {
        if (checkboxData[modelIdVal] == true) {
            var actionInputObjVal = new GlideRecord("sys_hub_action_input");
            if (actionInputObjVal.get("model_id", modelIdVal)) {
                columnName = actionInputObjVal.getDisplayValue("element");
            }
            commaSeparatedIds = "";
        }
        insertOrUpdatePolicy(modelIdVal, commaSeparatedIds, columnName, policyConditionTableName);
    }

    for (var modelIdObj in resultData) {
        var modelId = modelIdObj.split(",")[0];
        var resultObj = resultData[modelId];
        modelId = modelId.split("_")[0];
        var actionInputObj = new GlideRecord("sys_hub_action_input");
        actionInputObj.addEncodedQuery("model_id=" + modelId, true);
        actionInputObj.query();
        var newRecordSysId = resultObj["policy_action_sys_id"];
        // To allow multiple notifications to be added for the same policy, we would need to update/create records accordingly
        var notificationSysId = resultObj["policy_action_sys_id"];
        var isNewEntry = resultObj["create_new_entry"];
        while (actionInputObj.next()) {
            commaSeparatedIds = "";
            columnName = actionInputObj.getDisplayValue("element");
            // Need to check if the user has not touched the pre-existing values. 
            if (resultData[modelIdObj][columnName]) {
                var variablesData = resultData[modelIdObj][columnName]["value"];
                var tableToUpdate = resultData[modelIdObj][columnName]["tableToUpdate"];
                sysId = [];
                if (variablesData) {
                    for (var value in variablesData) {
                        var attributeId = variablesData[value].id;
                        if (attributeId) {
                            sysId.push(attributeId);
                        }
                    }
                }

                if (sysId.length) {
                    commaSeparatedIds = sysId.map(String).join(', ').toString();
                }

                // The column name needs to be passed so we can update same model for different fields ( when we have more than one param inside single condition/notification)
                // Specially a case for when same type of notification is added for a given policy.
                if (newRecordSysId) {
                    insertOrUpdatePolicy(modelId, commaSeparatedIds, columnName, tableToUpdate, newRecordSysId, false);
                } else {
                    newRecordSysId = insertOrUpdatePolicy(modelId, commaSeparatedIds, columnName, tableToUpdate, notificationSysId, isNewEntry);
                }
            }
        }
    }

    // Update conditions/notifications for the policy
    function insertOrUpdatePolicy(modelSysId, commaSeparatedIds, columnToUpdate, tableToUpdate, recordSysId, createNewEntry) {
        var gr = new GlideRecord(tableToUpdate);
        if (recordSysId) {
            gr.get(recordSysId);
        } else {
            gr.addEncodedQuery("action_type=" + modelSysId + "^policy=" + id, true);
        }
        gr.query();
        // We might end up receiving multiple notifications for the same policy. So in those cases the 'createNewEntry' will decide to create new or not
        if (gr.next() && !createNewEntry) {
            gr.action_attributes[columnToUpdate] = commaSeparatedIds;
            gr.update();
        } else {
            //insert condition/notification
            var insertGr = new GlideRecord(tableToUpdate);
            insertGr.initialize();
            insertGr.policy = id;
            insertGr.action_type = modelSysId;
            insertGr.action_attributes[columnToUpdate] = commaSeparatedIds;
            insertGr.insert();
            return insertGr.getValue('sys_id');
        }
    }

    if (removedConditionsIds && removedConditionsIds.length) {
        // Delete conditions for the policy when removed button is clicked
        for (var i = 0; i < removedConditionsIds.length; i++) {
            var removeConditionsGr = new GlideRecord('sn_vsc_security_policy_condition');
            removeConditionsGr.addQuery("action_type", removedConditionsIds[i]);
            removeConditionsGr.addQuery("policy", id);
            removeConditionsGr.query();
            if (removeConditionsGr.next()) {
                removeConditionsGr.deleteRecord();
            }
        }
    }

    if (removedNotificationsIds && removedNotificationsIds.length) {
        // Delete notifications for the policy when removed button is clicked
        for (var j = 0; j < removedNotificationsIds.length; j++) {
            var removeNotificationGr = new GlideRecord('sn_vsc_security_policy_action');
            removeNotificationGr.addQuery("sys_id", removedNotificationsIds[j]);
            removeNotificationGr.query();
            if (removeNotificationGr.next()) {
                removeNotificationGr.deleteRecord();
            }
        }
    }

    if (condition_logic) {
        var gr = new GlideRecord("sn_vsc_security_policy");
        if (gr.get(id)) {
            gr.setValue("events_criteria_type", condition_logic);
            gr.update();
        }
    }

    policyManager.updatePolicyConfigureValue(id);
}

function transform(input) {
	for(var i = 0; i < input.evambroker.items.length; i++){
		var model = input.evambroker.items[i].propValues;
		var currDate = new GlideDateTime();
