result.script:
function transform(inputs) {
    var result = {};
    var ocTeamsCalendarUtil = new sn_uib_on_call.OnCallTeamsCalendarUtil();
    ocTeamsCalendarUtil.clearErrorMessages();

    var allGroups = [];
    var aggregateGr = new GlideAggregate('cmn_rota');
    aggregateGr.addQuery('active', true); //Show only active shifts count
    aggregateGr.groupBy('group');
    aggregateGr.groupBy('state');
    aggregateGr.addAggregate('COUNT');
    aggregateGr.query();
    var groupCounts = {};
    var groupsSysIds = [];
    while (aggregateGr.next()) {
        // Show only published shifts and ignore drafts shifts
        if (!aggregateGr.getValue('state')) {
            groupCounts[aggregateGr.getValue('group')] = aggregateGr.getAggregate('COUNT');
        }
    }
    var groupGr = new GlideRecord('sys_user_group');
    groupGr.addActiveQuery();
    if (inputs.searchText)
        groupGr.addQuery("name", "CONTAINS", inputs.searchText);
    groupGr.setLimit(25); // TODO: Add a property to configure this value
    groupGr.query();

    while (groupGr.next()) {
        var userGroupSysId = groupGr.getValue('sys_id');
        var groupName = groupGr.getValue('name');
        let groupObj = {
            id: userGroupSysId,
            label: groupName,
            sublabel: gs.getMessage("(No active shifts)"),
            editable: new global.OCCalendarUtils().canWriteByGroupSysId(userGroupSysId) + ""
        };
        var finalNumberOfShifts = groupCounts[userGroupSysId] || 0;

        if (finalNumberOfShifts == 1) {
            groupObj.sublabel = gs.getMessage('(1 active shift)');
        } else if (finalNumberOfShifts > 1) {
            groupObj.sublabel = gs.getMessage('({0} active shifts)', finalNumberOfShifts);
        }
        allGroups.push(groupObj);
        groupsSysIds.push(userGroupSysId);
    }

    var groupSettingMap = new global.OCRosterSpanApprovalUtil().getPTOApprovalAndCoverageStatusByGroups(groupsSysIds);

    for (let i = 0; i < allGroups.length; i++) {
        allGroups[i]["isApprovalRequired"] = groupSettingMap[allGroups[i].id].isPTOAccessible + "";
        allGroups[i]["isProposedCoverMandatory"] = groupSettingMap[allGroups[i].id].isProposedCoverMandatory + "";
    }

    result.allGroups = allGroups;
    result.errorMessages = ocTeamsCalendarUtil.getErrorMessages();

    return result;
}

