result.script:
function transform(input) {
    var table = input.table;
    var idField = input.idField;
    var labelField = input.labelField;
    var internalNameField = input.internalNameField;

    if (!table || !gs.tableExists(table))
        return [];

    // Bypassing ACLs for sys_db_object to be able to show list of tables in condition builder dropdown
    var bypassACL = (table === 'sys_db_object' && idField == 'name' && internalNameField === 'name' && labelField === 'label') ? true : false;

    var encodedQuery = '';
    if (input.encodedQuery && input.encodedQuery.length) {
        encodedQuery = input.encodedQuery;
    }

    if (input.searchTerm && encodedQuery.length) {
        encodedQuery = encodedQuery + '^' + input.searchField + 'LIKE' + input.searchTerm;
    } else if (input.searchTerm) {
        encodedQuery = input.searchField + 'LIKE' + input.searchTerm;
    }

    var dropdownvalues = [];
    var label = '';

    var recordGr = new GlideRecord(table);
    if (bypassACL || recordGr.canRead()) {
        recordGr.addEncodedQuery(encodedQuery, !bypassACL);
        if (!gs.nil(input.labelField)) {
            recordGr.orderBy(input.labelField);
        }
        recordGr.query();
        var rowCount = recordGr.getRowCount();
        if (rowCount === 0) {
            label = gs.getMessage('No results found');
        } else if (rowCount <= parseInt(input.limit, 10)) {
            label = gs.getMessage('Showing 1-{0} of {0}', [rowCount]);
        } else {
            label = gs.getMessage('Showing 1-{0} of {1}', [input.limit, rowCount]);
            recordGr.setLimit(parseInt(input.limit, 10));
            recordGr.query();
        }
        while (recordGr.next() && (bypassACL ||recordGr.canRead())) {
            dropdownvalues.push({
                id: recordGr.getValue(input.idField),
                label: recordGr.getDisplayValue(input.labelField),
                internal_name: recordGr.getValue(input.internalNameField),
            });
        }
    } else {
        label = gs.getMessage('No results found');
    }
    return [{
        id: 0,
        label: label,
        children: dropdownvalues
    }];
}

