function transform(input) {
	if (!input.problemState)
		return [];


	var currentStates = [];
	var order = 0;
	if (input.problemModel) {
		var prb_model = new GlideRecordSecure('prb_model');
		if (prb_model.get(input.problemModel)) {
			var modelStates = new global.ProblemModel(prb_model).getStates();
			while(modelStates.next()) {
				var modelChoice = {
					"value": modelStates.getValue("state_value"),
					"label": modelStates.getDisplayValue("state_label"),
					"order": order++
				};
				currentStates.push(modelChoice);
			}
		}
	} else {
		var blankModel = {
			table_name: "problem",
			state_field: "state",
			getValue: function(name) { return this[name];}
		};
		var choiceGr = new global.STTRMStateChoiceUtil(blankModel)._getSysChoices();
		while (choiceGr.next()) {
			if (choiceGr.getValue("inactive") === "1")
				continue;

			var prbChoice = {
				"value": choiceGr.getValue('value'),
				"label": choiceGr.getValue('label'),
				"order": order++
			};
			currentStates.push(prbChoice);
		}
	}

	var stepperData = {};
	var beforeState = true;
	stepperData.steps = [];
	for (var i = 0; i < currentStates.length; i++) {
		var step = {};
		step.id = 'step' + (i+1);
		step.label = currentStates[i].label;
		if (input.problemState + '' === currentStates[i].value + '') {
			step.progress = 'partial';
			stepperData.selected = step.id;
			beforeState = false;
		} else {
			if (beforeState)
				step.progress = 'done';
			else
				step.progress = 'none';
		}

		stepperData.steps.push(step);
	}
	return stepperData;
}

