function transform(input) {
	var numbers = input.phoneNumbers;
	var userId = gs.getUserID();

	var invalidNumbers = {
		numbers: [],
		isValid: true
	};

	if (!numbers || !numbers.length) return invalidNumbers;

	for (var numberIdx = 0; numberIdx < numbers.length; numberIdx++) {
		var number = numbers[numberIdx];
		if (!(new global.PhoneNumberValidationUtils().validatePhoneNumberForSysUser(number))) {
			invalidNumbers.isValid = false;
			invalidNumbers.numbers.push(number);
		}
	}

	if (invalidNumbers.numbers.length) {
		invalidNumbers.numbers = invalidNumbers.numbers.join(",");
	}

	return invalidNumbers;
}

