function transform(inputs) {
    if (!inputs.teamPreferencesData.groupId)
        return;
    if (!(new global.OnCallSecurityNG().rotaMgrAccess(inputs.teamPreferencesData.groupId)))
        return {};
    var allowRotaOverlapMapping = {
        "yes": "yes",
        "no": "no",
        "default_no": "default",
        "default_yes": "default"
    };
    var groupPrefGr = new GlideRecord('on_call_group_preference');
    groupPrefGr.addQuery('group', inputs.teamPreferencesData.groupId);
    groupPrefGr.setLimit(1);
    groupPrefGr.query();
    if (groupPrefGr.next()) {
        var rotaManagersArray = inputs.teamPreferencesData.rotaManagers;
        var idsArray = rotaManagersArray.map(function(manager) {
            return manager.id;
        });
        var idsString = idsArray.join(', ');
        groupPrefGr.setValue('rota_managers', idsString);
        groupPrefGr.setValue('pto_approval_config', inputs.teamPreferencesData.ptoApprovalConfig);
        groupPrefGr.allow_rota_overlap = allowRotaOverlapMapping[inputs.teamPreferencesData.allowRotaOverlap];
        if (inputs.teamPreferencesData.allowRotaOverlap == "yes" || inputs.teamPreferencesData.allowRotaOverlap == "default_yes") {
            groupPrefGr.escalation_rule_rota_overlap = inputs.teamPreferencesData.escalationRuleRotaOverlap;
        }
        if (inputs.teamPreferencesData.firstDayOfWeek) {
            groupPrefGr.setValue('first_day_of_the_week', inputs.teamPreferencesData.firstDayOfWeek);
        }
        groupPrefGr.update();
    } else {
        groupPrefGr.initialize();
        groupPrefGr.group = inputs.teamPreferencesData.groupId;
        var newRotaManagersArray = inputs.teamPreferencesData.rotaManagers;
        var newUserIdsArray = newRotaManagersArray.map(function(manager) {
            return manager.id;
        });
        var newUserIdsString = newUserIdsArray.join(', ');
        groupPrefGr.rota_managers = newUserIdsString;
        groupPrefGr.pto_approval_config = inputs.teamPreferencesData.ptoApprovalConfig;
        groupPrefGr.allow_rota_overlap = allowRotaOverlapMapping[inputs.teamPreferencesData.allowRotaOverlap];
        if (inputs.teamPreferencesData.allowRotaOverlap == "yes" || inputs.teamPreferencesData.allowRotaOverlap == "default_yes") {
            groupPrefGr.escalation_rule_rota_overlap = inputs.teamPreferencesData.escalationRuleRotaOverlap;
        }
        if (inputs.teamPreferencesData.firstDayOfWeek) {
            groupPrefGr.setValue('first_day_of_the_week', inputs.teamPreferencesData.firstDayOfWeek);
        }
        groupPrefGr.insert();
    }
}

