			}

            /*var laneDiscriminators = board.lane_discriminators = {};
            if (board.laneDiscriminators) {
                board.laneDiscriminators.forEach(function(ld) {
                    laneDiscriminators[ld.name] = ld.value;
                });
                delete board.laneDiscriminators;
