function transform(input) {
    function getContentBlocksLegacy(appConfigGr) {
        var appConfig = appConfigGr.getValue('app_config');
        return new ACEAppBuilderUtilV2().getContentBlocks(input.page_route, input.page_params, appConfig, false, false, false);
    }

    /**
     * Fetch content blocks from bundle only if ace version is v2 and the sys_property to disable
     * bundle fetch is false.
     */
    function shouldFetchContentBlocksFromBundle(pageGr) {
        return gs.getProperty('ace.disable_page_bundle_fetch.' + input.page_route, false + "") == "false" &&
            pageGr.ace_app.version == 'v2';
    }

    function isApplicationACEV1(appConfigGr, pageGr) {
		var appVersion = appConfigGr.getValue('version');
        var pageVersion = pageGr.getValue('version');

        if (pageVersion && pageVersion.toLowerCase() === 'v2')
            return false;
        if (appVersion && appVersion.toLowerCase() === 'v2')
            return false;

        return true;
    }

    if (!input || !input.application_id || !input.page_route) return [];
    var contentBlocks = [];
    var appConfigGr = new GlideRecord('sn_ace_app_config');
    if (!appConfigGr.get(input.application_id))
        return [];

    var pageGr = new GlideRecord('sn_ace_page');
    if (!pageGr.get(input.page_route))
        return [];

    //interceptor for legacy v1 application
    if (isApplicationACEV1(appConfigGr, pageGr)) {
        var blocks = new ACEAppBuilderUtil().getContentBlocks(input.page_route, input.page_params);
        blocks.forEach(function(cb) {
            cb.isRoot = true;
        });
        return blocks;
    }

    var aceAppBuilderUtil = new ACEAppBuilderUtilV2();
    var aceGlobalUtil = new global.ACEAppBuilderGlobalScopedUtil();

    try {
        var cbPageParams = {
            'pageParams': input.page_params,
			'blockcontext':input.block_context
        };

        if (shouldFetchContentBlocksFromBundle(pageGr)) {
            contentBlocks = aceGlobalUtil.getDecompressedPageBundle(pageGr.getValue('bundle'));
			aceAppBuilderUtil.processReusableBlocks(contentBlocks, cbPageParams);
			aceAppBuilderUtil.mergeContentBlockContext(contentBlocks);
            if (contentBlocks && contentBlocks.length) {
                contentBlocks = aceAppBuilderUtil.eagerlyEvaluateDataBlocks(contentBlocks, cbPageParams);
                cbPageParams = aceAppBuilderUtil.addDataBlockParamsToScriptVariables(contentBlocks, cbPageParams);
                contentBlocks = aceAppBuilderUtil.processCachedContentBlocks(contentBlocks, cbPageParams);
            } else {
                contentBlocks = getContentBlocksLegacy(appConfigGr);
            }
        } else {
            contentBlocks = getContentBlocksLegacy(appConfigGr);
        }
    } catch (e) {
        gs.error("ACE Fetch Content Blocks: Error in parsing page composition: " + e.toString());
        contentBlocks = getContentBlocksLegacy(appConfigGr);
    }

    contentBlocks.forEach(function(cb) {
        cb.isRoot = true;
    });
    return contentBlocks;
}

function transform(input) {
    var gr = new GlideRecord('sn_bm_common_indicator');
    gr.addQuery('generate_global_scores', true);
    gr.orderBy('category.order');
    gr.query();
    var result = {};
    while (gr.next()) {
        if (!result.hasOwnProperty(gr.category)) {
            result[gr.category] = {};
            result[gr.category]['opted_in'] = 0;
            result[gr.category]['opted_out'] = 0;
            result[gr.category]['name'] = gr.getDisplayValue('category');

        }
        if (gr.active)
            result[gr.category]['opted_in'] = result[gr.category]['opted_in'] + 1;
        else
            result[gr.category]['opted_out'] = result[gr.category]['opted_out'] + 1;

    }
    var arr = [];

    for (var key in result) {
        result[key]['sys_id'] = key;
        result[key]['total'] = result[key]['opted_in'] + result[key]['opted_out'];


        var groupGr = new GlideRecord("sn_bm_common_indicator_group");
        groupGr.addQuery('category', key);
        groupGr.orderByDesc('order');
        groupGr.query();
        groupGr.next();
        var groups = ""+groupGr.name;
        while (groupGr.next()) {
            groups = groupGr.name + ", " + groups;
        }
        result[key]['groups'] = groups;
        arr.push(result[key]);
    }

    return arr;
}

function transform() {
    var util = new sn_cmdb_ws.CMDBWsPerfInsightsUtil();
    var gr = util.getIntegrationAggDataGr('', util.COLS.APPLICATION);

    if (!gr.hasNext()) {
        return {};
    }

    var OUTLIER = gs.getMessage("Outlier");
    var NON_OUTLIER = gs.getMessage("Non-outlier");

    var result = {
        processingRate: {
            allOptions: [],
            defaultOption: null
        },
        importCount: {
            allOptions: [],
            defaultOption: null
        }
    };

    var processingRateOutlierApplications = [];
    var processingRateNonOutlierApplications = [];
    var importCountOutlierApplications = [];
    var importCountNonOutlierApplications = [];

    var _aggData = [];
    var _appIds = {};
    var _appIdToName = {};

    while (gr.next()) {
        var feature = gr.getValue(util.COLS.CHART);
        var count = gr.getValue(util.COLS.COUNT);
        var applicationId = gr.getValue(util.COLS.APPLICATION);
        var hasOutlier = count > 0;
        _aggData.push({
            feature: feature,
            applicationId: applicationId,
            hasOutlier: hasOutlier
        });
        _appIds[applicationId] = true;
    }

    var uniqueAppIds = Object.keys(_appIds);

    while (uniqueAppIds.length > 0) {
        var appGr = new GlideRecord(util.TABLES.CMDB_INST_APPLICATION);
        appGr.addQuery(util.COLS.SYS_ID, "IN", uniqueAppIds.splice(0, 1000));
        appGr.query();

        while (appGr.next()) {
            _appIdToName[appGr.getUniqueValue()] = appGr.getDisplayValue();
        }
    }

    for (var d in _aggData) {
        var feature = _aggData[d].feature;
        var applicationId = _aggData[d].applicationId;
        var hasOutlier = _aggData[d].hasOutlier;
        var label = _appIdToName[applicationId];

        var dropdownOption = {
            id: applicationId,
            label: label
        };

        if (feature === util.SUB_CATEGORY.INTEGRATION_PROCESSING_RATE_OUTLIER) {
            if (hasOutlier) {
                processingRateOutlierApplications.push(dropdownOption);
            } else {
                processingRateNonOutlierApplications.push(dropdownOption);
            }
        } else if (feature === util.SUB_CATEGORY.INTEGRATION_ROWS_OUTLIER) {
            if (hasOutlier) {
                importCountOutlierApplications.push(dropdownOption);
            } else {
                importCountNonOutlierApplications.push(dropdownOption);
            }
        }
    }

    if (processingRateOutlierApplications.length > 0) {
        result.processingRate.allOptions.push({
            id: 'processing_rate_outlier',
            label: OUTLIER,
            children: processingRateOutlierApplications
        });
    }

    if (processingRateNonOutlierApplications.length > 0) {
        result.processingRate.allOptions.push({
            id: 'processing_rate_non_outlier',
            label: NON_OUTLIER,
            children: processingRateNonOutlierApplications
        });
    }

    if (importCountOutlierApplications.length > 0) {
        result.importCount.allOptions.push({
            id: 'import_count_outlier',
            label: OUTLIER,
            children: importCountOutlierApplications
        });
    }

    if (importCountNonOutlierApplications.length > 0) {
        result.importCount.allOptions.push({
            id: 'import_count_non_outlier',
            label: NON_OUTLIER,
            children: importCountNonOutlierApplications
        });
    }

    result.processingRate.defaultOption = [result.processingRate.allOptions[0].children[0]];
    result.importCount.defaultOption = [result.importCount.allOptions[0].children[0]];

    return result;
}

