function transform(input) {
	if (!input.sysId || !input.relatedList)
		return [];

	var returnListData = [];
	for (var i = 0; i < input.relatedList.length; i++) {
		var tableData = input.relatedList[i];
		var countGr = new GlideRecordSecure(tableData.relatedTable);
		countGr.addEncodedQuery(tableData.fixedQuery);
		if (tableData.query)
			countGr.addEncodedQuery(tableData.query);

		countGr.query();
		if (countGr.next())
			tableData.count = countGr.getRowCount();
		else
			tableData.count = 0;

		returnListData.push(tableData);
	}
	return returnListData;
}

