function transform(input) {
    var query = '';
    if (input.source && input.source != '' && input.target && input.target != '' && input.sourceId && input.sourceId != '') {
        if (input.source == 'sn_customerservice_case') {
            var gr = new GlideRecord(input.source);
            gr.get(input.sourceId);
            gr.query();
            query += new global.KBAjax().getTableMappingAsQueryWithParams(input.source, input.sourceId, input.target, '', true);
            query += '^short_description=' + gr.getValue('short_description') + '^source=' + input.sourceId;

        } else
            query += new global.KBAjax().getTableMappingAsQueryWithParams(input.source, input.sourceId, input.target, '', false);
    }
    if (input.knowledge_base && input.knowledge_base != '') {
      if(query != '')
        query += '^kb_knowledge_base=' + input.knowledge_base;
      else
        query += 'kb_knowledge_base=' + input.knowledge_base;
      if(input.sourceId && input.sourceId != '')
        query += '^source=' + input.sourceId;
    }
    return query;
}

