        users = [];
    else {
        var userGr = new GlideRecord('sys_user');
        if (input.userSearchTerm) userGr.addEncodedQuery('nameLIKE' + input.userSearchTerm);
        userGr.addActiveQuery();
        userGr.setLimit(25);
        userGr.orderBy('name');
        userGr.query();
        while (userGr.next()) {
            users.push({
                "id": userGr.getValue('sys_id'),
                "label": userGr.getValue('name')
            });
        }
    }

    if (groupOptions.hide || groupOptions.disable)
        groups = [];
    else {
        var groupGr = new GlideRecord('sys_user_group');
        if (input.groupSearchTerm) groupGr.addEncodedQuery('nameLIKE' + input.groupSearchTerm);
        groupGr.addActiveQuery();
        groupGr.setLimit(25);
        groupGr.orderBy('name');
        groupGr.query();
        while (groupGr.next()) {
            groups.push({
                "id": groupGr.getValue('sys_id'),
                "label": groupGr.getValue('name')
            });
        }
    }

    if (deviceOptions.hide || deviceOptions.disable)
        devices = [];
    else {
        var deviceGr = new GlideRecord('cmn_notif_device');
        if (input.deviceSearchTerm) deviceGr.addEncodedQuery('nameLIKE' + input.deviceSearchTerm);
        deviceGr.addActiveQuery();
        deviceGr.setLimit(25);
        deviceGr.orderBy('name');
        deviceGr.query();
        while (deviceGr.next()) {
            devices.push({
                "id": deviceGr.getValue('sys_id'),
                "label": deviceGr.getValue('name')
            });
        }
    }

    res.users = users;
    res.groups = groups;
    res.devices = devices;
    res.options = audienceOptions;
    return res;
}

