result.script:
function transform(inputs) {
    var buildGroupObj = function(groupGr) {
        var groupSettingMap = new global.OCRosterSpanApprovalUtil().getPTOApprovalAndCoverageStatusByGroups([groupGr.getValue('sys_id') + ""]);
        return {
            "id": groupGr.getValue('sys_id'),
            "label": groupGr.getValue('name'),
            "editable": new global.OCCalendarUtils().canWriteByGroupSysId(groupGr.getValue('sys_id')) + "",
            "isApprovalRequired": groupSettingMap[groupGr.getValue('sys_id') + ""].isPTOAccessible + "",
            "isProposedCoverMandatory": groupSettingMap[groupGr.getValue('sys_id') + ""].isProposedCoverMandatory + "",
        };
    };
    var result = {};
    var ocTeamsCalendarUtil = new sn_uib_on_call.OnCallTeamsCalendarUtil();
    ocTeamsCalendarUtil.clearErrorMessages();
    var userGroup = inputs.group;
    var userGroupsList = gs.getUser().getMyGroups();
    if (!gs.nil(userGroup))
        userGroupsList.push(userGroup);
    var userGroups = [];
    var userGroupGr = new GlideRecord('sys_user_group');
    userGroupGr.addEncodedQuery("managerDYNAMIC90d1921e5f510100a9ad2572f2b477fe^ORsys_idIN" + userGroupsList.join(","));
    userGroupGr.query();
    while (userGroupGr.next()) {
        userGroups.push(buildGroupObj(userGroupGr));
    }
    if (!inputs.isRecordPage)
        userGroups = userGroups.sort(function(a, b) {
            return a.label.localeCompare(b.label);
        });
    var defaultGroup = {};
    if (userGroups.length == 0) {
        var groupGr = new GlideRecord('sys_user_group');
        groupGr.addActiveQuery();
        groupGr.orderBy('name');
        groupGr.setLimit(1);
        groupGr.query();
        if (groupGr.next()) {
            defaultGroup = buildGroupObj(groupGr);
        }
    } else if (!gs.nil(userGroup)) {
        var urlParamGroupGr = new GlideRecord('sys_user_group');
        if (urlParamGroupGr.get(userGroup)) {
            defaultGroup = buildGroupObj(urlParamGroupGr);
        }
    } else if (userGroups.length > 0 && !inputs.isRecordPage) {
        var firstGroup = userGroups[0];
        var firstGroupHasShifts = hasShifts(firstGroup.id);
        if (!firstGroupHasShifts) {
            var groupWithShifts = userGroups.find(function(group) {
                return hasShifts(group.id);
            });
            if (groupWithShifts) {
                userGroups = userGroups.filter(function(group) {
                    return group.id !== groupWithShifts.id;
                });

                userGroups.unshift(groupWithShifts);
            }
        }
    }
    result.userGroups = userGroups;
    result.userGroupsList = gs.getUser().getMyGroups();
    result.defaultGroup = defaultGroup;
    result.groups = new global.OCRotationV2().getGroups() || [];
    result.timeZones = new global.OnCallCommon().getTimezoneList();

    result.supportsNewSubscriptionURL = (gs.getProperty('itsm_family_release_version_check', 0) >= 25);
    result.supportsGroupLevelSubscriptionWithSSO = (gs.getProperty('itsm_family_release_version_check', 0) >= 26);

    result.errorMessages = ocTeamsCalendarUtil.getErrorMessages();

    return result;

    function hasShifts(groupId) {
        var aggregate = new GlideAggregate('cmn_rota');
        aggregate.addQuery('group', groupId);
        aggregate.addAggregate('COUNT');
        aggregate.query();
        return aggregate.next() && aggregate.getAggregate('COUNT') > 0;
    }
}

