function transform(input) {
    var ocTeamsCalendarUtil = new sn_uib_on_call.OnCallTeamsCalendarUtil();
    ocTeamsCalendarUtil.clearErrorMessages();
    var result = {};
    if (input.group_ids && input.date_range && input.date_range.from && input.date_range.to)
        result = ocTeamsCalendarUtil.getSpans(input);
    else
      return result;

    function getCurrentDateTime(targetTimezone) {
        var gr = new GlideScheduleDateTime();
        var currentTime = gr.convertTimeZone(gs.getSession().getTimeZoneName(), targetTimezone);
        var gdt = new GlideDateTime();
        gdt.setDisplayValue(currentTime);
        return gdt.getDisplayValueInternal();
    }
    var groups = input.group_ids;
    var rotaIds = [];
    var rotaMembers = {};
    var rotaGR = new GlideRecord('cmn_rota');
    rotaGR.addQuery('group', 'IN', groups);
    rotaGR.query();
    while (rotaGR.next()) {
        rotaIds.push(rotaGR.getUniqueValue());
    }

    var curUserId = gs.getUserID();
    var curUserRotas = [];

    for (var i = 0; i < rotaIds.length; i++) {
        var memberGR = new GlideAggregate('cmn_rota_member');
        memberGR.addQuery('roster.rota', rotaIds[i]);
        memberGR.addQuery('member.active', true);
        memberGR.groupBy('member');
        memberGR.query();
        rotaMembers[rotaIds[i]] = [];
        while (memberGR.next()) {
            var userId = memberGR.getValue('member');
            if (userId == curUserId && curUserRotas.indexOf(rotaIds[i]) == -1) {
                curUserRotas.push(rotaIds[i]); 
            }
            var user = {
                id: userId,
                name: memberGR.getDisplayValue('member'),
                avatar: ocTeamsCalendarUtil.getUserAvatar(userId)

            };
            rotaMembers[rotaIds[i]].push(user);
        }

    }


    result.rotaMembers = rotaMembers;
    result.curUserRotas = curUserRotas;
    result.rotaIds = rotaIds.join(',');
    result.current_date_time = getCurrentDateTime(input.target_tz);
    result.errorMessages = ocTeamsCalendarUtil.getErrorMessages();

    return result;
}

