function transform(input) {
	if(input.functionName === "getUpgradeDetails") {
		var data = new global.UpgradeManagementApiUtil().getUpgradeDetails();

		return data;
	} else if(input.functionName === "getPredictedSkippedRecordsUrl") {
		var data = new global.UpgradeManagementApiUtil().getPredictedSkippedRecordsUrl();

		return data;
	} else if(input.functionName === "getSkippedRecordsUrl") {
		var data = new global.UpgradeManagementApiUtil().getSkippedRecordsUrl();

		return data;
	} else if(input.functionName === "checkIfATFInstalled") {
		var data = new global.UpgradeManagementApiUtil().checkIfATFInstalled();

		return data;
	}

	var data = new global.UpgradeManagementApiUtil().getUpgradeDetails();

	return data;
}

