function transform(input) {
    var jsonResponse = {
        maxCollectionDate: '',
        formattedMaxCollectionDate: '',
        top10TablesByStorage: ''
    };

    // Filter Query for Primary Database or Primary Database Gateway/Shard
    var primaryDBFilter = 'db_size_stats_id.storage_type=7376690077d042107d6b5631dc5a99f1^ORdb_size_stats_id.storage_type=8896690077d042107d6b5631dc5a99f8^sample_period_type=day';

    // Running Glide Aggregate Query to get Recent Sample Collection Date
    var sptsGA = new GlideAggregate('sys_physical_table_stats');
    sptsGA.addQuery(primaryDBFilter);
    sptsGA.setGroup(false);
    sptsGA.addAggregate('MAX', 'sample_period_start');
    sptsGA.query();

    // Fetch the max collection date if available
    if (sptsGA.next()) {
        var maxCollectionDate = sptsGA.getAggregate('MAX', 'sample_period_start');
        jsonResponse.maxCollectionDate = new GlideDateTime(maxCollectionDate).getDisplayValueInternal();
    }

    // Return early if no max collection date found
    if (!jsonResponse.maxCollectionDate) {
        return jsonResponse;
    }

    try {
        // Create GlideDateTime object from maxCollectionDate
        var gdt = new GlideDateTime(maxCollectionDate);

        // Format the date for DM Console Subheader with the user's time zone
        var displayValue = gdt.getDisplayValueLang('long');
        if (displayValue) {
            var timeZone = gs.getSession().getTimeZoneShortName();
            jsonResponse.formattedMaxCollectionDate = `Data as per ${displayValue} ${timeZone}`;
        }

    } catch (error) {
        gs.error("Error in transform function for Data Management Console: " + error.message);
    }

    // Get filter query for top 10 tables  
    const spts = new GlideAggregate("sys_physical_table_stats");
    spts.addQuery("sample_period_start", new GlideDateTime(maxCollectionDate));
    spts.addQuery(primaryDBFilter);
    spts.addAggregate("SUM", "table_size_in_gb");
    spts.groupBy("table_group");
    spts.orderByAggregate("SUM", "table_size_in_gb");
    spts.setLimit(9);
    spts.query();

    const top10tableGroups = [];
    while (spts.next()) {
        top10tableGroups.push(spts.getValue("table_group"));
    }
    const top10filterQuery = top10tableGroups.map(group => `table_group=${group}`).join('^OR');
    jsonResponse.top10TablesByStorage = top10filterQuery;

    return jsonResponse;
}

