function transform(input) {

    function getCiEncodedQuery(obj) {
        var parsedXml = obj.update_ci;
        var fieldObj = parsedXml[Object.keys(parsedXml)[0]];

        var index = 0;
        var qry = '';
        var encQry = '';

        for (var fld in fieldObj) {
            if (fieldObj[fld]["@newValue"] + '' !== 'NULL') {
                qry = fld + '=' + fieldObj[fld]["@newValue"];
                if (index === 0)
                    encQry = qry;
                else
                    encQry += "^" + qry;

                index++;
            }
        }

        return encQry;
    }

    if (!input.taskCISysId || !input.action)
        return {
            error: gs.getMessage('ERROR')
        };

    var taskCiGr = new GlideRecordSecure("task_ci");
    if (!taskCiGr.get(input.taskCISysId))
        return;

    var ciGr = new GlideRecordSecure(taskCiGr.ci_item.getRefRecord().getTableName());
    if (!ciGr.get(taskCiGr.ci_item))
        return;

    var proposedCiGr = new GlideRecordSecure(taskCiGr.ci_item.getRefRecord().getTableName());
    if (!proposedCiGr.get(taskCiGr.ci_item))
        return;

    var ProposeChangeUtil = new global.ProposeChangeUtil();
    var obj = ProposeChangeUtil.xmlHelper.toObject(taskCiGr.xml);

    if (input.action === 'VIEW_PROPOSED_CHANGE') {
        var rows = [];
        var key = 0;

        if (obj) {
            proposedCiGr.applyEncodedQuery(getCiEncodedQuery(obj));

            var parsedXml = obj.update_ci;
            var fieldObj = parsedXml[Object.keys(parsedXml)[0]];

            for (var field in fieldObj) {
                if (fieldObj[field]["@newValue"] + '' !== 'NULL') {
                    var currVals = {
                        key: key,
                        cells: {
                            change: {
                                highlightedValue: null,
                                value: ciGr.getElement(field).getLabel()
                            },
                            current_value: {
                                highlightedValue: null,
                                value: fieldObj[field]["@oldValue"] ? ciGr.getDisplayValue(field) : "-"
                            },
                            proposed_value: {
                                highlightedValue: null,
                                value: fieldObj[field]["@newValue"] ? proposedCiGr.getDisplayValue(field) : "-"
                            }
                        }
                    };
                    rows.push(currVals);
                    key++;
                }
            }
        }

        var valsToReturn = {
            rows: rows,
            updatedOn: taskCiGr.getDisplayValue("sys_updated_on"),
            currentCI: taskCiGr.getDisplayValue("ci_item")
        };

        return valsToReturn;
    } else if (input.action === 'PROPOSE_SINGLE_CHANGE') {
        var CIData = {};
        CIData.table = {};
        CIData.template = {};

        CIData.name = taskCiGr.getDisplayValue("ci_item");
        CIData.table.value = ciGr.getValue('sys_class_name');
        CIData.table.displayValue = ciGr.getDisplayValue('sys_class_name');
        CIData.template.dependentValue = ciGr.getValue('sys_class_name');
        CIData.template.label = gs.getMessage('Proposed change');

        var encQry = '';
        if (obj) {
            encQry = getCiEncodedQuery(obj);
        }

        CIData.template.value = encQry;
        CIData.template.displayValue = encQry;

        return CIData;
    }
}

