}

function transform(input) {
	const requestData = input.requestData;
	var output;
	if (input.isOdditType) {
		var gdtStart = new GlideDateTime();
		gdtStart.setDisplayValue(requestData.startSpanDate);
		gdtStart.add(1);
		var gdtEnd = new GlideDateTime();
		gdtEnd.setDisplayValue(requestData.endSpanDate);
		gdtEnd.add(-1);

		var gr = new GlideRecord("cmn_rota_roster");
		if (gr.get(requestData.rosterId)) {
			var schd = new GlideSchedule(gr.rota.schedule);
			var startOverlapping = schd.isInSchedule(gdtStart);
			var endOverlapping = schd.isInSchedule(gdtEnd);

			if (!startOverlapping && !endOverlapping) {
				output = [{
					messageType: "error",
					messageContent: gs.getMessage("No schedules cover that time slot. Specify extra times for a Shift first.")
				}];
				return output;
			}
		}
		gdtStart.add(-1);
		gdtEnd.add(1);

		output = new global.OnCallCommon().addOverride(gdtStart.getDisplayValueInternal(), gdtEnd.getDisplayValueInternal(), requestData.userId, requestData.rosterId, requestData.groupId);
	}
	else {
		output = new sn_uib_on_call.OnCallOddityParser().saveTimeOffRequest(requestData);
	}

	return output;
}

