result.script:
function transform(input) {
    if (!input.searchGraphqlData) {
        gs.error("Invalid empty value provided as input for searchGraphqlData. Check that the search executed successfully.");
        return {
            items: []
        };
    }
    if (!("searchResults" in input.searchGraphqlData)) {
        gs.error("No searchResults property exists on provided input to Generate Regular Search Results transform. Check that the search executed successfully.");
        return {
            items: []
        };
    }
    var evamDefinitionId = input.evamDefinitionId;
    if (!evamDefinitionId) {
        gs.error("No EVAM Definition ID provided as input to Generate Regular Search Results transform");
        return {
            items: []
        };
    }

    var searchResultsJson = input.searchGraphqlData.searchResults;
    var locale = input.locale;

    var searchResultTemplates = sn_search.ScriptableResultTemplateMapper.getRegularResultTemplates(
        evamDefinitionId,
        JSON.stringify(searchResultsJson),
        locale
    );

    var searchResultTemplatesSearch = JSON.parse(searchResultTemplates);
    var vaEvamDefinitionId = input.vaEvamDefinitionId;

    //We dont want to send empty items when evam definition id is not sent to us
    if (!vaEvamDefinitionId) {
        gs.debug("No VA EVAM Definition ID provided as input to Generate Regular Search Results Templates transform.");
    }
    //We only want to query & add VA items if VA evam definition id is sent to us
    if (vaEvamDefinitionId) {
        var searchResultTemplatesVA = sn_search.ScriptableResultTemplateMapper.getRegularResultTemplates(
            vaEvamDefinitionId,
            JSON.stringify(searchResultsJson),
            locale
        );

        var searchResultTemplatesForVA = JSON.parse(searchResultTemplatesVA);
        // We would only want to add if there is a need for handoff to VA and queryForConversationalCatalog is true
        var searchResultsConfigBySysId = {};
        searchResultsJson.forEach(function(searchResult) {
            var searchResultItemId;
            var searchResultConversationalParam;

            for (searchResultColumnIdx in searchResult.columns) {
                srObj = searchResult.columns[searchResultColumnIdx];
                if (srObj.fieldName === 'sys_id')
                    searchResultItemId = srObj.value;
                if (srObj.fieldName === 'conversational')
                    searchResultConversationalParam = srObj.value;
                if (searchResultItemId && searchResultConversationalParam) {
