result.script:
function transform(input) {
    if (!input.searchGraphqlData) {
        gs.error("Invalid empty value provided as input for searchGraphqlData. Check that the search executed successfully.");
        return {
            items: []
        };
    }
    if (!("geniusResults" in input.searchGraphqlData)) {
        gs.error("No geniusResults property exists on provided input to Build Genius Result Templates transform. Check that the search executed successfully.");
        return {
            items: []
        };
    }

    var evamDefinitionId = input.evamDefinitionId;
    if (!evamDefinitionId) {
        gs.error("No EVAM Definition ID provided as input to Build Genius Result Templates transform.");
        return {
            items: []
        };
    }

    function swapCitationVersionForSingleString(value) {
        var regex = /\[\{.*?\}\]/g; // Matches the stringified JSON within brackets
        return value.replace(regex, function(match) {
            try {
                var citationJSON = JSON.parse(match);
                if (citationJSON && citationJSON[0] && citationJSON[0].replacementV1) {
                    return citationJSON[0].replacementV1;
                } else {
                    return match; // Return the original match if replacementV1 is missing or JSON is invalid
                }
            } catch (e) {
                var backupRegex = /replacementV1.*\"(.*\[\d{1,2}?\].*)\"/;
                var backupMatch = match.match(backupRegex);
                if (backupMatch && backupMatch[1]) {
                    return backupMatch[1];
                } else {
                    gs.error("Error during citation replacement - unable to parse citation JSON: ", e);
                    return match; // Return the original match if JSON parsing fails
                }
            }
        });
    }

    function swapCitationVersionForV1(geniusResultsJson) {
        var fieldsToReplace = ['answer', 'answer_markdown', 'raw_citations'];
        for (var i = 0; i < geniusResultsJson.length; i++) {
            var grJson = geniusResultsJson[i];
            for (var colIndex = 0; colIndex < grJson.data.length; colIndex++) {
                var col = grJson.data[colIndex];
                if (fieldsToReplace.indexOf(col.fieldName) > -1) {
                    var newValue;
                    var newDiplayValue;
                    if (col.fieldName == 'raw_citations') {
                        var citationsJSON = JSON.parse(col.value);
                        var newCitations = [];
                        for (var citationIndx = 0; citationIndx < citationsJSON.length; citationIndx++) {
                            citationsJSON[citationIndx].content = swapCitationVersionForSingleString(
                                citationsJSON[citationIndx].content
                            );
                            newCitations.push(citationsJSON[citationIndx]);
                        }
                        newValue = JSON.stringify(newCitations);
                        newDiplayValue = newValue; // value and display value are the same for raw_citations
                    } else {
                        newValue = swapCitationVersionForSingleString(col.value);
                        newDiplayValue = swapCitationVersionForSingleString(col.displayValue);
                    }
                    col.value = newValue;
                    col.displayValue = newDiplayValue;
                    grJson.data[colIndex] = col;
                }
            }
            geniusResultsJson[i] = grJson;
        }
        return geniusResultsJson;
    }

    var geniusResultsJson = input.searchGraphqlData.geniusResults;
    var locale = input.locale;

    var geniusResultTemplates = sn_search.ScriptableResultTemplateMapper.getGeniusResultTemplates(
        evamDefinitionId,
        JSON.stringify(geniusResultsJson),
        locale
    );
    var geniusResultTemplatesSearch = JSON.parse(geniusResultTemplates);
    var vaEvamDefinitionId = input.vaEvamDefinitionId;
    var queryForConversationalCatalog = input.queryForConversationalCatalog;
    //We dont want to send empty items when evam definition id is not sent to us
    if (!vaEvamDefinitionId) {
        gs.debug("No VA EVAM Definition ID provided as input to Build Genius Result Templates transform.");
    }

    //We only want to query & add VA items if VA evam definition id is sent to us
    if (vaEvamDefinitionId) {
        var grJsonForVA = geniusResultsJson;

        // Need this for the handoff to VA when portal is using citation format v2, but VA is using v1
        if (parseInt(gs.getProperty("glide.search.va_handoff_citation_version", "1")) < 2) {
            grJsonForVA = swapCitationVersionForV1(geniusResultsJson);
        }

        var geniusResultTemplatesVA = sn_search.ScriptableResultTemplateMapper.getGeniusResultTemplates(
            vaEvamDefinitionId,
            JSON.stringify(grJsonForVA),
            locale
        );

        var geniusResultTemplatesForVA = JSON.parse(geniusResultTemplatesVA);

        geniusResultTemplatesSearch.forEach(function(geniusResultTemplate) {
            var sys_id = geniusResultTemplate.propValues.model.sys_id;
            geniusResultTemplatesForVA.forEach(function(geniusResultVA) {
                var VA_sys_id = geniusResultVA.propValues.model.sys_id;
                if (sys_id == VA_sys_id) {
                    geniusResultTemplate.vaEvamItem = Object.assign({}, geniusResultVA);
                }
            });

        });
    }
    return {
        items: geniusResultTemplatesSearch
    };
}

