function transform(inputs) {
    var result = {};
    if (!inputs.groupId)
        return {};
    if (!(new global.OnCallSecurityNG().rotaMgrAccess(inputs.groupId)))
        return {};
    var ptoRequests = {
        "not_allowed": gs.getMessage("Not allowed"),
        "with_approval": gs.getMessage("With approval"),
        "without_approval": gs.getMessage("Without approval")
    };
    var scheduleOverlaps = {
        "true": gs.getMessage("Yes"),
        "false": gs.getMessage("No")
    };
    var escalationRules = {
        "start": gs.getMessage("Escalate to outgoing shift"),
        "end": gs.getMessage("Escalate to incoming shift"),
        "all": gs.getMessage("Escalate to all shifts")
    };
    var days = {
        "1": gs.getMessage("Sunday"),
        "2": gs.getMessage("Monday"),
        "3": gs.getMessage("Tuesday"),
        "4": gs.getMessage("Wednesday"),
        "5": gs.getMessage("Thursday"),
        "6": gs.getMessage("Friday"),
        "7": gs.getMessage("Saturday")
    };
    var defaultPtoRequest = gs.getProperty('com.snc.on_call_rotation.pto.configuration');
    var defaultShiftScheduleOverlap = gs.getProperty('com.snc.on_call_rotation.allow_rota_overlap');
    var defaultEscalationRuleOnShiftOverlap = gs.getProperty('com.snc.on_call_rotation.escalation_rule_rota_overlap');
    var defaultSelectedFirstDayOfWeek = new global.OCCalendarUtils().getSystemFirstDayOfWeek();
    var ptoRequestsDropdownItems = [{
            id: 'default',
            label: gs.getMessage('System default ({0})', [ptoRequests[defaultPtoRequest]])
        },
        {
            id: 'with_approval',
            label: ptoRequests['with_approval']
        },
        {
            id: 'without_approval',
            label: ptoRequests['without_approval']
        },
        {
            id: 'not_allowed',
            label: ptoRequests['not_allowed']
        }
    ];

    var shiftScheduleOverlapDropdownItems = [{
            id: (defaultShiftScheduleOverlap == "true") ? 'default_yes' : 'default_no',
            label: gs.getMessage('System default ({0})', [scheduleOverlaps[defaultShiftScheduleOverlap]])
        },
        {
            id: 'yes',
            label: scheduleOverlaps['true']
        },
        {
            id: 'no',
            label: scheduleOverlaps['false']
        }
    ];

    var escalationRuleDropdownItems = [{
            id: 'default',
            label: gs.getMessage('System default ({0})', [escalationRules[defaultEscalationRuleOnShiftOverlap]])
        },
        {
            id: 'start',
            label: escalationRules['start']
        },
        {
            id: 'end',
            label: escalationRules['end']
        },
        {
            id: 'all',
            label: escalationRules['all']
        }
    ];
    var rotaStartDayDropdownItems = [{
            id: '0',
            label: gs.getMessage('System default ({0})', [days[Math.floor(defaultSelectedFirstDayOfWeek)]])
        },
        {
            id: '1',
            label: days['1']
        },
        {
            id: '2',
            label: days['2']
        },
        {
            id: '3',
            label: days['3']
        },
        {
            id: '4',
            label: days['4']
        },
        {
            id: '5',
            label: days['5']
        },
        {
            id: '6',
            label: days['6']
        },
        {
            id: '7',
            label: days['7']
        }
    ];
    var firstDayOfWeekExists = false;
    var dictionaryGr = new GlideRecord('sys_dictionary');
    dictionaryGr.addQuery('name', 'on_call_group_preference');
    dictionaryGr.addQuery('element', 'first_day_of_the_week');
    dictionaryGr.setLimit(1);
    dictionaryGr.query();
    if (dictionaryGr.next())
        firstDayOfWeekExists = true;
    var groupPrefGr = new GlideRecord('on_call_group_preference');
    groupPrefGr.addQuery('group', inputs.groupId);
    groupPrefGr.setLimit(1);
    groupPrefGr.query();
    if (groupPrefGr.next()) {
        var rotaManagers = groupPrefGr.getValue('rota_managers');
        var selectedRotaManagers = [];
        var userGr = new GlideRecord('sys_user');
        userGr.addQuery('sys_id', 'IN', rotaManagers);
        userGr.query();
        while (userGr.next()) {
            selectedRotaManagers.push({
                id: userGr.getUniqueValue(),
                label: userGr.getValue('name')
            });
        }
        result.ptoRequestsDropdownItems = ptoRequestsDropdownItems;
        result.shiftScheduleOverlapDropdownItems = shiftScheduleOverlapDropdownItems;
        result.escalationRuleDropdownItems = escalationRuleDropdownItems;
        result.selectedRotaManagers = selectedRotaManagers;
        result.ptoRequests = groupPrefGr.getValue('pto_approval_config') ? groupPrefGr.getValue('pto_approval_config') : gs.getProperty('com.snc.on_call_rotation.pto.configuration');
        if (firstDayOfWeekExists) {
            var selectedFirstDayOfWeek = groupPrefGr.getValue('first_day_of_the_week') || '0';
            result.firstDayOfWeek = selectedFirstDayOfWeek;
            result.firstDayOfWeekDropdownItems = rotaStartDayDropdownItems;
        }
        result.firstDayOfWeekExists = firstDayOfWeekExists;
        var allowShiftScheduleOverlap = groupPrefGr.getValue('allow_rota_overlap');
		allowShiftScheduleOverlap = (allowShiftScheduleOverlap == 'default') ? shiftScheduleOverlapDropdownItems[0].id : allowShiftScheduleOverlap;
        result.allowShiftScheduleOverlap = allowShiftScheduleOverlap;
        var selectedDropdownItem;
        for (var i = 0; i < shiftScheduleOverlapDropdownItems.length; i++) {
            if (shiftScheduleOverlapDropdownItems[i].id === allowShiftScheduleOverlap) {
                selectedDropdownItem = shiftScheduleOverlapDropdownItems[i];
                break;
            }
        }
        result.rotaOverlapValue = selectedDropdownItem.id;
        if (result.allowShiftScheduleOverlap) {
            result.escalationRule = groupPrefGr.getValue('escalation_rule_rota_overlap') ? groupPrefGr.getValue('escalation_rule_rota_overlap') : gs.getProperty('com.snc.on_call_rotation.escalation_rule_rota_overlap');
        }
    } else {
        result.ptoRequestsDropdownItems = ptoRequestsDropdownItems;
        result.shiftScheduleOverlapDropdownItems = shiftScheduleOverlapDropdownItems;
        result.escalationRuleDropdownItems = escalationRuleDropdownItems;
        result.ptoRequests = 'default';
        result.allowShiftScheduleOverlap = shiftScheduleOverlapDropdownItems[0].id;
        result.rotaOverlapValue = shiftScheduleOverlapDropdownItems[0].id;

        result.firstDayOfWeekExists = firstDayOfWeekExists;
        if (firstDayOfWeekExists) {
            result.firstDayOfWeek = '0';
            result.firstDayOfWeekDropdownItems = rotaStartDayDropdownItems;
        }

    }
    return result;
}

