        const studioShellConfig = input?.isStatusBarEnabled ?
            JSON.parse(input.isStatusBarEnabled) :
            null;
        isStatusBarEnabled = studioShellConfig?.enableStatusBar ?? false;
    } catch (error) {
        gs.error(`Error parsing isStatusBarEnabled: ${error.message}`);
