function transform(input) {
    var output = {
            success: false,
            partial_success: false,
            error_messages: [],
            data: []
        },
        resp = {},
        errors = [];

    var onCallCreationWizardUtils = new global.OnCallCreationWizardUtils();
    var ocsNg = new global.OnCallSecurityNG();
    var ocTeamsCalendarUtil = new sn_uib_on_call.OnCallTeamsCalendarUtil();

    function unEscapeErrorMessages(msgs) {
        var errors = [];
        if (msgs.length != 0) {
            msgs.forEach(function(msg) {
                errors.push(msg.replaceAll(/&#39;/g, "'"));
            }, this);
        }
        return errors;
    }

    function removeEntry(tableName, sysId) {
        if (gs.nil(tableName) || gs.nil(sysId))
            return;

        var rotaGr = new GlideRecord(tableName);
        if (rotaGr.get(sysId))
            rotaGr.deleteRecord();
    }

    var shiftData = input.shiftData;
    if (shiftData && shiftData.type) {
        var type = shiftData.type;
        if (gs.nil(shiftData.group)) {
            output.error_messages = [gs.getMessage("Group Sys Id not provided")];
            return output;
        }
        if (!ocsNg.rotaMgrAccess(shiftData.group))
            return output;

        ocTeamsCalendarUtil.clearErrorMessages();
        if (type == 'use_shift_template') {

            var onCallRotation = new global.OnCallRotation();

            if (gs.nil(shiftData.name)) {
                output.error_messages = [gs.getMessage("Shift name is not provided")];
                return output;
            }

            if (gs.nil(shiftData.schedule_template)) {
                output.error_messages = [gs.getMessage("Schedule Template is not provided")];
                return output;
            }

            try {
                var startDate = new GlideDate().getDisplayValueInternal();
                var overlappingRota = onCallRotation._getExistingSpanOverlappingRota(shiftData.group, shiftData.schedule_template, new GlideDateTime(startDate));
                if (global.JSUtil.notNil(overlappingRota)) {
                    output.error_messages = [gs.getMessage("Cannot create the shift, The selected schedule overlaps with the '{0}' rotation schedule.", [overlappingRota])];
                    return output;
                }

                resp = onCallCreationWizardUtils.createUpdateShiftFromTemplates([{
                    "name": shiftData.name,
                    "schedule_type": "schedule_template",
                    "start_date": startDate,
                    "schedule_template": shiftData.schedule_template,
                    "specify_holiday": false,
                    "send_reminders": true,
                    "reminder_lead_time": 2,
                    "coverage_lead_type": "weekly",
                    "coverage_interval": 12,
                    "index": 0
                }], shiftData.group);

                errors = ocTeamsCalendarUtil.getErrorMessages();
                if (errors.length)
                    output.error_messages = unEscapeErrorMessages(errors);
                else {
                    output.success = true;
                    output.error_messages = [];
                    output.data = resp.rotaData;
                }
                return output;
            } catch (e) {
                output.error_messages = [e.message];
                return output;
            }
        }
        if (type == 'use_schedule_template') {
            var schedule = "",
                records = shiftData.records,
                group = shiftData.group,
                rotaData = [];

            if (gs.nil(records) || !Array.isArray(records) || records.length == 0) {
                output.error_messages = [gs.getMessage("Shifts Information not provided")];
                return output;
            }
            for (var i = 0; i < records.length; i++) {
                var shift = records[i];
                if (shift.ignore)
                    continue;

                var isAllDay = shift.all_day;
                var gdt = new GlideDateTime();
                var gd = new GlideDate();
                gd.setDisplayValue(gdt.getLocalDate());
                if (!isAllDay) {
                    shift.start_date_time = gd.getDisplayValue() + " " + shift.start_time;
                    gdt.addDaysLocalTime(parseInt(shift.end_time_day));
                    gd.setDisplayValue(gdt.getLocalDate());
                    shift.end_date_time = gd.getDisplayValue() + " " + shift.end_time;

                    gdt.setDisplayValue(shift.start_date_time);
                    shift.start_date_time = gdt.getDisplayValueInternal();

                    gdt.setDisplayValue(shift.end_date_time);
                    shift.end_date_time = gdt.getDisplayValueInternal();
                } else {
                    var formattedDate = gd.getByFormat("yyyy-MM-dd");
                    shift.start_date_time = formattedDate + " 00:00:00";
                    shift.end_date_time = formattedDate + " 23:59:59";
                }
                delete shift.start_time;
                delete shift.end_time;
                delete shift.end_time_day;
                try {
                    resp = onCallCreationWizardUtils.createUpdateShiftFromTemplates([shift], group);
                    errors = ocTeamsCalendarUtil.getErrorMessages();
                    if (errors.length) {
                        output.data = resp.rotaData;
                        output.success = true;
                        output.partial_success = true;
                        output.error_messages = unEscapeErrorMessages(errors);
                        return output;
                    }
                    if (resp && resp.rotaData && resp.rotaData[0]) {
                        schedule = resp.rotaData[0].schedule && resp.rotaData[0].schedule.value;
                        rotaData.push(resp.rotaData[0]);
                    }
                } catch (e) {
                    output.error_messages = [e.message];
                    return output;
                }
            }
            if (output.error_messages && output.error_messages.length)
                return output;
            return {
                success: true,
                error_messages: [],
                partial_success: false,
                data: rotaData
            };
        }

        if (type == 'create_new_shift') {
            if (shiftData.all_day) {
                var stDate = new GlideDate();
                stDate.setDisplayValue(shiftData.start_date);
                shiftData.start_date_time = stDate.getByFormat("yyyy-MM-dd") + " 00:00:00";
                shiftData.end_date_time = stDate.getByFormat("yyyy-MM-dd") + " 23:59:59";
            }
            var glideDateTime = new GlideDateTime();
            glideDateTime.setDisplayValue(shiftData.start_date_time);
            shiftData.start_date_time = glideDateTime.getDisplayValueInternal();
            shiftData.start_date = glideDateTime.getLocalDate();
            glideDateTime.setDisplayValue(shiftData.end_date_time);
            shiftData.end_date_time = glideDateTime.getDisplayValueInternal();
            try {
                resp = onCallCreationWizardUtils.createUpdateShiftFromTemplates([shiftData], shiftData.group);
                var result = {
                        "errorMessages": [],
                        "shift_details": {}
                    },
                    daysOfWeek = {
                        'monday': '1',
                        'tuesday': '2',
                        'wednesday': '3',
                        'thursday': '4',
                        'friday': '5',
                        'saturday': '6',
                        'sunday': '7'
                    };
                var inputData = {
                    shift_id: (resp && resp.rotaData && resp.rotaData[0] && resp.rotaData[0].sys_id.value) || "",
                    shift_details: {
                        repeat_type: shiftData.repeat_type,
                        repeat_number: shiftData.repeat_number
                    }
                };
                if (!gs.nil(shiftData.repeats_until) && shiftData.repeat_type != "never")
                    inputData.shift_details.repeats_until_iso = shiftData.repeats_until.split("T")[0];

                if (!gs.nil(shiftData.selected_holiday_schedule))
                    inputData.shift_details.selected_holiday_schedule = shiftData.selected_holiday_schedule;

                var responseData = new sn_uib_on_call.ShiftUtility().updateShiftDetails(inputData, null, result, daysOfWeek);
                if (responseData.errorMessages && responseData.errorMessages.length != 0) {
                    removeEntry('cmn_rota', inputData.shift_id);
                    output.error_messages = [responseData.errorMessages[0]];
                    return output;
                }
                return {
                    success: true,
                    error_messages: [],
                    partial_success: false,
                    data: resp.rotaData
                };
            } catch (e) {
                output.error_messages = [e.message];
                return output;
            }
        }
    }
    return output;
}

