    var wwwna_application_enabled = [];
    var wwwna_application_available = [];

    try {
        var util = new sn_nowassist_admin.NowAdminAssistUtil();
        var wwnaSkills = util.wwnaSkills();

        wwnaSkills.forEach((wwnaSkill) => {
            var skillFamily = wwnaSkill.skillFamily;
            var skillConfigId = wwnaSkill.skillConfigId;
            var workflow = wwnaSkill.workflow;
            var product = wwnaSkill.product;
            var feature = wwnaSkill.feature;
            var skillDetails = wwnaSkill.skillDetails;
            var skillName = wwnaSkill.skillName;
            var featureId = wwnaSkill.featureId;
            var status = wwnaSkill.status;

            var skillNavigationURL = "now/now-assist-admin/skills/" + featureId;
            var skillConfigNavigationURL = "now/now-assist-admin/skill-guided-setup/" + skillConfigId;

            var rowPayload = {
                "key": skillConfigId,
                "cells": {
                    "product": {
                        "value": product
                    },
                    "workflow": {
                        "value": workflow
                    },
                    "feature": {
                        "value": sn_i18n.Message.getMessage("sn_nowassist_admin", feature)
                    },
                    "skill": {
                        "value": skillName
                    },
                    "status": {
                        "componentProps": {
                            "color": skillDetails['colour'],
                            "label": skillDetails['label'],
                            "variant": "tertiary",
                            "icon": "circle-fill"
                        }
                    },
                    "action": {
                        "componentProps": {
                            "label": (status === "true" || status === "1") ? sn_i18n.Message.getMessage("sn_nowassist_admin", "Manage") : sn_i18n.Message.getMessage("sn_nowassist_admin", "Activate skill"),
                            "href": (status === "true" || status === "1") ? skillConfigNavigationURL : skillNavigationURL,
                            "opens-window": false
                        }
                    }
                }
            };

            if (status === "true" || status === "1") {
                if (!wwwna_application_enabled.some(item => item.key === skillConfigId)) {
                    wwwna_application_enabled.push(rowPayload);
                }
            } else {
                if (!wwwna_application_available.some(item => item.key === skillConfigId)) {
                    wwwna_application_available.push(rowPayload);
                }
            }
        });

		var actionColumn =   {
			"cellEditingDisabled": true,
			"componentTag": "now-text-link",
			"key": "action",
			"label": sn_i18n.Message.getMessage("sn_nowassist_admin", "Action"),
			"type": "custom"
		}

        var columnDefinitions = {

            "columns": [{
                    "cellEditingDisabled": true,
                    "key": "product",
                    "label": sn_i18n.Message.getMessage("sn_nowassist_admin", "Product name"),
                    "type": "string"
                },
                {
                    "cellEditingDisabled": true,
                    "key": "workflow",
                    "label": sn_i18n.Message.getMessage("sn_nowassist_admin", "Product workflow"),
                    "type": "string"
                },
                {
                    "cellEditingDisabled": true,
                    "key": "feature",
                    "label": sn_i18n.Message.getMessage("sn_nowassist_admin", "Used in"),
                    "type": "string"
                },
                {
                    "cellEditingDisabled": true,
                    "key": "skill",
                    "label": sn_i18n.Message.getMessage("sn_nowassist_admin", "Uses Now Assist skill"),
                    "type": "string"
                },
                {
                    "cellEditingDisabled": true,
                    "componentTag": "now-highlighted-value",
                    "key": "status",
                    "label": sn_i18n.Message.getMessage("sn_nowassist_admin", "Skill status"),
                    "type": "custom"
                }
            ]
        }

		!util.isNowAssistAdminUser() && (columnDefinitions.columns.push(actionColumn));

        return {
			columnDefinitions : columnDefinitions,
            availableWWNASkills: wwwna_application_available,
            enabledWWNASkills: wwwna_application_enabled,
            wwwnaEnabled: (wwwna_application_available.length > 0 || wwwna_application_enabled.length > 0) ? true : false
        };
    } catch (error) {
        gs.error("Failed to get WWNA configured skills" + error.toString());
    }
}

function transform(input) {
	for(var i = 0; i < input.evambroker.items.length; i++){
		var model = input.evambroker.items[i].propValues;
		var currDate = new GlideDateTime();
		var dueDate = new GlideDateTime();
		var price = model.model.price;

		var noValueMessage = gs.getMessage("N/A");
		var buttonLabel = model.titleLabel;
		var detailValueOne = model.detailValueOne || noValueMessage;
		var detailValueTwo = model.detailValueTwo || noValueMessage;

		if (model.highlightedHeaderLabel) {
			if (model.detailLabelOne && model.detailLabelTwo) 
				buttonLabel = gs.getMessage(
					"{0}, {1}, {2}: {3}, {4}: {5}",
					[
						model.titleLabel,
						model.highlightedHeaderLabel,
						model.detailLabelOne,
						detailValueOne,
						model.detailLabelTwo,
						detailValueTwo
					]
				);
			else if (model.detailLabelOne)
				buttonLabel = gs.getMessage(
					"{0}, {1}, {2}: {3}",
					[
						model.titleLabel,
						model.highlightedHeaderLabel,
						model.detailLabelOne,
						detailValueOne
					]
				);
			else if (model.detailLabelTwo)
				buttonLabel = gs.getMessage(
					"{0}, {1}, {2}: {3}",
					[
						model.titleLabel,
						model.highlightedHeaderLabel,
						model.detailLabelTwo,
						detailValueTwo
					]
				);
			else
				buttonLabel = gs.getMessage(
					"{0}, {1}",
					[
						model.titleLabel,
						model.highlightedHeaderLabel
					]
				);
		} else if (model.detailLabelOne && model.detailLabelTwo)
			buttonLabel = gs.getMessage(
				"{0}, {1}: {2}, {3}: {4}",
				[
					model.titleLabel,
					model.detailLabelOne,
					detailValueOne,
					model.detailLabelTwo,
					detailValueTwo
				]
			);
		else if (model.detailLabelOne)
			buttonLabel = gs.getMessage(
				"{0}, {1}: {2}",
				[
					model.titleLabel,
					model.detailLabelOne,
					detailValueOne
				]
			);
		else if (model.detailLabelTwo)
			buttonLabel = gs.getMessage(
				"{0}, {1}: {2}",
				[
					model.titleLabel,
					model.detailLabelTwo,
					detailValueTwo
				]
			);

		model.buttonLabel = buttonLabel;

		dueDate.setValue(model.model.due_date);
		var dueDateValue = dueDate.getValue();
		if(model.model.table == "sc_req_item"){
			if(model.model.due_date != ""){
				model.highlightedHeaderLabel = gs.getMessage("Completion date: {0}", model.model.due_date);
				if (model.model.due_date.includes('ago')) { 
					model.highlightedHeaderBkgColor = "critical";
				} else if (dueDate.isValid() && currDate.getDisplayValue() > dueDate.getDisplayValue()){
					model.highlightedHeaderBkgColor = "critical";
				} 
			}
			if(model.model.recurring_frequency != null)
				model.subtitle = model.subtitle + " + " + model.model.recurring_price + " " + model.model.recurring_frequency;
			if(price && price.substring(1) == "0.00" && model.model.recurring_frequency == null )
				model.subtitle = " ";
		} else{
			if(model.model.due_date != ""){
				model.highlightedHeaderLabel = gs.getMessage("Completion date: {0}", model.model.due_date);
				if (model.model.due_date.includes('ago')) { 
					model.highlightedHeaderBkgColor = "critical";
				} else if (dueDate.isValid() && currDate.getDisplayValue() > dueDate.getDisplayValue()){
					model.highlightedHeaderBkgColor = "critical";
				} 
			}
		}
	}
	return input;
}

