function transform(input) {

	function _getParentString(rel) {
		if (rel.level > 1) {
			return "[" + _getParentNames(rel.parents) + " \u21E2 ] ";
		}
		return "";
	}

	function _getParentNames(parentArr) {
		var parentNames = [];
		for (var i = 0; i < parentArr.length; i++) {
			parentNames.push(parentArr[i].name);
		}

		return parentNames.sort().join(", ");
	}

	var result = {};
	var entries = JSON.parse(input.relData).relations;

	var items = [];
	var expandedItems = [];
	entries.forEach(function(entry) {
		var category = {};
		category.id = entry.relationTypeSysId + entry.ciClassDisplayName;
		category.relationTypeDesc = entry.relationTypeDesc;
		category.label = entry.relationTypeDesc + " - " + entry.ciClassDisplayName;
		category.children = [];
		expandedItems.push([category.id]);
		entry.relations.forEach(function(rel) {
			var node = {};
			node.id = rel.childSysId;
			node.ciName = rel.childName;
			node.label = "[L" + rel.level + "] " + _getParentString(rel) + rel.childName;
			node.actionable = true;
			node.table = entry.ciSysClass;
			node.sysId = rel.childSysId;
			node.children = [];
			category.children.push(node);
		});
		category.children.sort(function compare(a, b) {
			return a.label.localeCompare(b.label);
		});
		items.push(category);
	});

	items.sort(function compare(a, b) {
		return a.relationTypeDesc.localeCompare(b.relationTypeDesc);
	});

	result.items = items;
	result.expandedItems = expandedItems;

	return result;
}

function transform(input) {
    var daTemplateToActionName = JSON.parse(input.daTemplateToActionName);
    var config = {
        daActionMapping: {}
    };
    if (!daTemplateToActionName)
        return config;
    var assignmentIds = [];
    for (var assignmentId in daTemplateToActionName)
        assignmentIds.push(assignmentId);

    if (!assignmentIds.length)
        return config;

    var assignmentGR = new GlideRecord("sys_declarative_action_assignment");
    assignmentGR.addQuery("sys_id", assignmentIds);
    assignmentGR.addActiveQuery();
    assignmentGR.query();
    var daActionMapping = {};
    while (assignmentGR.next()) {
        var clientActionId = assignmentGR.getValue("client_action");
        if (clientActionId) {
            var payloadDef = new GlideRecord("sys_declarative_action_payload_definition");
            if (payloadDef.get(clientActionId)) {
                var actionId = assignmentGR.getUniqueValue();
                daActionMapping[daTemplateToActionName[actionId]] = {
                    "assignmentId": actionId,
                    "name": payloadDef.getValue("action_key"),
                    "label": assignmentGR.getValue("label"),
                    "actionType": "uxf_client_action",
                    "actionDispatch": payloadDef.getValue("action_key"),
                    "actionPayload": payloadDef.getValue("payload_template")
                };
            }
        }
    }
    config.daActionMapping = daActionMapping;
    return config;
}

function transform(input) {
    var prevScore = 0;
    var currScore = 0;
    var currScoreDate = "";
    var arrowDir = "";
    var prevScoreDate = "";
    var updatedSettings = 0;
    var n = 0;

    // get last 2 compliance scores and their calculation date
    var gr = new GlideRecord('sn_vsc_hardening_compliance_scores');
    gr.orderByDesc('harc_collected');
    gr.query();

    while (gr.next()) {
        if (n == 0) {
            currScore = parseInt(gr.getValue('harc_score'));
            currScoreDate = new GlideDateTime(gr.getValue("harc_collected")).getDisplayValue();
        } else {
            // if there is more than 1 record with same date in hardening compliance score, get previous scan date not equal to current scan date 
            var prevHarcDate = new GlideDateTime(gr.getValue("harc_collected")).getDisplayValue();
            if (prevHarcDate.split(" ")[0] != currScoreDate.split(" ")[0]) {
                prevScore = parseInt(gr.getValue('harc_score'));
                prevScoreDate = prevHarcDate;
                break;
            }

        }
        n++;
    }

    // if there is no previous run/score, set it with current compliance score 
    if (prevScore == 0) {
	prevScore = currScore;
        prevScoreDate = currScoreDate;
    }

    // set the arrow direction based on current and previous score
    if (currScore < prevScore) {
        arrowDir = "down";
    } else if (currScore > prevScore) {
        arrowDir = "up";
    }

    // get the most affected security area and priority of updated settings
    var scUtil = new sn_vsc.SCComparisonUtil();
    if (currScoreDate != prevScoreDate) { //ensure changed settings are from different dates
        var grSettings = scUtil.getUpdatedSettings(currScoreDate, prevScoreDate);
        if (grSettings) {
            updatedSettings = grSettings.getRowCount();
        }

        var securityArea = scUtil.mostRepeatedSecurityArea(currScoreDate, prevScoreDate);
        var priority = scUtil.getAllPriorityForSecurityArea(securityArea, currScoreDate, prevScoreDate);
    }


    // return all the summary data 
    return [{
        "changeFromPrev": currScore - prevScore,
        "currScore": currScore + "%",
        "prevScore": prevScore + "%",
        "arrowDirection": arrowDir,
        "previousScoreDate": prevScoreDate,
        "updatedSettings": updatedSettings,
        "securityArea": securityArea,
        "priority": priority
    }];

}

