function transform(input) {
	if (!input.sysId || !input.relatedList)
		return [];

	var returnListData = [];
	for (var i = 0; i < input.relatedList.length; i++) {
		var tableData = input.relatedList[i];
		var countGr = new GlideRecordSecure(tableData.table);
		if (tableData.countQuery)
			countGr.addEncodedQuery(tableData.countQuery, true);
		else
			countGr.addQuery('task', input.sysId);

		countGr.query();
		if (countGr.next())
			tableData.count = countGr.getRowCount();
		else
			tableData.count = 0;

		returnListData.push(tableData);
	}
	return returnListData;
}

