{
    "$schema": "http://json-schema.org/draft-06/schema#",
    "title": "chrome_header",
    "type": "object",
    "description": "Chrome Header Page Property Schema",
    "additionalProperties": false,
    "properties": {
        "privatePage": {
            "$ref": "#/definitions/privatePage"
        },
        "publicPage": {
            "$ref": "#/definitions/publicPage"
        }
    },
    "required": [
        "privatePage",
        "publicPage"
    ],
    "definitions": {
        "privatePage": {
            "nowName": "privatePage",
            "nowLabel": "private page configuration",
            "nowDescription": "config object with properties like global_tools and more, see schema",
            "title": "privatePage",
            "description": "Private Page Schema",
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "notificationsEnabled": {
                    "type": "boolean"
                },
                "searchEnabled": {
                    "type": "boolean"
                },
                "userPrefsEnabled": {
                    "type": "boolean"
                },
                "globalTools": {
                    "$ref": "#/definitions/globalTools"
                },
                "contextualConfigurationItems": {
                    "$ref": "#/definitions/contextualConfigurationItems"
                }
            },
            "required": [
                "notificationsEnabled",
                "searchEnabled",
                "userPrefsEnabled",
                "globalTools",
                "contextualConfigurationItems"
            ]
        },
        "publicPage": {
            "nowName": "publicPage",
            "nowLabel": "public page configuration",
            "nowDescription": "config object with complex property action_buttons, see schema",
            "title": "publicPage",
            "description": "Public Page Scheme",
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "actionButtons": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/publicPageActionButton"
                    }
                }
            },
            "required": [
                "actionButtons"
            ]
        },
        "publicPageActionButton": {
            "title": "publicPageActionButton",
            "description": "Public Page Action Button Schema",
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "label": {
                    "type": "string"
                },
                "bare": {
                    "type": "boolean"
                },
                "primary": {
                    "type": "boolean"
                },
                "value": {
                    "$ref": "#/definitions/actionButtonValue"
                }
            },
            "required": [
                "label",
                "value",
                "bare",
                "primary"
            ]
        },
        "contextualConfigurationItems": {
            "title": "contextualConfigurationItems",
            "description": "Configurations to enable contextual configuration items",
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "_roles": {
                    "type": "array",
                    "items": {
                        "type": "string"
                    }
                },
                "enabled": {
                    "type": "boolean"
                },
                "position": {
                    "type": "integer"
                }
            }
        },
        "globalTools": {
            "title": "globalTools",
            "description": "Global Tools Schema",
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "collapsingMenuId": {
                    "type": "integer"
                },
                "primaryItems": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/primaryItem"
                    }
                },
                "secondaryItems": {
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/secondaryItem"
                    }
                }
            }
        },
        "primaryItem": {
            "title": "primaryItem",
            "description": "Primary Item Schema",
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "_roles": {
                    "type": "array",
                    "items": {
                        "type": "string"
                    }
                },
                "label": {
                    "type": "string"
                },
                "icon": {
                    "type": "string"
                },
                "type": {
                    "type": "string"
                },
                "position": {
                    "type": "integer"
                },
                "primaryDisplay": {
                    "type": "string",
                    "enum": [
                        "both",
                        "none",
                        "icon"
                    ]
                },
                "alert": {
                    "type": [
                        "object",
                        "null"
                    ],
                    "properties": {
                        "table": {
                            "type": "string"
                        },
                        "filter": {
                            "type": "string"
                        }
                    }
                },
                "value": {
                    "$ref": "#/definitions/itemValue"
                }
            }
        },
        "secondaryItem": {
            "title": "secondaryItem",
            "description": "Secondary Item Schema",
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "_roles": {
                    "type": "array",
                    "items": {
                        "type": "string"
                    }
                },
                "label": {
                    "type": "string"
                },
                "icon": {
                    "type": "string"
                },
                "type": {
                    "type": "string"
                },
                "position": {
                    "type": "integer"
                },
                "value": {
                    "$ref": "#/definitions/itemValue"
                },
                "primaryDisplay": {
                    "type": "string",
                    "enum": [
                        "both",
                        "none",
                        "icon"
                    ]
                },
                "alert": {
                    "type": [
                        "object",
                        "null"
                    ],
                    "properties": {
                        "table": {
                            "type": "string"
                        },
                        "filter": {
                            "type": "string"
                        }
                    }
                }
            }
        },
        "itemValue": {
            "title": "itemValue",
            "description": "Item Value Schema",
            "type": "object",
            "anyOf": [
                {
                    "type": "object",
                    "properties": {
                        "nav_item": {
                            "$ref": "#/definitions/navItem"
                        }
                    }
                },
                {
                    "type": "object",
                    "properties": {
                        "children": {
                            "type": "array",
                            "items": {
                                "$ref": "#/definitions/navItem"
                            }
                        }
                    }
                },
                {
                    "type": "object",
                    "properties": {
                        "action": {
                            "type": "string"
                        },
                        "payload": {
                            "$ref": "#/definitions/routePayload"
                        }
                    }
                }
            ]
        },
        "navItem": {
            "title": "navItem",
            "description": "Nav Item Schema",
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "label": {
                    "type": "string"
                },
                "leftIcon": {
                    "type": "string"
                },
                "rightIcon": {
                    "type": "string"
                },
                "target": {
                    "type": "string"
                },
                "type": {
                    "type": "string",
                    "enum": [
                        "external",
                        "email",
                        "phone",
                        "modal",
                        "route"
                    ]
                },
                "value": {
                    "anyOf": [
                        {
                            "type": "string"
                        },
                        {
                            "$ref": "#/definitions/routePayload"
                        },
                        {
                            "$ref": "#/definitions/modalPayload"
                        }
                    ]
                }
            }
        },
        "routePayload": {
            "title": "routePaylaod",
            "description": "Route Payload Schema",
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "route": {
                    "type": "string"
                },
                "fields": {
                    "type": "string"
                },
                "params": {
                    "type": "string"
                }
            },
            "required": [
                "route",
                "fields"
            ]
        },
        "modalPayload": {
            "title": "modalPayload",
            "description": "Modal Payload Schema",
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "modalId": {
                    "type": "string"
                },
                "showModal": {
                    "type": "boolean"
                },
                "displayOptions": {
                    "type": "object"
                }
            },
            "required": [
                "modalId",
                "showModal",
                "displayOptions"
            ]
        },
        "actionButtonValue": {
            "title": "actionButtonValue",
            "description": "Action Button Value Schema",
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "_meta": {
                    "type": "object",
                    "properties": {
                        "_path": {
                            "type": "string"
                        },
                        "_value": {
                            "type": "object",
                            "properties": {
                                "value": {
                                    "properties": {
                                        "route": {
                                            "type": "string",
                                            "fields": {
                                                "type": "object"
                                            }
                                        }
                                    },
                                    "type": "object"
                                },
                                "type": {
                                    "type": "string"
                                }
                            }
                        },
                        "_default": {
                            "type": "object",
                            "properties": {
                                "value": {
                                    "type": "string"
                                },
                                "type": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "showModal": {
                        "type": "boolean"
                    },
                    "displayOptions": {
                        "type": "object"
                    }
                }
            }
        }
    }
}
