(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var result = {
		hasCompanyCode: false,
		isPartOfActivePipeline: false,
	};

	try {		
		var vendorInfo = new sn_appauthor.ScopedAppVendorInfoObtainer().obtainFromAppRepo();

		var currentInstanceInfo = new sn_pipeline.PipelineConfigChecks().getCurrentInstanceInfo(DeploymentPipelineConstants.APPLICATION_DEPLOYMENT);

		result['hasCompanyCode'] = !!vendorInfo.key;
		result['isPartOfActivePipeline'] = currentInstanceInfo.hasGivenPipelineTypeConfiguredInController;
	} catch (exception) {
		throw new sn_ws_err.ServiceError()
			.setStatus(500)
			.setMessage(gs.getMessageLang('Unable to retrieve company code or pipeline details. {0}', 'en', exception.message))
			.setDetail(gs.getMessage('Unable to retrieve company code or pipeline details. {0}', exception.message));
	}
	response.setBody(result);
	response.setStatus(200);

})(request, response);

(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var currentId = request.body.data.currentId; // Change_request id
	var currentTable = request.body.data.currentTable; // Change_request
	var resourceId = request.body.data.resourceId; // Incident id
	var resourceTable = request.body.data.resourceTable; // Incident
	var actionStage = request.body.data.actionStage;

	var currentGr = new GlideRecordSecure(currentTable);
	if (!currentGr.get(currentId))
		return response.setBody({successful: false, message: gs.getMessage("Could not find Change Request")});

	var resourceGr = new GlideRecordSecure(resourceTable);
	if (!resourceGr.get(resourceId))
		return response.setBody({successful: false, message: gs.getMessage("Could not find Incident")});

	var currentNumber = currentGr.number;
	var resourceNumber = resourceGr.number;

	var body = {};
	if (actionStage === 'pre_modal') {
		body.successful = true;
		body.useModal = true;
		body.modalTitle = gs.getMessage('Link to Incident');
		body.errMsg = gs.getMessage('Could not link Incident to Change Request');
		body.modalInfoMsg = gs.getMessage('Now linking {0} to {1}.', [currentNumber, resourceNumber]);
		body.modalFields = [{label: currentGr.work_notes.getLabel(), name: 'work_notes', value: '', fieldType: 'textarea'}, {label: currentGr.comments.getLabel('comments'), name: 'comments', value: '', fieldType: 'textarea'}];
		body.postModalRestRequired = true;
	} else if (actionStage === 'post_modal') {
		resourceGr.caused_by = currentGr.sys_id;
		resourceGr.update();
		body.successful = true;
		body.infoMsg = gs.getMessage('{0} has been identified as a possible cause for {1}.', [currentNumber, resourceNumber]);
		body.useModal = true;
	} else
		return response.setBody({successful: false, message: gs.getMessage("Invalid action stage")});

	response.setBody(body);

})(request, response);

(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var DEFAULT_COMMIT_MODE_SYS_PROPERTY = 'glide.sourcecontrol.default_commit_mode';
    var ALLOW_COMMIT_MODE_SELECTION_SYS_PROPERTY = 'sn_devstudio.vcs.allow_commit_mode_selection';

    var defaultCommitMode = 'exclude_untracked';
    var allowCommitModeSelection = 'true';

    var grProps = new GlideRecord('sys_properties');
    grProps.addEncodedQuery('name=' + DEFAULT_COMMIT_MODE_SYS_PROPERTY + '^ORname=' + ALLOW_COMMIT_MODE_SELECTION_SYS_PROPERTY);
    grProps.query();

    while (grProps.next()) {
        if (grProps.getValue('name') === DEFAULT_COMMIT_MODE_SYS_PROPERTY)
            defaultCommitMode = grProps.getValue('value');
        else if (grProps.getValue('name') === ALLOW_COMMIT_MODE_SELECTION_SYS_PROPERTY)
            allowCommitModeSelection = grProps.getValue('value');
    }

    response.setBody({
        defaultCommitMode: defaultCommitMode,
        allowCommitModeSelection: allowCommitModeSelection
    });
    response.setStatus(200);
})(request, response);

