(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var result = {};
	response.setContentType('application/json');

	try {
		var service_sys_id = '';
		var error = {'message':'Unknown error'};
		var payload = request.body.data;

		if(!isRequestPayloadValid(payload, error)) {
			response.setError(throwError(400, error));
			return;
		}

		var helper = new CSDMAppServiceHelper();

		if(payload.hasOwnProperty('basic_details')) {
			//TODO: Rectify error message for uniqeness and mandatory attributes
			service_sys_id = helper.createApplicationService(payload);
			if(JSUtil.nil(service_sys_id)) {
				error.message = 'Could not create Application Service based on given inputs';
				response.setError(throwError(400, error));
				return;
			}
			result.service_sys_id = service_sys_id;
			var gr = new GlideRecord('cmdb_ci_service_auto');
			if(gr.get(service_sys_id))
				result.number = gr.getValue('number');
		}

		if(payload.hasOwnProperty('relationships') ) {
			helper.createCSDMRelationships(service_sys_id, payload.relationships);
			//TODO: add new rel_sys_ids to the result
		}

		response.setStatus(200);
		response.setBody(result);

	} catch(err) {
		response.setError(throwError(500, error));
		return;
	}


	// Supporting functions
	function isRequestPayloadValid(payload, errorMsg) {
		if(payload.hasOwnProperty('basic_details') || payload.hasOwnProperty('relationships')) {

			var basic = payload.basic_details;
			if(JSUtil.notNil(basic)) {
				if(basic.hasOwnProperty('sys_id') || basic.hasOwnProperty('sys_class_name') || basic.hasOwnProperty('sys_class_path')) { // TODO: Accomodate all sys fields
					errorMsg.message = "Updating system fields is not allowed";
					return false;
				}
			}
			var rels = payload.relationships;
			if(JSUtil.notNil(rels)) {
				if(!rels.hasOwnProperty('business_app') && !rels.hasOwnProperty('business_service_offering') && !rels.hasOwnProperty('technical_service_offering')) {
					errorMsg.message = "Relationships are not specified correctly";
					return false;
				}
			}
			return true;
		}
		errorMsg.message = "Request payload is not in expected format";
		return false;
	}

	function throwError(status, errMsg) {
		var genericError = new sn_ws_err.ServiceError();
		genericError.setStatus(status);
		genericError.setMessage("Problem processing request");
		genericError.setDetail(errMsg.message);
		return genericError;
	}

})(request, response);

(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var table = request.body.data.table || null;
    if (!table)
        return new sn_ws_err.BadRequestError('table not provided');

    var sysId = request.body.data.sysId || null;
    if (!sysId)
        return new sn_ws_err.BadRequestError('sysId not provided');

    var view = request.body.data.view || null;
    if (!view)
        return new sn_ws_err.BadRequestError('view not provided');

    var state = request.body.data.state || '';
    var isNew = (request.body.data.isNew + '' === 'true') || (sysId === '-1');

    var overviewUtils = new SOWOverviewUtils();
    var result = {
        formData: overviewUtils.getFormSectionsForLayout(table, sysId, view),
        stateFormData: overviewUtils.getSectionsForState(table, sysId, state, isNew),
        versionNumber: gs.getProperty('itsm_family_release_version_check',0)
    };

    return result;
})(request, response);

(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var offset = isNaN(parseInt(request.queryParams.offset)) ? 0 : parseInt(request.queryParams.offset);
	var limit = isNaN(parseInt(request.queryParams.chg_model_limit)) ? 40 : parseInt(request.queryParams.chg_model_limit);
	var filter = (request.queryParams.filter && request.queryParams.filter !== "undefined") ? request.queryParams.filter + "" : "all";
	var orderBy = (request.queryParams.orderby && request.queryParams.orderby !== "undefined") ? request.queryParams.orderby + "" : "name";
	var textSearch = (!request.queryParams.text_search || request.queryParams.text_search === "undefined") ? "" : request.queryParams.text_search + "".trim();
	var restrict = (!request.queryParams.restrict || request.queryParams.restrict === "undefined") ? "" : request.queryParams.restrict + "".trim();

	return new SowChangeCards().getChangeCards(offset, limit, filter, orderBy, textSearch, restrict);

})(request, response);

(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var requestBody = request.body.nextEntry();
	var isPreview = requestBody["isPreview"];
	if (isPreview == true) {
		var value = requestBody["sysparm_value"];
		var regexId = requestBody["sysparm_regexId"];
		if (global.JSUtil.notNil(value) && global.JSUtil.notNil(regexId)) 
			return GlideappQuestion.validateRegexForValue(regexId, value);

		return true;		
	}
    var questionId = request.pathParams.sys_id;
	if (!questionId)
		throw new sn_ws_err.BadRequestError("SYS_ID should be provided as path param.");

	var questionValue = requestBody["sysparm_value"];
	if (!questionValue) {
		throw new sn_ws_err.BadRequestError("Question value should be provided as request body.");
	}

	var question = GlideappQuestion.getQuestion(questionId);
	if (!question.validateRegex(questionValue)) {
		var error_response = {};
		response.setStatus(400);
		error_response.errMsg = "Validation error: " + question.getValidationMessage();
		return error_response;
	} else
		return true;

})(request, response);

(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var currentTable = request.body.data.currentTable;
    var currentId = request.body.data.currentId;
    var resourceTable = request.body.data.resourceTable;
    var resourceId = request.body.data.resourceId;
    var allActions = request.body.data.allActions;
    var actions = [];
    var uniqueActions = [];

    var sau = new SNC.SearchActionUtils();
    //PRB1367764: Treat answer as a question to pass the check that the actions exist
    if (resourceTable === 'kb_social_qa_answer') {
        resourceTable = 'kb_social_qa_question';
    }

    if (sau.loadRecords(currentTable, currentId, resourceTable, resourceId)) {
        for (var i = 0; i < allActions.length; i++) {
            var currentCondition = allActions[i].currentActionCondition;
            var resourceCondition = allActions[i].actionCondition;

			if (allActions[i].sysId) {
				if (sau.checkVisibility(allActions[i].sysId)) {
					actions.push(allActions[i].actionId);
					uniqueActions.push(allActions[i].sysId);
				}
			} else {
				if (sau.checkVisibilityPreOrlandoOnly(currentTable, currentCondition, resourceTable, resourceCondition)) {
					actions.push(allActions[i].actionId);
					uniqueActions.push(allActions[i].sysId);
				}
      }
        }
    }

    var body = {};
    body.succeed = true;
    //itemId not used since Orlando. Leaving it here only to serve backward compatibility for pre-Orlando to Orlando upgrade.
    body.itemId = resourceTable + ':' + resourceId;
    //keeping this for backwards compatibility
    body.actions = actions;
    body.uniqueActions = uniqueActions;
    response.setBody(body);
})(request, response);

