(function process(request, response) {
    var appId = request.pathParams.appId;

	var allowed = VCSAppAccessCheck.verifyWriteAccess(appId) && VCSAppAccessCheck.verifyPublishAccess(appId);

    if (!allowed) 
		VCSAppAccessCheck.throwAccessDenied(appId);

    var version = request.body.data.version,
        publishToStore = !!request.body.data.publish_to_store,
        isAppCustomization = !!request.body.data.is_app_customization,
        versionInfo = {},
        appRecord,
        errorMessage,
        body = {};

    var skipVersionCheck = false;
    if (isAppCustomization) {
		var anchorScopeId = appId;
		var scopedPlugin = new GlideRecord('sys_scoped_plugin');
		if (scopedPlugin.isValid()) {
			scopedPlugin.addQuery('scope', appId);
			scopedPlugin.query();
			if (scopedPlugin.next())
				anchorScopeId = scopedPlugin.getValue('parent_scope_id') || appId;
		}

        appRecord = new GlideRecord('sys_app_customization');
        appRecord.addQuery('vendor_app', anchorScopeId);
        appRecord.query();
        if (appRecord.next())
            appId = appRecord.getUniqueValue();
        else
            skipVersionCheck = true;
    }

    versionInfo = new sn_appauthor
        .ScopedAppVendorInfoObtainer()
        .obtainDisplayVersionFromAppRepo(appId, version, publishToStore);

    // We get null back if we can't talk to the repo. assume the number is OK
    // so the user can continue other operations like saving to git
    if (!versionInfo)
        versionInfo = {
            allowed: false,
            errorMessage: gs.getMessage('Unable to validate app version because the app repository is not available.')
        };
    else if (skipVersionCheck)
        versionInfo = {
            allowed: true
        };

    body.allowed = !!versionInfo.allowed;

    if (body.allowed) {

        if (isAppCustomization)
            new sn_appclient.SetAppCustomizationVersion().setVersion(appId, version);
        else {
            appRecord = new GlideRecord('sys_app');

            if (appRecord.get(appId)) {
                appRecord.setValue('version', version);
                appRecord.update();
            }
        }
    } else {
        body.errorMessage = versionInfo.errorMessage || gs.getMessage('Invalid version number');
    }

    response.setBody(body);
    response.setContentType('application/json');
    response.setStatus(200);

})(request, response);

(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var tables_output = [];
    var rotatedTables = getRotatedTables();
    var queryParams = request.queryParams;

    if (!queryParams.sysparm_transaction_scope) {
        throw new sn_ws_err.ServiceError()
            .setStatus(500)
            .setMessage('sysparm_transaction_scope query parameter is required.');
    }

    function addAdditionalQueries(gr) {
        if (queryParams.only_extendable && queryParams.only_extendable[0] === 'true')
            gr.addQuery('is_extendable', 'true');
        if (queryParams.only_in_scope && queryParams.only_in_scope[0] === 'true')
            gr.addQuery('sys_scope', queryParams.sysparm_transaction_scope);
        if (queryParams.only_out_of_scope && queryParams.only_out_of_scope[0] === 'true')
            gr.addQuery('sys_scope', '!=', queryParams.sysparm_transaction_scope);
        if (queryParams.order_by && typeof queryParams.order_by[0] === 'string')
            gr.orderBy(queryParams.order_by[0]);
        if (queryParams.limit && typeof queryParams.limit[0] === 'string')
            gr.setLimit(queryParams.limit[0]);
        if (queryParams.search_query && typeof queryParams.search_query[0] === 'string') {
            gr.addEncodedQuery(queryParams.search_query[0]);
        }
    }

    function isExcludedRotation(name) {
        return rotatedTables.indexOf(name) > -1;
    }

    function getRotatedTables() {
        var rotatedList = [];
        var rotations = [];
        var rotatedTable = new GlideRecord("sys_table_rotation");
        rotatedTable.query();

        while (rotatedTable.next())
            rotatedList.push(rotatedTable.getValue("name"));

        var rotatedExtension = new GlideRecord("sys_table_rotation_schedule");
        rotatedExtension.query();

        while (rotatedExtension.next()) {
            if (rotatedList.indexOf(rotatedExtension.getValue("table_name")) === -1)
                rotations.push(rotatedExtension.getValue("table_name"));
        }
        return rotations;
    }

    function startsWith(str, word) {
        return str.lastIndexOf(word, 0) === 0;
    }

    function shouldExcludeTable(tableName) {
        if (isExcludedRotation(tableName))
            return true;

        if (
            startsWith(tableName, 'v_') ||
            startsWith(tableName, 'sysx_') ||
            startsWith(tableName, 'var__') ||
            startsWith(tableName, 'ts_')

        )
            return true;

        return false;
    }

    var tablesGR = new GlideRecord('sys_db_object');
    addAdditionalQueries(tablesGR);
    tablesGR.query();

    while (tablesGR.next()) {
        var tableName = tablesGR.getValue('name');

        if (shouldExcludeTable(tableName))
            continue;

        var table = {
            name: tablesGR.getValue('name'),
            sys_id: tablesGR.getUniqueValue(),
            label: tablesGR.getDisplayValue('label'),
            is_extendable: tablesGR.getValue('is_extendable')
        };
        tables_output.push(table);
    }

    response.setBody(tables_output);
    response.setStatus(200);

})(request, response);

