(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    const scopeId = request.pathParams.scope_id;

    if (!scopeId) {
        response.setStatus(400);
        return response.setError('Missing required query parameter: scope_id');
    }

    const DEFAULT_IGNORED_TABLES = ['sys_metadata_link', 'sys_module', 'sys_ux_lib_asset'].join(',');
    const ignoredTables = gs.getProperty('sn_glider.sync.ignored_tables', DEFAULT_IGNORED_TABLES)?.split(',');

    function fetchLatestMetadata(scopeId) {
        var gr = new GlideRecord('sys_metadata');
        gr.addQuery('sys_scope', scopeId);
        gr.addQuery('sys_class_name', 'NOT IN', ignoredTables.join(','));
        gr.orderByDesc('sys_updated_on');
        gr.setLimit(1);
        gr.query();

        if (gr.next()) {
            var metadata = {
                sys_id: gr.getValue('sys_id'),
                sys_updated_on: new GlideDateTime(gr.getValue('sys_updated_on')).getNumericValue(),
                sys_updated_by: gr.getValue('sys_updated_by'),
                sys_created_on: new GlideDateTime(gr.getValue('sys_created_on')).getNumericValue(),
                sys_scope: gr.getValue('sys_scope'),
                sys_class_name: gr.getValue('sys_class_name')
            };

            return metadata;
        }
        return null;
    }

    try {
        var metadata = fetchLatestMetadata(scopeId);

        if (metadata) {
            response.setStatus(200);
            response.setBody(metadata);
            return;
        }

        // Return an empty response if no metadata found
        response.setStatus(200);
        response.setBody({});
    } catch (e) {
        response.setStatus(500);
        response.setError(e.message);
    }

})(request, response);

