(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var body = "";
	var msg = "";
	var json = "";

	var bsid = "";
	var errorId = "";
	var targetid = "";

	var bsManager = new SNC.BusinessServiceManager();

	try {
		body = GlideStringUtil.getStringFromStream(request.body.dataStream);

		if (GlideStringUtil.nil(body)) {
			msg = gs.getMessage("Wrong input for Skip and Resume, input must not be empty");
		} else {
			try {
				json = JSON.parse(body);
			}
			catch (eJson) {
				msg = gs.getMessage("Wrong input for Skip and Resume, input must be JSON. {0}", [eJson]);				
			}

			if (msg === "") {
				bsid = json.SKIP_N_RESUME.bsid;
				errorId = json.SKIP_N_RESUME.errorId;
				targetid = json.SKIP_N_RESUME.targetId;

				bsManager = new SNC.BusinessServiceManager();
				msg = bsManager.skipAndResume(bsid,errorId,targetid);
				if (msg !== "") {
					msg = gs.getMessage("Add CI Manualy failed: {0}", [msg]);
				}
			}
		}
	}catch (e){
		msg = gs.getMessage("Add CI Manualy failed: {0}", [msg]);
	}

	if ((msg === "") && (json.manualEP)){

		try {
			var epClassName=json.manualEP.epClassName;
			var epAttributes=json.manualEP.epAttributes;
			var ep = new GlideRecord(epClassName);

			if (ep.isValid()) {

				ep.initialize();
				for (var attribName in epAttributes)
					ep.setValue(attribName, epAttributes[attribName]);

				ep.setWorkflow(true);


				try{
					bsManager.addManualConnection(targetid,ep,bsid);
				}
				catch(e){
					//converting the error to string.
					// extracting the message. the format of stringified exception is --> exceptionClass: message.
					//if the exception is not in that format, we will show unformatted exeption.
					var ex=e + "";
					var msgs=ex.split(":");
					var msg=ex;

					if(msgs[1]){
						msg=msgs[1];
					}


					msg = gs.getMessage("Add CI succeded, add connection failed. {0}", [msg]);
				}
			}
			else {
					msg = gs.getMessage("Add CI succeded, add connection failed. Table '{0}' must be an endpoint.", [epClassName]);				
			}

		}catch (e){
			msg = gs.getMessage("Add CI succeded, add connection failed due to the following error: {0}", [e]);
		}
	}

	if (msg !== "") {
		response.setStatus(400);
		throw new sn_ws_err.BadRequestError(msg);
	}

})(request, response);

(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var requestBody = request.body.data;
    var recommender = requestBody.recommender;
    var recommended_by = requestBody.recommended_by;
    var action_sys_id = requestBody.action_sys_id;
    var inputData = requestBody.inputData;

    var responseStatus = 200;
    var ErrorCode = {
        "InvalidArguments": 2
    };
    var apiResult = {};
    try {
        if (recommender && recommended_by && (inputData && action_sys_id)) {
			var guidanceModel = new sn_gd_guidance.GuidedDecisionsGuidanceModel();	
			var isUserValid = guidanceModel.isUserValid(recommender, recommended_by);
			if(!isUserValid){
				var accessDeniedResponseBody={status:"400", errorType:"accessDenied", errorMessage:gs.getMessage("Access Denied: Check permissions to view the guidance")};
				response.setBody(accessDeniedResponseBody);
				return;
			}
            var guidedDecisionsExecution = new sn_gd_guidance.GuidedDecisionsGuidanceExecution();
            var result = guidedDecisionsExecution.updateGuidanceInstanceExecution(
                recommender, recommended_by, action_sys_id, inputData);
			var guidanceConstant = new sn_gd_guidance.GuidanceConstantsSNC();
			if(result.actionStatus == guidanceConstant.ERROR){
				responseStatus = 200;
				var responseBody = result;
				response.setStatus(responseStatus);
				response.setBody(responseBody);
				return;
			}
            if (result === ErrorCode.InvalidArguments) {
                responseStatus = 400;
                response.setError(new sn_ws_err.BadRequestError(gs.getMessage('Invalid Record to update. Update Failed.')));
            } else if(typeof result === 'object' && result.guidanceDetails.state =='in_error' ) {
				response.setBody(result);

			} else {
                apiResult.message = "Input Values Updated successfully.";
                apiResult.status = "success";
                response.setBody(apiResult);
            }
        } else {
            responseStatus = 400;
            response.setError(new sn_ws_err.BadRequestError(gs.getMessage('Missing required parameters')));
        }
    } catch (err) {
        responseStatus = 500;
        var customErrObj = new sn_ws_err.ServiceError();
        customErrObj.setStatus(responseStatus);
        gs.error(JSON.stringify(err));
        customErrObj.setMessage(gs.getMessage('Updating Input Values Failed : {0}', [err + '']));
        response.setError(customErrObj);
		response.setStatus(responseStatus);
     } finally {
        response.setStatus(responseStatus);
        response.setContentType('application/json');
    }

})(request, response);

function getPortalPreferences(portal_section) {
	var id;
	var gr = new GlideRecord('sys_portal_preferences');
	gr.addQuery('portal_section',portal_section);
	gr.query();
	var prefs = {};
	prefs ["portal_section"] = portal_section;
	while(gr.next()) {
		var key = gr.getValue("name");
		var valForKey = gr.getValue("value");
		prefs[key] = valForKey;
		if(gr.getValue('name') === 'renderer')
			id =gr.getValue('name');
	}

	return {"id": id,"prefs":prefs};
}

