    }, errorHandler);


    function errorHandler() {
        g_form.setDisplay('fulfillment_steps', false);
        g_form.setDisplay('delivery_time_var', false);
        g_form.addErrorMessage(ERROR_MESSAGE_VALIDATION_FAILED);
    }
