result.post_processor_script:
new ProbePostProcessor({

	/**
     * Runs the probe instance
     */
	process : function() {
		var packages = [];
		var softwareMatch = {};
		var softwareArray = [];
		var token;

		var line = output.split('\n');
		for (var i = 0; i < line.length; i++) {
			if (line[i].indexOf(':') != -1) {
				token = line[i].split(':');
				for (var k = 0; k < token.length; k++)
					token[k] = token[k].replace(/^\s*/, "");

				softwareMatch[token[0]] = token[1];
			}

			if (line[i] == '' || line.length - i == 1) {
				softwareArray.push(softwareMatch);
				softwareMatch = {};
			}
		}

		for (var j = 0; j < softwareArray.length; j++) {
			softwareMatch = softwareArray[j];

			var pkg_name = softwareMatch.PKGINST;
			var name     = softwareMatch.NAME;
			var cat      = softwareMatch.CATEGORY;
			var version  = softwareMatch.VERSION;
			var vendor   = softwareMatch.VENDOR;
			var desc     = softwareMatch.DESC;

			var software = {};
			if (!name)
				continue;

			software.name = name;
			if (cat)
				software.category = cat;

			if (version)
				software.version = version;

			if (vendor)
				software.vendor = vendor;

			if (desc)
				software.short_description = desc + (pkg_name ? (" ; Pkginst: " + pkg_name + ".") : "");

			packages.push(software);
		}

		related_data.packages= packages;

	},

	type: "ProbePostProcessor"
});

new ProbePostProcessor({
	/**
     * Runs the probe instance
     */
	process : function() {		
		current.mongodb_conf = output.trim();

		var filename = g_probe.getParameter("powershell_param_confFilePath") + '';
		if (filename.indexOf("\\") > -1) {
			current.config_file = filename;
			current.config_directory = filename.substring(0, filename.lastIndexOf("\\"));
		}

		var param_port_set = (g_probe.getParameter("port_set") == "true");
		if (param_port_set)
			return;
		var lines = output.split("\n");
		for (var i = 0; i < lines.length; i++) {
			var line = lines[i];

			if (line == '' || line.startsWith('#'))
				continue;

			var parts = line.indexOf("=") > 0 ? line.split("=") : line.split(":");
			var name = parts[0].trim();
			var value = parts[1].trim();

			if (name=='port') {
				current.tcp_port = value;
				break;		// no longer need to search the file
			}
		}
		if (!current.tcp_port)
			current.tcp_port="27017";
	},

	type: "ProbePostProcessor"
});

new ProbePostProcessor({

	/**
     * Runs the probe instance
     */
	process : function() {
		var patches = [];

        var lines = output.split(/\n/);
        for (var i = 0; i < lines.length; i++) {
            var parts = lines[i].split(" ");
            if (parts[0] && parts[0] == "Patch:" && parts[1]) {
                	var patchName = parts[1];
                	var patch = {};
                	patch.name = patchName;
                	patches.push(patch);
            }
        }

		related_data.patches = patches;
	},

	type: "ProbePostProcessor"
});

new ProbePostProcessor({

    /**
     * Runs the probe instance
     */    
    process : function() {
		var lines = output.split("\n");

		var ram = 0;

		for (var i = 0; i < lines.length; i++) {
			var columns = lines[i].split(/\s+/);

            if (columns[0] == "MemTotal:")
                ram += parseInt(columns[1]) / 1024;
		}

		if (ram > 0)
			current.ram = ram;		
	},

    type: "ProbePostProcessor"
});

new ProbePostProcessor({

	/**
     * Runs the probe instance
     */
	process : function() {

        var serials = [];
        var lines = output.split('\n');
        for (var i=0; i<lines.length; i++) {
            var line = lines[i];
            var m = line.match(/^"(.+?)".*"(.+?)"$/);
            if (!m)
                continue;

            var serial = {};

            serial.type = m[1];
            serial.value = m[2];

            serials.push(serial);
        }

		related_data.serials = serials;

	},

	type: "ProbePostProcessor"
});

new ProbePostProcessor({

	packages: [],

	/**
     * Runs the probe instance
     */    
    process : function() {    
		related_data.packages = this.packages; 

		var lines = output.trim().split('\n');
		for (var i = 0; i < lines.length; i++) {
            var line = lines[i];

		    if (JSUtil.nil(line))
                continue;

		    var res = line.split(":");
		    var prefix = res[0].trim();
		    var value = line.substring(res[0].length+1).trim();

            var eventHandler = 'on_' + prefix;
            if (this[eventHandler])
                this[eventHandler](value);
            else
                this.log('Error: Invalid prefix in install software output: ' + prefix);
        }

		this.pushPkg(); // Push the last package... 		
    },

	on_Package: function(value) {
	    this.pushPkg(); 
 	   this.name = value;	   
    },    

    on_Status: function(value) {
 	   this.status = true;
        var installed = /^.*ok.*installed.*$/.exec(value);
	    if (!installed)
		    this.status = false;	   
    },

    on_Maintainer: function(value) {
	    if (!this.status)
		    return;

	    this.maintainer = value;
        var maintMat = /^(?:.*?)<.+?@(?:(?:.+?)\\.)*(.+?)\\.(?:(?:org)|(?:com)|(?:net))>$/.exec(value);
        if (maintMat)
            this.maintainer = maintMat[1];
    },

    on_Version: function(value) {
	    if (!this.status)
		    return;

	    this.version = value;
    },

    on_Release: function(value) {   
	    if (!this.status)
		    return;

        // version is now RPM %{Version}-%{Release}
        this.version += JSUtil.nil(value) ? '' : '-' + value;
    },

    pushPkg: function() {
 	   // If there's no package name... then we have nothing to push
	    if (JSUtil.nil(this.name))
		    return;

	    // The status of pkg is not installed.. reset and return
	    if (!this.status) {  
		    this.resetPkg();
		    return;
	    }

        var software = {};
        software.name = this.name;
        software.version = this.version;
        software.vendor = this.maintainer;

        this.packages.push(software);

	    this.resetPkg();
    },

    resetPkg: function() {
	    this.name = null;
	    this.version = null;
	    this.maintainer = null;
	    this.status = null
    },

	type: "ProbePostProcessor"
});

new ProbePostProcessor({
	/**
 	* Runs the probe instance
 	*/
	process : function() {
		if (JSUtil.notNil(output))
			this.parseOutput(output);
	},

	parseOutput: function(output) {
		var lines = output.split(/\n/);
		for (var i = 0; i < lines.length; i++) {
			var line = lines[i];

			var homeIndex = line.indexOf("snc_ora_home=");
			if(homeIndex >= 0){
				current.oracle_home = line.replace("snc_ora_home=","").trim();
				continue;
			}

			var versionIndex = line.indexOf("snc_ora_version=");
			if(versionIndex >= 0){
				current.version = line.replace("snc_ora_version=","").trim();
				continue;
			}			
		}
	},
	type: "ProbePostProcessor"
});

new ProbePostProcessor({

    /**
     * Runs the probe instance
     */    
    process : function() {
		var lines = output.split("\n");

		// no results
		if (lines.length == 0)
			return;

		var regexp = new RegExp("^\\d+\\s+([^\\s]+)", 'im');
		var match = regexp.exec(output);
		if (match) {
			var mainClass = match[1].trim();
			var shortName = mainClass.substring( mainClass.lastIndexOf('.') + 1);
			current.name = shortName + '@' + this.getParameter('computerName');
		}
	},

    type: "ProbePostProcessor"
});

new ProbePostProcessor({
	/**
 	* Runs the probe instance
 	*/
	process : function() {
		if (JSUtil.notNil(output))
			current.pfile = output.trim();
	},
	type: "ProbePostProcessor"
});

new ProbePostProcessor({

    /**
     * Runs the probe instance
     */    
	process : function() {    
		// parse the results from our lsof output, leaving the result in result.current.connections...
		if (JSUtil.nil(output))
			return;
		var sliceIndex = output.indexOf('\n');
		if (sliceIndex === -1) {
			g_probe.setError('Unexpected output: ' + output);
			return;
		}
		var commandOutput = output.slice(sliceIndex + 1);
		if (output.startsWith('lsof output'))
			this.processOutput(LSOFParser,commandOutput);
		else if (output.startsWith('netstat output')) {
			commandOutput = this.normalizeNetstat(commandOutput);
			this.processOutput(DiscoveryNetstatParser,commandOutput);
		}
		else
			g_probe.setError('Both netstat and lsof commands failed to get connection information');	
    },

	//netstat output gives IPs in the form of ip.port converting it to ip:port
	//eg: 10.196.33.215.22  ->  10.196.33.215:22 
	normalizeNetstat : function(output) {
		var outputArray = output.split("\n");
		for(var i=0; i<outputArray.length; i++) {
			var outputParts = outputArray[i].split(/\s+/);
			outputParts[3] = outputParts[3].replace(/(.*)\.(.*)/, '$1:$2');
			outputParts[4] = outputParts[4].replace(/(.*)\.(.*)/, '$1:$2');
			outputArray[i] = outputParts.join(" ");
		}
		return outputArray.join("\n");
	},

	processOutput : function(parser, output) {
		var np = new parser();
		np.parse(output);
		related_data.connections = np.tcp;
		related_data.isValid = np.isValid;
		related_data.error_string = np.errorString;
	},

	type: "ProbePostProcessor"
});

new ProbePostProcessor({
	/**
     * Runs the probe instance
     */
	process : function() {

		current.mongodb_conf = output.trim();

		var filename = g_probe.getParameter("filename") + '';
		// Remove unnecessary quotes due to space in filename
		filename = filename.replace(/['"]+/g, '');
		if (filename.indexOf("/") > -1) {
			current.config_file = filename;
			current.config_directory = filename.substring(0, filename.lastIndexOf("/"));
		}

		// port will be set by classify if specified on command line
		var param_port_set = (g_probe.getParameter("port_set") == "true");
		if (param_port_set)
			return;

		var lines = output.split("\n");
		for (var i = 0; i < lines.length; i++) {
			var line = lines[i];

			if (line == '' || line.startsWith('#'))
				continue;

			var parts = line.indexOf("=") > 0 ? line.split("=") : line.split(":");
			var name = parts[0].trim();
			var value = parts[1].trim();

			if (name=='port') {
				current.tcp_port = value;
				break;		// no longer need to search the file
			}
		}
		if (!current.tcp_port)
			current.tcp_port = "27017";
	},

	type: "ProbePostProcessor"
});

new ProbePostProcessor({

    /**
     * Runs the probe instance
     */
    process: function() {
        //Check if output is from lsof or pfiles and parse accordingly
        if (JSUtil.nil(output))
            return;
        var sliceIndex = output.indexOf('\n');
        if (sliceIndex === -1) {
            g_probe.setError('Unexpected output: ' + output);
            return;
        }
        var commandOutput = output.slice(sliceIndex + 1);
        if (output.startsWith('lsof output')) {
            // parse the results from our lsof output, leaving the result in result.current.connections...
            var lp = new LSOFParser();
            lp.parse(commandOutput);
            related_data.connections = lp.tcp;
            related_data.isValid = lp.isValid;
            related_data.error_string = lp.errorString;
        } else if (output.startsWith('pfiles output')) {
            // parse the results from our pfiles output, leaving the result in result.current.connections...
            var pfilesParser = new DiscoveryPfilesParser();
            pfilesParser.parse(commandOutput);
            related_data.connections = pfilesParser.tcp;
            related_data.isValid = pfilesParser.isValid;
            related_data.error_string = pfilesParser.errorString;
        }
    },

    type: "ProbePostProcessor"
});

new ProbePostProcessor({

    /**
     * Runs the probe instance
     */
    process: function() {
        // parse the results from our ps output, leaving the result in sensor.running_processes...
        var psop = new PsOutputParser();
        var procs = psop.parse(output);
        try {
            var rpf = this.getParameter("running_process_filter");
            if (rpf) {
				try {
    	            var filtering = new RunningProcessesFilter();
	                var filterDefs = JSON.parse(rpf);
        	        procs = filtering.filterResults(procs, filterDefs);
				} catch (e) {
					var evaluator = Packages.com.snc.discovery.runningprocess.api.RunningProcessFilterEvaluatorJSProvider.get();
					procs = JSON.parse(evaluator.filter(rpf, procs));
				}
            }
        } catch (e) {
            if (typeof ms != 'undefined')
                ms.log("AIX - Active Processes: Something went wrong while trying to filter processes. Error=" + e);
            else
                // If we don't have access to ms, we are not on the MID
                // Which means we are in classic mode:
                DiscoveryLogger.warn("AIX - Active Processes: Process filtering skipped. Classic mode is not supported.");
        }
        related_data.running_processes = procs;
        related_data.error_msg = psop.error_msg;
    },

    type: "ProbePostProcessor"
});

new ProbePostProcessor({

    /**
     * Runs the probe instance
     */
    process : function() {
		var lowercaseOutput = output.toLowerCase();
		var versionMatch = null;

        if (this._isMatch('red hat', lowercaseOutput)) {
			if (this._isMatch('oracle', lowercaseOutput)) {
				current.os = "Linux Oracle";
				versionMatch = /release ([0-9\.]+)/.exec(lowercaseOutput);
			}
			else{
				current.os = "Linux Red Hat";
				versionMatch = /release ([0-9\.]+)/.exec(lowercaseOutput);
			}
		}
        else if (this._isMatch('fedora', lowercaseOutput)) {
            current.os = "Linux Fedora";
			versionMatch = /release ([0-9\.]+)/.exec(lowercaseOutput);
		}
        else if (this._isMatch('suse', lowercaseOutput)) {
            current.os = "Linux SuSE";
			//Sample OS version formats that are expected to match the regex are 15-SP1, 15.1, 15
			versionMatch = /version ?= ?"([0-9]+((-|.)[a-z0-9]+)*)"/.exec(lowercaseOutput);
		}
        else if (this._isMatch('centos', lowercaseOutput)) {
            current.os = "Linux CentOS";
			versionMatch = /release ([0-9\.]+)/.exec(lowercaseOutput);
		}
		else if (this._isMatch('ubuntu', lowercaseOutput)) {
			current.os = "Linux Ubuntu";
			versionMatch = /version="([0-9\.]+)/.exec(lowercaseOutput);
		}
        else {
            current.os = "GNU/Linux";
		}

		if(versionMatch && versionMatch[1])
			current.os_version = versionMatch[1].toUpperCase();
    },

	// Return false if name not found, or name found on line preceded by 'id_like='.
	// If present, id_like= is followed by a list of similar Linux distribution names.
	//
	// Added this method for PRB942869 fix so won't incorrectly classify a Linux server if the *release 
	// files have an ID_LIKE= line followed by a list containing a different OS names
	// AND that line appears before the line with ID=<correct OS name>.
	// Many versions of Linux don't have ID_LIKE= and ID= lines (example: Centos 6.7 doesn't have
	// them, Centos 7.0 does).
	_isMatch : function (osName, text) {
		var osIdx = text.indexOf(osName);
		if (osIdx == -1)
			return false;

		var lastNewlineIdx = text.lastIndexOf('\n', osIdx - 1);
		if (lastNewlineIdx == -1)
			lastNewlineIdx = 0;
		var sub = text.substring(lastNewlineIdx, osIdx);
		return sub.indexOf('id_like=') == -1;
	},

    type: "ProbePostProcessor"
});

new ProbePostProcessor({

    /**
     * Runs the probe instance
     */
    process: function() {
        // parse the results from our ps output, leaving the result in sensor.running_processes...
        var psop = new PsOutputParser();
        var procs = psop.parse(output);

        try {
            var rpf = this.getParameter("running_process_filter");
            if (rpf) {
				// There's both a Java and JavaScript implementation of process filtering. The Java
				// version is significantly faster so it's preferred and is what's used OOB.
				// The JavaScript version is shipped in case there's a bug in the Java version. If
				// there is a bug in the Java version then chances are good the JavaScript version
				// has the same bug, but the difference is that the JavaScript version can be fixed
				// and tested on a customer instance, providing immediate relief.
				// Switch to the JavaScript version by simply activating the MID script include
				// record named "RunningProcessesFilter".
				// IMPORTANT: Any fixes in this script need to be replicated in the Java code in
				// the RunningProcessFilterEvaluatorModule.
				try {
					var filtering = new RunningProcessesFilter();
					var filterDefs = JSON.parse(rpf);
					procs = filtering.filterResults(procs, filterDefs);
				} catch (e) {
					// Doing this in the catch() makes it seem like this is fallback code when it's really
					// the preferred code path. Unfortunately we don't have a good way to know if someone
					// has activated the JavaScript (the real fallback) unless we try to use it.
					var evaluator = Packages.com.snc.discovery.runningprocess.api.RunningProcessFilterEvaluatorJSProvider.get();
					procs = JSON.parse(evaluator.filter(rpf, procs));
				}
            }
        } catch (e) {
            if (typeof ms != 'undefined')
                ms.log("Unix - Active Processes: Something went wrong while trying to filter processes. Error=" + e);
            else
                // If we don't have access to ms, we are not on the MID
                // Which means we are in classic mode:
                DiscoveryLogger.warn("Unix - Active Processes: Process filtering skipped. Classic mode is not supported.");
        }

        related_data.running_processes = procs;
        related_data.error_msg = psop.error_msg;

        if (this.getParameter('filter_kernel_procs') == 'true')
            psop.filterKernelProcs(related_data.running_processes);
    },

    type: "ProbePostProcessor"
});

new ProbePostProcessor({

	/**
     * Runs the probe instance
     */
	process : function() {
        var lines = output.split("\n");
		for (var i = 0; i < lines.length; i++) {
            var line = lines[i];

            if (line.indexOf("db version ") > -1) {
				var matches = line.match(/db version v([\d\w.]+)/);
				if (matches)
					current.version = matches[1];
            } 
		}
	},

	type: "ProbePostProcessor"
});

new ProbePostProcessor({

    /**
     * Runs the probe instance
     */    
    process : function() {
		var lines = output.split("\n");

		// no results
		if (lines.length == 0)
			return;

		var regexp = new RegExp("version\n([^, ]+)", 'im');
		var match = regexp.exec(output);
		if (match)
			current.version = match[1].trim();			
	},

    type: "ProbePostProcessor"
});

new ProbePostProcessor({

	/**
 	* Runs the probe instance
 	*/
	process : function() {
		related_data.adapters = [];
		// get our adapters and configurations...
		var network_confs = g_array_util.ensureArray(output.Win32_NetworkAdapterConfiguration);
		var network_adapters = g_array_util.ensureArray(output.Win32_NetworkAdapter);
		var network_routes = g_array_util.ensureArray(output.Win32_IP4RouteTable);
		var idutil = new IDSensorUtil();

		// filter routes down for better searching later
		var fixedRoutes = [];
		var seenRoutes = {};

		for (var i=0; i < network_routes.length; i++) {
			var route = network_routes[i];

			if (route.Destination.indexOf('127.') == 0 || route.Destination == '255.255.255.255' ||
				(route.Destination.indexOf('224.') == 0 && route.Mask.indexOf('240.') == 0) ) {
				// ignore broadcast, localhost, and multicast routes
				continue;
			}

			// Only add the route if we haven't seen it yet
			var hashedRoute = this.hashRoute(route);
			if (!seenRoutes[hashedRoute]) {
				seenRoutes[hashedRoute] = true;
				fixedRoutes.push(route);
			}
		}

		var vips = {};
		this.buildVIPHash(vips);

		for(var i = 0; i < network_confs.length; i++) {
			var nc = network_confs[i];
			var na = network_adapters[i];

			if (parseInt(nc.Index) != parseInt(na.Index)) //Just in case Index doesn't match...
				na = this.findCorrectIndex(nc.Index, network_adapters);

			if (JSUtil.nil(nc.IPAddress) || !nc.IPEnabled)
				continue;

			// Filter out loopback adapters
			if (JSUtil.notNil(nc.Caption) && nc.Caption.indexOf("Loopback Adapter") >= 0)
				continue;

			var ipParsed = nc.IPAddress.split(",");
			var subnetParsed = [];
			if (!JSUtil.nil(nc.IPSubnet))
				subnetParsed = nc.IPSubnet.split(",");

			var ips = [];

			for(var j = 0; j < ipParsed.length; j++) {
				// The ip shouldn't be a VIP...
				if (vips[ipParsed[j]])
					continue;

				var ip_address = ipParsed[j];
				var isIPv6 = (ip_address.indexOf(":") > -1);
				if (isIPv6) {
					ip_address = SncIPAddressV6.get(ip_address);
					if (!ip_address)
						continue;
				} else {
					// check if valid nic ip address..
					if (!SncIPAddressV4.get(ip_address) || ip_address == "0.0.0.0" ||
						ip_address == "255.255.255.255" || ip_address == "127.0.0.1")
					continue;
				}

				var ip = {};
				ip.ip_address = ip_address;
				ip.netmask = (j < subnetParsed.length) ? subnetParsed[j] : null;
				ip.ip_version = isIPv6 ? '6' : '4';
				ips.push(ip);
			}

			var adapter = {};
			adapter.name	           = na.NetConnectionID;
			adapter.short_description  = nc.Caption;
			adapter.ip_address         = (ips.length > 0) ? ips[0].ip_address : null;
			adapter.netmask            = (ips.length > 0) ? ips[0].netmask : null;
			adapter.ip_default_gateway = nc.DefaultIPGateway ? nc.DefaultIPGateway.toString() : null;
			adapter.mac_address        = nc.MACAddress ? nc.MACAddress.toString() : null;
			adapter.dhcp_enabled       = (nc.DHCPEnabled.toString() == 1) ? true : false;
			adapter.ip_addresses       = ips;
			adapter.manufacturer 	  = na.Manufacturer;
			adapter.routes             = {ifRoutes: [], gwRoutes: []};

			// handle routes
			for (var j=0; j < adapter.ip_addresses.length; j++) {
				var ip = adapter.ip_addresses[j];

				if (ip.ip_version == '6' || ip.netmask == null)
					continue;

				// search for matching routes
				var cidr = idutil.maskToCidr(ip.netmask);
				var network = new SncIPNetworkV4(ip.ip_address + '/' + cidr);
				for (var k=0; k < fixedRoutes.length; k++) {
					var route = fixedRoutes[k];

					cidr = idutil.maskToCidr(route.Mask);
					var destIpNetwork = route.Destination + '/' + cidr;
					var newRoute = { dest_ip_network: destIpNetwork };
					if (route.NextHop == '0.0.0.0') {
						// possible exit interface route
						if (route.Mask == '255.255.255.255') {
							// host or broadcast exit interface route, will be contained by a subnet route
							continue;
						}
						if (route.Destination == '' + network.address) {
							newRoute.router_interface = adapter.name;
							adapter.routes.ifRoutes.push(newRoute);
						}
					} else {
						// possible next hop route
						var nextHop = new SncIPAddressV4(route.NextHop);
						if (network.contains(nextHop) || network.isBroadcastAddress(nextHop) ) {
							newRoute.route_interface = adapter.name;
							newRoute.next_hop_ip_address = String(nextHop);
							adapter.routes.gwRoutes.push(newRoute);
						}
					}
				}

			}

			related_data.adapters.push(adapter);

			//if this nic has a default gateway configured, set the default_gateway for the computer CI:
			if(JSUtil.notNil(adapter.ip_default_gateway))
				related_data.ip_default_gateway = adapter.ip_default_gateway;
		}
	},

	/*************************************************************
 	* Function definitions
 	*************************************************************/

	buildVIPHash: function(vips) {
		// if we have a VIPs parameter, parse it and build a hashmap of them...
		var vipsParam = g_probe.getParameter('vips');
		if (vipsParam) {
			var vipsParamParts = vipsParam.split(',');
			for (var i = 0; i < vipsParamParts.length; i++)
				vips[vipsParamParts[i]] = true;
		}
	},

	/*
 	* Win32_NetworkAdapter and Win32_NetworkAdapterConfiguration seem to always have the same
 	* length of array data and the order matches. But just in case it doesn't, we need to
 	* manually match them up by index.
 	*/
	findCorrectIndex: function(index, network_adapters) {
		for(var i=0; i<network_adapters.length; i++)
			if (parseInt(network_adapters[i].Index) == parseInt(index))
			return network_adapters[i];
	},

	// Routes are unique based on Destination, Mask, and NextHop
	hashRoute: function(route) {
		var routeHash = "";
		routeHash += "D:" + route.Destination;
		routeHash += "M:" + route.Mask;
		routeHash += "NH:" + route.NextHop;

		return routeHash;
	},

	type: "ProbePostProcessor"
});

new ProbePostProcessor({

	/**
	 * Runs the probe instance
	 */
	process : function() { 
		if (!(g_probe.haveWarning() || g_probe.haveError()) && !output.contains("System Information")) {
			g_probe.setError("Unable to parse output of dmidecode: " + output);
			related_data.warn = "Unable to parse output of dmidecode: " + output;
			return;
		}
		var modules = [];
		var handleRegex = /.*DMI type ([0-9]+).*/;
		related_data.modules = modules; 

		/**
		 *  Output for dmidecode will be in this format:
		 *    Handle 0x0107, DMI type 3, 6 bytes
		 *    Serial Number: VMware-42 08 4b d3 41 9e eb e3-5e fd 7e 7b 42 42 c0 66;
		 *    UUID: 42084BD3-419E-EBE3-5EFD-7E7B4242C066
		 *
		 *    Handle 0x0105, DMI type 23, 13 bytes
		 *    ...
		 *
		 *  The following code splits the output into blocks (around "Handle 0x").
		 *  A bit of optimization is done based on the fact that we only care about
		 *  DMI type 17.
		 */
		var blocks = output.split(/Handle\s0x/);
		for(var i = 0; i < blocks.length; i++) {
			var block = blocks[i];
			var mat = handleRegex.exec(block);
			if (mat) {
				currentBlock = parseInt(mat[1]);
				// we're only interested in block 17...
				if (currentBlock != 17)
					continue;
			}

			var lines = block.split(/\n/);

			for (var j = 0; j < lines.length; j++) {
				var line = lines[j];

				if (line == "Memory Device") {
					var module = this.parseModule(lines, j + 1);

					if (module != null)
						modules.push(module);
				}
			}
		}
	},

	parseModule: function(lines, start) {
        var module = {};

        for (var i = start; i < lines.length; i++) {
            var line = lines[i];

            if (line == "")
                return module;

            var parts = line.split(":");
            var name = parts[0].trim();
            var value = parts[1].trim();

            if (name == "Total Width")
                module.total_width = value.split(" ")[0];
            else if (name == "Data Width")
                module.data_width = value.split(" ")[0];
            else if (name == "Size") {
                if (value == "No Module Installed")
                    return null;
                var sizeComponents = value.split(" ");
                var unit = sizeComponents[1];
                if (unit && unit == "GB")
                    module.capacity = sizeComponents[0] * 1024;
                else
                    module.capacity = sizeComponents[0];
            } else if (name == "Form Factor")
                module.form_factor = (value.startsWith("FB-")) ? value.split("-")[1] : value;
            else if (name == "Locator") {
                module.device_locator = value;
                module.name = value;
            } else if (name == "Bank Locator")
                module.bank_label = (value == "Not Specified") ? null : value;
            else if (name == "Type")
                module.type = value.split(" ")[0];
            else if (name == "Type Detail")
                module.type_detail = value;
            else if (name == "Speed")
                module.speed = (value == "Unknown") ? null : value.split(" ")[0];
            else if (name == "Serial Number")
                module.serial_number = value;
            else if (name == "Part Number")
                module.part_number = value;
            else if (name == "Manufacturer")
                module.manufacturer = value;
            else if (name =="Product Name")
                module.model_id = value;
        }

        return module;
    },

    type: "ProbePostProcessor"
});

new ProbePostProcessor({

	/**
	 * Runs the probe instance
	 */
	process : function() {
		if (!(g_probe.haveWarning() || g_probe.haveError()) && !output.contains("System Information")) {
			g_probe.setError("Unable to parse output of dmidecode: " + output);
			related_data.warn = "Unable to parse output of dmidecode: " + output;
			return;
		}

		related_data.tDataObj = {};

		// key (property name) is currentBlock + ':' + field name...
		var matchMap = {
			'1:Manufacturer'  : 'manufacturer',
			'1:Product Name'  : 'model_id',
			'1:Version'       : 'model_number',
			'1:Serial Number' : 'system_serial_number',
			'1:UUID'          : 'uuid_serial',
			'2:Serial Number' : 'baseboard_serial',
			'3:Serial Number' : 'chassis_serial'
		};
		var handleRegex = /.*DMI type ([0-9]+).*/;
		var valueRegex = /^\s*([^:]*)\s*:\s*(.*?)\s*$/;
		var currentBlock = -1;

		/**
		 *  Output for dmidecode will be in this format:
		 *    Handle 0x0107, DMI type 3, 6 bytes
		 *    Serial Number: VMware-42 08 4b d3 41 9e eb e3-5e fd 7e 7b 42 42 c0 66;
		 *    UUID: 42084BD3-419E-EBE3-5EFD-7E7B4242C066
		 *
		 *    Handle 0x0105, DMI type 23, 13 bytes
		 *    ...
		 *
		 *  The following code splits the output into blocks (around "Handle 0x").
		 *  A bit of optimization is done based on the fact that we only care about
		 *  DMI types between 1~3.
		 *  Skip processing on blocks with types that are not in this range.
		 *  If the type is between 1~3, we grab the field names that we care about and
		 *  save those in tDataObj.
		 */
		var blocks = output.split(/Handle\s0x/);
		for(var i = 0; i < blocks.length; i++) {
			var block = blocks[i];
			var mat = handleRegex.exec(block);
			if (mat) {
				currentBlock = parseInt(mat[1]);
				// we're only interested in blocks 1 through 3...
				if (currentBlock < 1 || currentBlock > 3)
					continue;
			}

			var values = block.split(/\n/);
			for (var j = 0; j < values.length; j++) {
				var value = values[j].trim();
				mat = value.match(valueRegex);
				if (!mat)
					continue;

				var fieldName = mat[1];
				var fieldValue = mat[2];
				var curName = matchMap['' + currentBlock + ':' + fieldName];
				if (curName == null)
					continue;

				// don't overwrite data we already have, as sometimes there are duplicate blocks with no data...
				if (related_data.tDataObj[curName] == null)
					related_data.tDataObj[curName] = fieldValue;
			}
		}
	}
});

