		var provider = new Packages.com.snc.sw.providers.wmi.WMIProviderImpl();

		try {
			provider.setHost(host);
			if (Win32_osName.indexOf('Windows 2000') != -1) {
				current.processes = provider.executeCommand('netstat.exe -anp TCP');
