var cpUtils = new global.CPIARUtils();
var listItems = new global.AutoResolutionLanguageChoiceListBuilder().getSupportedLanguagesForUIB();
var selectedItems = cpUtils.getPredictionFieldSelectedList(listItems, current.getValue("training_language"));
answer = JSON.stringify({
    placeholderText: gs.getMessage("Select language"),
    label:  gs.getMessage("Training language"),
	listItems: listItems,
	selectedItems: selectedItems,
	required: true
});

var cpUtils = new global.CPIARUtils();
var checkIntentListExists = cpUtils.checkIntentListExists(current.getValue("sys_id"));

answer = JSON.stringify({
    sysId: current.getValue('sys_id'),
	intentListExists: checkIntentListExists
});

//List of Guids generated to create new stages.
var stagePoolArray = [];
for (var j = 0; j < 10; j++)
    stagePoolArray.push(gs.generateGUID());

var stagesGr = new GlideRecord('sc_service_fulfillment_stage');
stagesGr.addQuery('cat_item', current.sys_id);
stagesGr.orderBy('order');
stagesGr.query();

var stagesObj = {};
var stageIds = [];
var fieldIsInvalid = false;
while (stagesGr.next()) {
    var stage = {
        sysId: stagesGr.getUniqueValue(),
        name: stagesGr.getValue('name'),
        order: stagesGr.getValue('order')
    };
    stagesObj[stage.sysId] = stage;
    stageIds.push(stage.sysId);
}

if (stageIds.length > 0) {
    var stepsGr = new GlideRecord('sc_service_fulfillment_step');
    stepsGr.addQuery('service_fulfillment_stage', 'IN', stageIds.join());
    stepsGr.query();

    while (stepsGr.next()) {
        var step = {
            sysId: stepsGr.getUniqueValue(),
            label: stepsGr.getDisplayValue('title') || '',
            action: stepsGr.getValue('service_fulfillment_step_configuration'),
            stage: stepsGr.getValue('service_fulfillment_stage')
        };

		if (!step.action)
			fieldIsInvalid = true;

        if (!stagesObj[step.stage].steps)
            stagesObj[step.stage].steps = [];

        stagesObj[step.stage].steps.push(step);
    }
}

var orderedStageObjs = [];
for (var i = 0; i < stageIds.length; i++) {
    if (!stagesObj.hasOwnProperty(stageIds[i]))
        continue;

    var stageObj = stagesObj[stageIds[i]];
    if (!Array.isArray(stageObj.steps) || stageObj.steps.length == 0)
        continue;

    orderedStageObjs.push(stageObj);
}

var output = {
    data: orderedStageObjs,
    stagePool: stagePoolArray,
	isFieldInvalid: fieldIsInvalid
};

answer = JSON.stringify(output);

result.post_insert_script:
var valueStr = producer.fulfillment_steps;
var value;
var stagesArray;
var stageIds = [];

try {
    value = JSON.parse(valueStr);
    stagesArray = value.data;
} catch (e) {
    value = {};
    stagesArray = [];
    gs.debug(e);
}

// Update order for each stage. For each stage get the step Ids and update the step records with proper stage
for (var i = 0; i < stagesArray.length; i++) {
    var stage = stagesArray[i];
    var stepArray = stage.steps;
    if (!stepArray || stepArray.length == 0)
        continue;

    var stageId = !GlideStringUtil.isEligibleSysID(stage.sysId) ? gs.generateGUID() : stage.sysId;

    var stageGr = new GlideRecord('sc_service_fulfillment_stage');
    stageGr.addQuery('sys_id', stageId);
    stageGr.query();
    if (!stageGr.next()) {
        stageGr.initialize();
        stageGr.setNewGuidValue(stageId);
    }
    stageGr.setValue('name', stage.name || '');
    stageGr.setValue('order', stage.order || 0);
    stageGr.setValue('cat_item', current.getUniqueValue());
    stageGr.insertOrUpdate('sys_id');

    var stepIds = [];
    for (var j = 0; j < stepArray.length; j++)
        if (GlideStringUtil.isEligibleSysID(stepArray[j].sysId))
            stepIds.push(stepArray[j].sysId);

    if (stepIds.length > 0) {
        var stepGr = new GlideRecord('sc_service_fulfillment_step');
        stepGr.addQuery('sys_id', 'IN', stepIds.join());
        stepGr.query();
        stepGr.setValue('service_fulfillment_stage', stageId);
        stepGr.updateMultiple();

        stageIds.push(stageId);
    }
}

// Delete nodes removed on UI
var gr = new GlideRecord('sc_service_fulfillment_stage');
gr.addQuery('sys_id', 'NOT IN', stageIds.join());
gr.addQuery('cat_item', current.getUniqueValue());
gr.deleteMultiple();

