result.advanced_condition:
// Set the variable 'answer' to true to indicate that this risk condition matches, and false if it does not match the change request
//
//   answer = false;
//   if (current.category == 'Network')
//      answer = true;

var stateValue = parseInt(current.getValue('state'));

// State is one of New, Assess or Authorize
if (stateValue === -5 || stateValue === -4 || stateValue === -3) {
	var ciu = new CIUtils();
	var services = ciu.servicesAffectedByTask(current);

	var critServices = new GlideRecord("cmdb_ci_service");
	critServices.addQuery("sys_id", services);
	critServices.addQuery("busines_criticality", "1 - most critical");
	critServices.query();
	answer = critServices.hasNext(); // return true if critical service affected
} else
	answer = false;

result.script_values:
if (serv.busines_criticality == "1 - most critical") {
	current.impact = 1;
	current.risk = 2;
}
if (serv.busines_criticality == "2 - somewhat critical") {
	current.impact = 2;
	current.risk = 3;
}

result.advanced_condition:
var stateValue = parseInt(current.getValue('state'));

// State is one of New, Assess or Authorize
if ((stateValue === -5 || stateValue === -4 || stateValue === -3) && current.cmdb_ci.sys_class_name == 'cmdb_ci_service') {
	var serv = new GlideRecord('cmdb_ci_service');
	if (serv.get(current.cmdb_ci) && (serv.busines_criticality == '1 - most critical' || serv.busines_criticality == '2 - somewhat critical'))
		answer = true;
} else
	answer = false;
