result.css:
$sp-space--xxs: 2px !default;
$sp-space--xs: 4px !default;
$sp-space--sm: 8px !default;
$sp-space--md: 12px !default;
$sp-space--lg: 16px !default;
$sp-space--xl: 24px !default;
$sp-space--3xl: 40px !default;
$background-primary : $body-bg !default;
$font-size-md: 16px !default;
$font-size-large: 20px !default;
$font-size-xl: 24px !default;
$brand-primary-darker: darken($brand-primary,16) !default;

#searchBox .btn{
  padding : 0px $sp-space--lg;
}

.dropdown-menu.empty-dropdown.ng-scope,.dropdown-menu.cart-dropdown.custom-drop-down,.dropdown-menu.wishlist-menu.custom-drop-down {
  background-color: transparent !important;
  padding-bottom: 0 !important;
  padding-left: 0 !important;
  padding-right: 0 !important;
}


.navbar-inverse {
  margin-bottom: 0px;
  border-radius: 0px; 
  background-color: $navbar-inverse-bg;
  .navbar-header {
    &.nav-container {
      width: 100%;

      .sub-navbar-overflow-entry-button {
        position: relative;
        float: right;
        margin: $sp-space--md $sp-space--lg $sp-space--md 0;
        padding: $sp-space--sm 1rem;
        background-color: transparent;
        background-image: none;
        border: .1rem solid transparent;
        border-radius: $border-radius-base;
        border-color: rgba(255, 255, 255, .25);

       .label {
         float: right;
         margin-right: -$sp-space--xs;
         margin-top: -$sp-space--sm;
         padding: $sp-space--xxs $sp-space--xs;
       }

       .label-as-badge {
          font-size: $font-size-xs;
          position: relative;
          top: -$sp-space--xxs;
          left: $sp-space--xxs;
          padding: $sp-space--xxs $sp-space--xs;
          margin-left: 0;
          background-color: $btn-danger-bg;
          color: $background-primary;
        }

        .icon-bar {
          display: block;
          width: 2.2rem;
          height: .2rem;
          border-radius: $border-radius-small;
          background-color: $background-primary;
        }
        .icon-bar + .icon-bar {
          margin-top: $sp-space--xs;
        }
      }
    }

    .mobile-view-on-desktop {
    	position: absolute;
      z-index: 999;
      /* 60px is height of the header strip */
      top: 60px;
      right: 0;
      min-height: 500px;
      max-width: 400px;
      background-color: $navbar-inverse-bg;
      box-shadow: 5px 5px 5px $navbar-inverse-link-color;
      color: $navbar-inverse-link-color;

      .mobile-view-navbar {
         list-style: none;
         padding: 0 $sp-space--sm;
         margin: 0;
         display: flex;
         flex-direction: column;
         border-top: 1px solid darken($navbar-inverse-link-color, 45%);

         .main-header-items {
           list-style: none;
           padding: $sp-space--md 0 0 $sp-space--lg;
           margin: 0;
           display: flex;
           flex-direction: column;

           & > li {
             &:not(:last-child) {
               padding-top: $sp-space--lg;
             }
             // Cart, tours, and Wishlist (Mobile View)
             .nav.navbar-nav {
               display: flex;
               flex-direction: column;

               li {
                 padding-top: $sp-space--lg;
               }
             }
           }
         }

         .mobile-mega-menu-entry {
           margin: 0 -$sp-space--sm;
           padding: $sp-space--xl $sp-space--lg;
           background-color: $sp-navbar-divider-color;

           .dropdown-menu {
           	background-color: transparent;
           }
         }
         .separator {
           margin: 20px -$sp-space--sm;
           border-top: 1px solid darken($navbar-inverse-link-color, 30%);
         }

         li > a {
           color: $navbar-inverse-link-color;
           padding: 0;

           &:hover, &:focus {
             color: $navbar-inverse-link-hover-color !important;
             background-color: transparent;
           }
           &:focus {
             box-shadow: inset 0 0 0 0.1rem $navbar-inverse-link-hover-color;
             padding: $sp-space--xs;
             border: 0.5px solid $navbar-inverse-link-color;
             border-radius: $border-radius-base;
           }
         }
        .header-menu-item > a{
        	padding: $sp-space--md $sp-space--sm;
        }
       }

      .impersonate-and-logout {
          div > ul > li:first-child {
            height: 0;

            ul.dropdown-menu {
              width: 400px;
              display: block;
              z-index: inherit;
              background-color: $navbar-inverse-bg;
              box-shadow: none;
              border-radius: 0;
              padding-left: $sp-space--xxs;
              padding-bottom: $sp-space--lg;

              li > a {
                line-height: 2;

                &:hover, &:focus {
                  color: $navbar-inverse-link-hover-color !important;
                  background-color: transparent;
                }
                &:focus {
                  box-shadow: inset 0 0 0 0.1rem $navbar-inverse-link-hover-color;
                  padding: $sp-space--xs;
                  margin-left: $sp-space--lg;
                  border: 0.5px solid $navbar-inverse-link-color;
                  border-radius: $border-radius-base;
                  width: fit-content;
                }
              }
              li:first-child {
                display: none;
              }
           }
         }
       }
     }

    .navbar-brand {
      margin: 0;
      height: 60px;
      padding: $sp-logo-margin-y 0px $sp-space--sm $sp-space--lg;
      img, span {
        margin: 0;
        display: block;
        max-height: $sp-logo-max-height;
        max-width: $sp-logo-max-width;
        position: relative;
        top: 50%;
        -webkit-transform: translateY(-50%);
        -ms-transform: translateY(-50%);
        transform: translateY(-50%);
	padding-right: $sp-space--sm;
      }
    }

    .navbar-right .dropdown-menu > li > a {
      &:hover, &:focus {
        background-color: darken($navbar-inverse-bg, 8%);
      }

      &:focus {
        border: 0.5px solid $navbar-inverse-link-color;
        border-radius: $border-radius-base;
        box-shadow: inset 0 0 0 0.1rem $navbar-inverse-link-hover-color;
      }
    }
  }
  .dropdown-menu > li > a {
    color: $navbar-inverse-link-color;
  }
  .dropdown-toggle:focus {
  	outline: thin dotted;
    outline: 5px auto -webkit-focus-ring-color;
  }
  .sub-navbar {
    background-color: $sp-navbar-divider-color;
    border: none;
    width: 100%;

    #cart-dropdown, #wishlist-menu {
      display: none;
      ~ * {
        display: none !important;
      }
    }
    .navbar-nav > li > a[href="?id=sc_cart"], .navbar-nav > li > a[href="?id=sc_wishlist"] {
      display: none !important;
    }
    .gtd-dropdown-container {
      display: none;
    }
    .open > a, .open > a:focus {
      background-color: darken($sp-navbar-divider-color, 8%);
    }
    #cart-dropdown {
      display: none;
      :after {
        content: "\f0d7";
        vertical-align: baseline;
        width: auto;
        height: auto;
        border: none;
        margin-left: sp-space--xs !important;
        display: inline-block;
        font: normal normal normal $font-size-small/1 FontAwesome;
        font-size: inherit;
        text-rendering: auto;
        -webkit-font-smoothing: antialiased;
        -moz-osx-font-smoothing: grayscale;
      }
    }

    .dropdown-menu {
      background-color: $sp-navbar-divider-color;
      > li > a:hover, > li > a:focus {
        background-color: darken($sp-navbar-divider-color, 8%);
	box-shadow:unset;
      }
    }
  }
  .dropdown-menu {
    background-color: rgba($navbar-inverse-bg, 0.97);
    border: none;
    &.case-menu {
      right: 0;
      left: auto;
      padding: 0;
      width: 450px;
      background: $background-primary;
      overflow-y: hidden;
    }
  }
  > .open > a:hover, > .open > a:focus  {
    background-color: darken($navbar-invese-bg, 8%);
  }
  .fa-caret-down {
    vertical-align: middle;
    margin-top: $sp-space--xxs;
  }
  .caret:before {
    content: "\f0d7";
  }
  .caret {
    vertical-align: baseline;
    width: auto;
    height: auto;
    border: none;
    margin-left: $sp-space--xs !important;
    display: inline-block;
    font: normal normal normal $font-size-small/1 FontAwesome;
    font-size: inherit;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }
  .label-as-badge {
    font-size: $font-size-xs;
    position: relative;
    top: -$sp-space--sm;
    left: -$sp-space--xxs;
    padding: -$sp-space--xxs $sp-space--xs;
    margin-left: 0;
    background-color: $btn-danger-bg;
    color: $background-primary;
  }
  .navbar {
    transition: 250ms opacity ease-in-out; 
    -webkit-transition: 250ms opacity ease-in-out; 
    border: 0;
  }
  .navbar-fade {
    opacity: 0.4;
  }
  .navbar-collapse {
    padding-right: 0px;
  }
  .case-menu {
    div#widget-tabs-2 {
      border: none;
      > .panel-heading {
        border: none;
        border-bottom: 1px solid $border-tertiary;
      }
    }
  }

  ul.nav.navbar-nav {
    // Hiding the loading indicator from Header Menu base system widget
    div.header-loader {
       display: none;
    }
    li.sp-loading-indicator {
       padding-top: $sp-space--xl;
       margin-top: $sp-space--xxs;
    }
  }

  #more-items-dropdown {
    overflow: visible;
  }

  .cart-dropdown, .wishlist-menu, .gtd-dropdown-container .empty-dropdown {
    width: 350px;
    padding: 0;
    z-index: 1000;
    border-top-left-radius: $border-radius-base !important;
    border-top-right-radius: $border-radius-base !important;

    ul {
      max-height: 300px;
      overflow: auto;
    }

    .subtotal {
      display: block;
    }

    .item-image {
      padding: 0;
      margin: 0;
      text-align: center;
      max-width:100%;
      height:auto;
    }
    label, p {
      padding: 0;
      margin: 0;
    }
    .media {
      padding: 0;
      margin: 0;
    }
    .media-left > a {
      display: block;
      width: 48px;
      max-width: 48px;
    }
    .media-body {
      label {
        font-weight: bold;
      }
    }
    li.media {
      border-top: 1px solid #cccccc;
      padding: 1rem;
    }
    .panel {
      margin: 0;
    }
    .panel .panel-body {
      padding: 0;
    }
    .btn-clear {
      color: #333;
      background-color: $background-primary;
    }
    p.quantity-price {
      padding-top: $sp-space--xs;
      span {
        color: $text-muted;
        padding-right: $sp-space--xs;
      }
    }
    .align-right {
      text-align: right;
    }
  }

  .item-added-tooltip, wishlist-item-added-tooltip {
    width: 184px;
    font-size: $font-size-small;
  }

  .gtd-dropdown-container {
    .empty-tour-content {
      text-align: center;
      margin: $sp-space--3xl $sp-space--lg;
    }
    .dropdown-menu li {
      a {
        cursor: pointer;
      }
      min-width: 20em;
    }
    .label-as-badge {
      width: 12px;
      height: 12px;
      display: inline-block;
      padding: 0px;
      padding-top: $sp-space--xxs;
    }
    .badge-container {
      display: inline-block;
      width: 12px;
    }
  }

}
@media screen and (min-width: 768px) {
  .navbar-inverse {
    .navbar-right > .navbar-nav > div {
      float: left;
    }
    .navbar-right {
      margin-right: $sp-space--sm;
      > ul {
        display: -webkit-box;
        display: -moz-box;
        display: box;

        -moz-box-orient: horizontal;
        box-orient: horizontal; 
        -webkit-box-orient: horizontal;
      }
      .navbar-nav > li > a {
        padding-right: $sp-space--md;
        padding-left: $sp-space--md;
      }
      .header-menu-item > a{
        	padding: $sp-space--sm;
        }
    }
    .navbar-avatar {
      margin-top: -$sp-space--xs;
    }
    a#todos_popup {
      padding-left:0px;
    }
    .navbar-nav .open .dropdown-menu > li > a {
      padding: 10px 20px;
    }
    .navbar-header {
      position:relative;
      .dropdown-toggle {
        height: 60px;
      }
      .navbar-nav  > li  > a {
        padding-top: 20px;
        padding-bottom: 20px;
        max-height:50px;
        padding-right: $sp-space--md;
        padding-left: $sp-space--md;
      }
    }
    .temp-mobile {
      display: inline-block;
    }
    .sub-navbar {
      display: flex;
      padding-left: 0;
    }

    .sub-navbar--mobile-view {
      max-height: 0px;
      /* Using !important here as we are overriding bootstrap style */
      overflow-y: hidden !important;
    }
    .navbar-nav  > li  > a {
		padding-top: 20px;
		padding-bottom: 20px;
	}
  }
}

// Search bar styles in the header (Should always be in the center)
.header-search {
   @media screen and (min-width: 768px) {
      position: absolute;
      left: 37%;
      margin: $sp-space--md 0;
      width: 20%;
   }

   @media (min-width : 320px) and (max-width : 767px) {
      margin-top: 0 !important;
      padding-bottom: 10px;
      float: right;
      width: 100%;
   }

   @media (min-width : 768px) and (max-width: 867px) {
      left: 23% !important;
   }

   @media (min-width : 868px) and (max-width: 1024px) {
      left: 27% !important;
   }

   @media (min-width : 1025px) and (max-width: 1200px) {
      left: 30% !important;
   }

   @media (min-width : 1201px) and (max-width: 1400px) {
      left: 33% !important;
   }
}

@media (min-width : 320px) and (max-width : 767px) {
  .navbar-inverse {
    .dropdown-menu.case-menu {
      width: 100%;
    }
    .navbar-toggle {
      border-color: rgba(255, 255, 255, .25);
    }
    .navbar-toggle:focus {
      -webkit-box-shadow: 0rem 0rem 0.5rem #1f8476 
    }
    .navbar-nav {
      display: block;
      margin: 0;

      .open .dropdown-menu > li > a {
        padding: 5px 30px;
      }
    }
    .sub-navbar {
      padding: 0px 0px;
    }
    .upper-collapse {
      width: 100%;
      padding-bottom: 0px;
    }
    .navbar-collapse {
      padding-top: 0px;
      padding-left: 0px;
      overflow: hidden;
      ul {
        margin: 0;
      }
    }
    .navbar-header.nav-container {
      .sub-navbar-overflow-entry-button {
        float: none;
        position: absolute;
        right: 0;
      }
      .mobile-view-on-desktop {
        min-width: 100%;
        // max-width: 100%; is needed for 400% zoom-level in WCAG resolution
        max-width: 100%;
        top: 60px;
        box-shadow: none;

        .impersonate-and-logout {
           div > ul > li:first-child {
             height: auto;
           }
         }
      }
      .mobile-view-on-desktop.search-visible {
         top: 53px;
      }

      > ul {
        display: inline-block;
        padding: 10px 10px 0 0;
        float: right;
        margin: 0;
      }
      > li {
        display: inline-block; 
      }
    }
    .height-adjust {
      padding: $sp-space--sm 0px;
      margin-bottom: $sp-space--md;
    }
    .popup-edit {
      background: $background-primary;
      position: fixed;
      top:0; 
      height:100%;
      margin-top: 0;
      max-height:100%;
      width:100%
    }
    .navbar-collapse-label {
      float: right;
      margin-right: -5px;
      margin-top: -7px;
    }
    button > span.label {
      padding: .2em .5em .2em;
    }
  }
  .nav > li > a {
  	padding-left: 0px;
  }
}
.navbar-right{
  .nav{
    .dropdown{
      .tooltip{
        .tooltip-inner{
        	word-wrap:unset;
        }
      }
    }
  }
}

/* Header search */
input[name="q"]::placeholder {
  font-size: $font-size-md !important;
}

input[name="q"],.search-combobox--header .input-container__input{
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.desktop-headersearch-wrapper {
  display:flex;
  justify-content: end;
  padding: $sp-space--md 0px $sp-space--md $sp-space--md;

  .desktop-headersearch-icon {
    padding: $sp-space--sm $sp-space--sm $sp-space--sm $sp-space--sm;
    color: $navbar-inverse-link-color;
    float: right;
    &:hover {
      color: $navbar-inverse-link-hover-color;
    } 
    &:focus {
      box-shadow: inset 0 0 0 0.1rem $navbar-inverse-link-hover-color;
      border: 0.5px solid $navbar-inverse-link-color;
      border-radius: $border-radius-base;
    }
  }
  .hide-search {
    width: 80px;
    transition: width 0.5s ease-in-out;
  }
  .visibility-collapse {
    visibility: collapse;
  }
  .show-search {
    width: 400px !important;
    transition: width 0.5s ease-in-out;
    visibility: visible;
  }
}
.mobile-header-search-icon-wrapper {
  float: none;
  position: absolute;
  right: 60px;
  padding: $sp-space--md;

  .mobile-header-search-icon{
    color: $navbar-inverse-link-color;
    padding: $sp-space--xs;
    &:hover {
      color: $navbar-inverse-link-hover-color;
    }
  }
}
.mobile-header-search {
  display: flex;
  height: 6rem;
  padding: $sp-space--md $sp-space--lg 0px $sp-space--lg;

  .mobile-header-search-back {
    font-size: $font-size-large;
    padding: $sp-space--xs;
    margin: 0px $sp-space--md $sp-space--lg 0px;
    align-self: center;
    .fa {
    	color: $navbar-inverse-link-color;
       	&:hover {
      		color: $navbar-inverse-link-hover-color;
    	}
    }
  }
  .full-width {
    width: 100%;
  }
}

/* Mobile menu modal */
#mobile-menu-items-modal{
  color: $navbar-inverse-link-color;
  position: fixed;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  z-index: 25;
  overflow: auto;
  background-color: $brand-primary-darker;

  a {
  	color: $navbar-inverse-link-color;
  }
  li {
    padding-left: $sp-space--md;
    .fa {
    	font-size: $font-size-xl;
      margin-right: $sp-space--md;
    }
  }
  li > a:focus {
    box-shadow: inset 0 0 0 0.1rem $navbar-inverse-link-hover-color;
    padding: $sp-space--xs;
    border: 0.5px solid $navbar-inverse-link-color;
    border-radius: $border-radius-base;
  }
  .dropdown-menu {
    background-color: transparent;
  }
  .mobile-menu-modal-list {
    list-style-type: none;
    padding: $sp-space--sm;
  }
  .user-profile-mobile {
    font-size: $font-size-large;
    font-weight: 600;
  }
  .line-separator-categories {
    margin-top: 0px;
    margin-bottom: $sp-space--lg;
    width: 100%;
    text-align: left;
    margin-left: 0;
  }
  .tree-mobile {
    display: flex;
    margin: 0 $sp-space--lg $sp-space--lg $sp-space--lg;
  }
  .result-heading-mobile {
    display: flex;
    align-items: center;
    font-style: normal;
    font-weight: 600;
    font-size: $font-size-md;
    line-height: 23px;
    gap: $sp-space--md;
    padding: $sp-space--md $sp-space--lg;
  }
  .close-modal-button {
    display: flex;
    justify-content: right;
    margin-right: $sp-space--lg;
    margin-top: $sp-space--lg;

    path {
    	fill: $navbar-inverse-link-color;
    }
  }
  .mobile-list-item {
    margin: $sp-space--xs 0px;
    padding: $sp-space--md;
  }
  .avatar-container .avatar.soloAvatar .sub-avatar {
    font-size: $font-size-large;
  }
  .user-profile-menu-item {
    font-size: $font-size-large;
    padding: $sp-space--md;
    margin: $sp-space--xs 0px;
  }
  .mobile-menu-icon {
    font-size: $font-size-xl;
    vertical-align: sub;
  }
  .guest-avatar {
    height: 42px;
    width: 42px;
    border-radius: 50%;
    text-align: center;
    display: inline-block;
    vertical-align: middle;
    border-color: $border;
    background-color: $background-tertiary;
  }
  .guest-avatar-icon {
    color: $brand-primary;
    font-size: $font-size-xl;
    vertical-align: -webkit-baseline-middle;
    margin: 0px !important;
  }
  .show-more-item{
  	margin-bottom: $sp-space--lg;
	}
  hr {
  	border-color: $brand-primary;
  }
  .navbar-avatar {
    margin-right: $sp-space--md !important;
  }
}

result.link:
function pspHeaderLink(scope, element, attrs, controller) {
    $(element).on('click', 'a.navbar-brand', function(e) {
        scope.expanded = false;
    });
    $(element).on('click.nav', '.navbar-collapse.in', function(e) {
        var target = $(e.target);
        if (target.is('a') && !target.hasClass("dropdown-toggle") && target.siblings('.dropdown-menu').length == 0) {
            // collapse mobile nav if clicked an anchor that isn't a dropdown toggle
            scope.expanded = false;
            return;
        }

        // if neither target nor parent is an anchor or the parent anchor is a dropdown-toggle, do nothing
        if (!target.parent().is('a') || target.parent().hasClass("dropdown-toggle") || target.parent().siblings('.dropdown-menu').length > 0)
            return;

        // collapse mobile nav if target is part of a submenu
        if (target.parents("ul.dropdown-menu").length > 0) {
            scope.expanded = false;
            return;
        }

        // collapse mobile nav is target is part of the dropdown
        if (target.parents("div.navbar-collapse").length > 0)
            scope.expanded = false;
    });
    $(element).on('keydown', '.dropdown-menu > .header-menu-item', function(e) {
        var target = e.target;
        if (target.localName === 'a') {
            if (e.keyCode === 37 || e.keyCode === 39) {
                e.preventDefault();
                $(target).parents('li').removeClass('open');
                $(target).parents('li').find('a').focus();
            } else if (e.keyCode === 9) {
                $(target).parents('li').removeClass('open');
            }
        }
    });

    $('body').click(function(event) {
        if ($(event.target).text() !== controller.toursTranslation && !$(event.target).is("[aria-expanded]") && scope.isMobileViewOnDesktopExpanded)
            scope.toggleMobileMenuVisibility();
    });

    // Closing mobile navigation menu after hitting Tab key on Logout option to focus on next DOM element on the page.
    $(document).on('keydown', 'ul > li.logout > a[href*="logout"]', function(keyDownEvent) {
        var TAB_KEY_VALUE = 'Tab';

        if (keyDownEvent.key === TAB_KEY_VALUE && scope.mobileDevice) {
            scope.$applyAsync(function() {
                scope.toggleMobileMenuVisibility();
            });
        }
    });
    $(document).on('keydown', 'ul > li.login', function(keyDownEvent) {
        var TAB_KEY_VALUE = 'Tab';

        if (keyDownEvent.key === TAB_KEY_VALUE && scope.mobileDevice) {
            scope.$applyAsync(function() {
                scope.toggleMobileMenuVisibility();
            });
        }
    });
    // Helper function to handle the arrow key navigation in the header
    function handleKeyPressEvents(e) {
        var target = e.target;
        var querySelector;
        if ($(this).hasClass('main-header-item'))
            querySelector = 'li.main-header-item';
        else if ($(this).hasClass('gt-menu-item'))
            querySelector = 'li.gt-menu-item';

        if (target.localName === 'a') {
            var currentMenuItem = $(target).parents('li');
            var parentUL = currentMenuItem.parents('ul');
            var allMenuItems = parentUL.children(querySelector);
            var firstMenuItem = allMenuItems.first();
            var lastMenuItem = allMenuItems.last();
            var menuItemToFocus;

            // Only Side arrow keys should work for gt-menu-item and only vertical arrow keys should work for main-header-item
            if (querySelector === 'li.gt-menu-item' && !(e.keyCode === 37 || e.keyCode === 39 || e.keyCode === 9))
                return;
            else if (querySelector === 'li.main-header-item' && !(e.keyCode === 38 || e.keyCode === 40))
                return;

            if (querySelector === 'li.gt-menu-item') {
                if (e.keyCode === 9) {
                    $(target).parents('li').removeClass('open');
                    return;
                } else {
                    e.preventDefault();
                    currentMenuItem.removeClass('open');
                }
            } else
                e.preventDefault();

            if (e.keyCode === 37 || e.keyCode === 38) {
                var previousMenuItem = currentMenuItem.prevAll(querySelector);
                currentMenuItem.prevAll(querySelector).first();
                //enable circular navigation
                menuItemToFocus = previousMenuItem.length ? previousMenuItem : lastMenuItem;
                menuItemToFocus.find('a').focus();
            } else if (e.keyCode === 39 || e.keyCode === 40) {
                var nextMenuItem = currentMenuItem.nextAll(querySelector).first();
                //enable circular navigation
                menuItemToFocus = nextMenuItem.length ? nextMenuItem : firstMenuItem;
                menuItemToFocus.find('a').focus();
            }
        }
    }

    // Handler to focus the first element of More Items dropdown on pressing Space/Enter/Down Arrow key.
    $(element).on('keydown', '#more-items-button', function(e) {
        if (e.keyCode === 32 || e.keyCode === 13 || e.keyCode === 40) {
            e.preventDefault();
            var target = e.target;
            var moreItemsDropdown = '#more-items-dropdown';
            $(target).click();
            $(moreItemsDropdown).find('li.header-menu-item a:first').focus();
        }
    });

    // Capturing the arrow key navigation in mega menu for both desktop and mobile view  
    $(element).on('keydown', '.gt-menu-item, .main-header-item', handleKeyPressEvents);

    var rateLimitedCallback = debounce(function() {
        scope.$apply(function() {
            // update current scope based on the new innerWidth and let angular update the view.
            scope.mobileDevice = controller.data.isMobile || (window.innerWidth <= controller.MOBILE_DEVICE_SCREEN_WIDTH);
        });
    }, 250);

    $(window).resize(rateLimitedCallback);

    // This helper returns a function, that, as long as it continues to be invoked, will not
    // be triggered. The function will be called after it stops being called for
    // N milliseconds. If `immediate` is passed, trigger the function on the
    // leading edge, instead of the trailing.
    function debounce(func, wait, immediate) {
        var timeout;
        return function() {
            var context = this,
                args = arguments;
            var later = function() {
                timeout = null;
                if (!immediate) func.apply(context, args);
            };
            var callNow = immediate && !timeout;
            clearTimeout(timeout);
            timeout = setTimeout(later, wait);
            if (callNow) func.apply(context, args);
        };
    }
}

