$footer-main-title-font-size: $font-size-md !default;
$footer-main-link-font-size: $font-size-small !default;
$footer-bottom-font-size: ceil(($font-size-base * 0.75)); !default; // $font-size-xs
$sp-space--xxl: 32px !default;

.scrub-footer {
  .sf-main {
    background: $sp-navbar-divider-color;
    display: flex;
    color: $navbar-inverse-link-color;
    margin-top: $sp-space--xxl;
    border-top: 1px solid darken($sp-navbar-divider-color, 15%);

    /* Logo section */
    .sf-logo-container {
      width: 277px;
      padding: 2rem 0;
      .sf-logo {
        max-width: 100%;
        padding: 1rem 1.4rem;
        display: block;
      }
      .sf-logo-content {
        padding: 1rem 1.4rem;
        word-break: break-word;
        font-size: 1.2rem;
      }
    }

    /* Group link section */
    .sf-links-container {
      flex: 1;
      padding-top: 3.4rem;
      .sf-group {
        width: 22rem;
        padding: 0 1.4rem 3.2rem;
        .sf-group-title {
          color: $navbar-inverse-link-color;
          font-size: $footer-main-title-font-size;
          font-weight: bold;
          padding-bottom: 1.6rem;
        }
        .sf-link {
          padding-bottom: 1.6rem;
          a {
            display: inline-block;
            color: $navbar-inverse-link-color;
            opacity: 0.7;
            font-size: $footer-main-link-font-size;
            word-break: break-word;
            &:hover {
              color: $navbar-inverse-link-hover-color;
            }
            &:focus {
              outline: 2px solid $navbar-inverse-link-color;
              outline-offset: 5px;
              box-shadow: none;
            }
          }
        }
      }
    }
  }

  /* Footer bottom section */
  .sf-bottom {
    background: $navbar-inverse-bg;
    color: $navbar-inverse-link-color;
    font-size: $footer-bottom-font-size;
    display: flex;
    border-top: 1px solid darken($navbar-inverse-bg, 15%);
    .sf-bottom-left {
      flex: 1;
      .sf-bottom-link a {
        color: $navbar-inverse-link-color;
        font-size: $footer-bottom-font-size;
        &:hover {
          color: $footer-link-hover-color;
        }
        &:focus {
          outline: 2px solid $navbar-inverse-link-color;
          outline-offset: 5px;
          box-shadow: none;
        }
      }
    }
    .sf-bottom-right {
      display: flex;
      .sf-social-icons {
        padding: 27px 0;
        .sf-social-icon {
          color: $navbar-inverse-link-color;
          padding: 0;
          margin: 0 0.8rem;
          font-size: $font-size-large;
          &:focus {
            outline: 2px solid $navbar-inverse-link-color;
            outline-offset: 5px;
            box-shadow: none;
          }
        }
      }
    }
  }

  .sf-bottom-link,
  .sf-copyright {
    display: inline-block;
    padding: 27px 11.5px;
  }
  .inline-block {
    display: inline-block;
  }
}

/* Masonry layout */
.masonry-root {
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
}

 /* IE 10-11 specific Class to override */
@media screen and (-ms-high-contrast: active),
  (-ms-high-contrast: none) {
  .masonry-root {
    max-height: 100% !important;
    .masonry-cell {
      display: -ms-inline-flexbox;
      flex-direction: column;
      -ms-flex-wrap: wrap;

      .sf-group-title {
        width: 100%;
      }
    }

    &:after {
      content: "";
      clear: both;
      display: table;
    }
  }
}

@media (max-width: 850px) {
  .scrub-footer {
    .sf-main .sf-links-container .sf-group {
      width: 15rem;
      padding-bottom: 22px;
    }

    .sf-bottom {
      padding: 18px 11px;
      .sf-bottom-link {
        padding: 0 8px 11px 8px;
      }
      .sf-bottom-right .sf-social-icons {
        flex: 1;
        padding: 0;
        .sf-social-icon {
          margin: 0 8px 0.8rem 0.8rem;
        }
      }
      .sf-bottom-left {
        display: block;
      }
      .sf-copyright {
        padding: 0 18px;
      }
    }
    .sf-links-container {
      padding-top: 3.4rem;
    }
    .sf-separator {
      flex: 0;
    }
  }
}


@media (max-width: 430px) {
  .scrub-footer {
    .sf-main, .sf-bottom {
      display: block;
    }
    .sf-main {
      flex-direction: column;
      .sf-links-container {
        padding-top: 0;
        .sf-group {
          width: 15rem;
       }
      }
    }
  }
}

