  padding: 19px 10px;
}

.more-menu-position {
  position: relative;
}

.navbar-inverse {
  margin-bottom: 0px;
  border-radius: 0px;
  background-color: $navbar-inverse-bg;
  position: relative;

  .navbar-header {
    &.nav-container {
      width: 100%;
      display: flex;
      flex-wrap: wrap;
      justify-content: end;

      .sub-navbar-overflow-entry-button {
        position: relative;
        float: right;
        margin: 1.3rem 1.5rem 1.3rem 0;
        padding: .9rem 1rem;
        background-color: transparent;
        background-image: none;
        border: .1rem solid transparent;
        border-radius: .4rem;
        border-color: rgba($navbar-inverse-link-color, .25);

        .label {
          float: right;
          margin-right: -.5rem;
          margin-top: -.7rem;
          padding: .2em .5em .2em;
        }

        .label-as-badge {
          font-size: 1.2rem;
          position: relative;
          top: -$sp-space--xxs;
