{"type":"FlowTopic","type":"FlowTopic","triggers":[{"type":"Trigger","name":"6b02871936944333b2397995b6956690","triggerType":"request","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"},"id":"6b02871936944333b2397995b6956690"}],"goals":[{"name":"primary","trigger_id":"6b02871936944333b2397995b6956690","nodes":[{"type":"StartGoal","type":"StartGoal","goal_id":"primary","greeting_msg":{"type":"String","type":"String","mode":"script","value":"(function execute() {
    vaVars.global_keyword_topic_finished = false;
    return '';
})()
"},"id":"53f5d329260941b38b724f3c7a62a36f","name":"Start","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"}},{"type":"TerminateGoal","type":"TerminateGoal","goal_id":"primary","confirmation_msg":{"type":"String","type":"String","mode":"script","value":"(function execute() {
    vaVars.global_keyword_topic_finished = true;
    return '';
})()
"},"id":"032d4cd502584f5faa7900a42b5491f8","name":"End","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"}},{"type":"ScriptedAction","type":"ScriptedAction","script":"(function execute() {
    var recipient = new GlideRecord(vaVars.global_notifications_subscription_user_table);
    recipient.get('sys_id', vaVars.global_notifications_subscription_user);

    // Store the platform-level global user notification preference
    var prefs = new sn_notification.Preferences(recipient);
    vaVars._is_user_subscribed_at_platform = prefs.getUserGlobalPreference();

    var providerApp = JSON.parse(sn_cs.VASystemObject.getProviderAppById(vaContext.providerAppId));
    var provider = JSON.parse(sn_cs.VASystemObject.getProviderById(providerApp.providerId));

    var cschannel = new GlideRecord('sys_cs_channel');
    cschannel.get('sys_id', provider.channelId);
    vaVars._notifications_enabled = (cschannel.getValue('enable_notification') === '1') ? true : false;

    var channelname = cschannel.getValue('name');
    if(channelname.toLowerCase().includes('teams'))
       vaVars._channel_name = 'Microsoft Teams';
    else
       vaVars._channel_name = channelname;

    var mapping = new GlideRecord('sys_cs_notification_channel_configuration');
    mapping.get('provider', providerApp.providerId); 

    var channel = new GlideRecord('sys_notification_channel');
    channel.get('sys_id', mapping.getValue('notification_channel'))

    gs.info('channel: ' + channel.getValue('name')); 
    var result = vaSystem.preProcessNotificationSubscription(vaVars.global_notifications_subscription_provider_user_id, vaVars.global_notifications_subscription_channel_id);
    var dests = prefs.getDestinationsByChannel(channel);
    if (dests.length == 0) {
        gs.error('destination preference does not exist');
        vaVars._is_user_subscribed_at_destination = false;
    } else {
        vaVars._is_user_subscribed_at_destination = dests[0].isActive();
    }
    if(result) vaSystem.postProcessNotificationSubscription();
})()
