})()
"},"formatType":"Text","validationScript":""},{"type":"ScriptedAction","type":"ScriptedAction","script":"(function execute() {
    if (!gs.nil(vaVars.global_task_number)) {
        vaInputs.task_number = vaVars.global_task_number;
        vaVars.global_task_number = "";
    }
    var task = sn_oe_sfs.IARFlowFailureDiagnostic.findTaskInContextTable(vaInputs.task_number);
    if (!gs.nil(task)) {
        vaVars.task_number_found = true;
        vaVars.context_sys_id = task["context_sys_id"];
        vaVars.task_sys_id = task["task_sys_id"];
        vaVars.task_table_name = task["task_table_name"];
        vaVars.duplicate_task_in_context = task["duplicate_task_in_context"] === "true" ? true : false;
    }
})()
