          "script": "(function execute() {\n     var actionId = vaVars.actionId;\n    if (actionId && actionId != '') {\n        var notification_content = JSON.parse(vaInputs.notification_content);\n        var gr = new GlideRecord('sys_cs_notification');\n        gr.get(notification_content.vaNotificationId);\n        gr.setValue('state', 'completed');\n        gr.setValue('conversation', vaSystem.getConversationId());\n        gr.update();\n        var actions = notification_content.actions;\n        var selectedActionTopic = '';\n        var selectedAcknowledgementMessage = '';\n        for (var i = 0; i < actions.length; i++) {\n            if(actionId === actions[i].actionId) {\n                selectedActionTopic = actions[i].topicId ? actions[i].topicId: '';\n                selectedAcknowledgementMessage = actions[i].acknowledgementMessage;\n                break;\n            }\n        }\n        //if va action then switch topic.\n        if(selectedActionTopic != '') {\n            var table_name = notification_content.tableName;\n            var sys_id = notification_content.recordSysId;\n            if (notification_content.recordAccessible && (table_name == null || sys_id == null)) {\n                var notificationGr = new GlideRecord('sys_cs_notification');\n                notificationGr.get(notification_content.vaNotificationId);\n                table_name = notificationGr.notification_execution.target_table.getValue();\n                sys_id = notificationGr.notification_execution.instance.getValue();\n            }\n            var eventParm1 = notification_content.eventParm1;\n            var eventParm2 = notification_content.eventParm2;\n            var actionContext = {'tableName': table_name, \n                                 'tableSysId': sys_id, \n                                 'actionId': actionId, \n                                 'eventParm1': eventParm1, \n                                 'eventParm2': eventParm2};\n            vaVars.global_notification_context = JSON.stringify(actionContext);\n            vaSystem.switchTopicById(selectedActionTopic);\n        } else {\n            vaVars.actionAcknowledgement = selectedAcknowledgementMessage;\n            var providerId = '';\n            if (vaSystem.isAMBClient(vaContext.deviceType)) {\n                providerId = '3918bf49533100102f40ddeeff7b122e';\n            } else {\n                var providerApp = JSON.parse(sn_cs.VASystemObject.getProviderAppById(vaContext.providerAppId));\n                var mapping = new GlideRecord('sys_cs_notification_channel_configuration');\n                mapping.get('provider', providerApp.providerId);\n\n                var channel = new GlideRecord('sys_notification_channel');\n                channel.get('sys_id', mapping.getValue('notification_channel'));\n                providerId = channel.getValue('provider');\n            }\n            \n            var act = new sn_notification.NotificationActionService(providerId, notification_content.executionId);\n            act.runById(actionId, true);\n        }\n    }\n})()",
