    var notification_content = JSON.parse(vaInputs.notification_content);
    var conversationLang = vaContext.getRequesterLang();

    // Card header variables
    var cardHeading = !gs.nil(notification_content.cardHeading) ? gs.getMessageLang(notification_content.cardHeading, conversationLang) : null;
    var cardLinkText = !gs.nil(notification_content.cardLinkText) ? gs.getMessageLang(notification_content.cardLinkText, conversationLang) : null;
    var cardLinkUrl = !gs.nil(notification_content.cardLinkUrl) ? notification_content.cardLinkUrl : null;

    // Card body variables
    var fields = notification_content.fields;
    var tableLabel = notification_content.tableLabel;
    var recordDisplayValue = notification_content.recordDisplayValue;
    var tableName = notification_content.tableName;
    var sysId = notification_content.recordSysId;
    var showRecordLink = notification_content.showRecordLink;

    var cardFields = [];
    Object.keys(fields).forEach(function(a){
        cardFields.push({"fieldLabel": a, "fieldValue": fields[a]});
    });

    var cardData = {
        title: !gs.nil(cardHeading) && cardHeading !== "null" ? cardHeading : tableLabel,
        subtitle: !gs.nil(cardLinkText) && cardLinkText !== "null" ? cardLinkText : recordDisplayValue,
        fields: cardFields,
    };

    if (showRecordLink) {
        var templatedUrl = vaSystem.applyLinkTemplate(sysId, tableName);
        cardData.url = !gs.nil(cardLinkUrl) && cardLinkUrl !== "null" ? cardLinkUrl : templatedUrl;
    }

    if (gs.nil(tableLabel))
        return;

    return vaSystem.renderCard(showRecordLink ? "Record Card" : "Record Card Without Link", JSON.stringify(cardData));
})()"},"secure":false},{"type":"ScriptedAction","script":"(function execute() {
    var greetingTopic = vaVars.global_notification_welcome_topic;
    if ( greetingTopic != "" && greetingTopic != undefined ) {
        vaVars.global_notification_welcome_topic = "";
        vaSystem.switchTopic(greetingTopic);
    }
})()
